/// by Dom Mandy in 2023


void mainImage(out vec4 o, vec2 i)
{
    // Edge distortion
    vec4 c = .01 * sqrt(texture(iChannel1, i / iResolution.xy));

    // Gamma correction
    vec4 s = sqrt(texture(iChannel0, c.xy + i / iResolution.xy));

    // Luma * 8
    float v = dot(s, vec4(2, 7, .7, 0));

    // Edges
    vec4 f = fwidth(s);

    o = min(1., log(.07 / max(max(f.x, f.y), f.z))) 
        * vec4(
            vec4(0, 4, 2, 6)[int(i.x) % 4] < v && 
            vec4(1, 5, 3, 7)[int(i.y) % 4] < v
        );    
}

vec3 palette( float t ) {

    vec3 a = vec3(0.498, 0.498, 0.098);
    vec3 b = vec3(-0.172, -0.222, 1.068);
    vec3 c = vec3(-2.082, -1.242, 0.268);
    vec3 d = vec3(0.000, 0.098, 0.667);

    return a + b*cos( 6.28318*(c*t+d) );
}

float sdBlobbyCross( in vec2 pos, float he )
{
        pos = abs(pos);
        pos = vec2(abs(pos.x-pos.y),1.0-pos.x-pos.y)/sqrt(2.0);

        float p = (he+pos.y-0.55/he)/(5.5*he);
        float q = pos.x/(he*he*16.0);
        float h = q*q - p*p*p;

        float x;
        if( h>0.0 ) { float r = sqrt(h); x = pow(q+r,1.0/3.0)-pow(abs(q-r),1.0/3.0)*sign(r-q); }
        else        { float r = sqrt(p); x = 2.0*r*cos(acos(q/(p*r))/3.0); }
        x = min(x,sqrt(2.0)/2.0);

        vec2 z = vec2(x,he*(1.0*2.0*x*x)) - pos;
        return length(z) * sign(z.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float d = sdBlobbyCross(uv, 1.);

    vec3 kleur = vec3(0.0);

    for (float i = 0.0; i < 2.0; i++) {

        d = sin(d*15. + iTime*2.);
        vec3 col = palette(length(uv) + i*0.6);

        d = pow(0.1/d, 0.9);

        kleur += col*d;
    }

    fragColor = vec4(kleur, 1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Hyperbolic Rings: mla, 2023
//
// The map z -> tan(log(z)) maps concentric rings of the complex plane
// onto the unit disc, which is the tesselated with a 6,6 hyperbolic
// tesselation (press 'x' to see untransformed disc). Some extra
// symmetry operations (p,q,r) produce wallpaper group effects.
//
// <mouse>: sets offset into texture (unless 'a' pressed)
// 1,2,3: show tile edges
// a: automatic mode (no mouse action)
// d: display outside disc/halfplane
// f: display principal region only
// p,q,r: apply extra symmetry operations
// x: toggle transform
//
////////////////////////////////////////////////////////////////////////////////

// Hexagonal tiling for maximum symmetry
const int P = 6;
const int Q = 6;
const int MAXITER = 100; // Number of folding iterations

vec3 getcolor(vec2 fragCoord) {
  vec2 mousepos = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
  // Just show one fundamental region
  bool fundamental = key(CHAR_F);
  vec2 C = vec2(icos(P),icos(Q));
  float R2 = 1.0/(dot(C,C)-1.0);
  float R = sqrt(R2);
  C *= R;

  vec2 ci = vec2(0,1); // Complex i

  vec2 z = (2.0*fragCoord - iResolution.xy)/iResolution.y;
  if (!key(CHAR_X)) {
    // Annulus
    //z *= 2.0;
    z = clog(z); // (1,0)-> (0,0)
    // The constant here depends on the tiling
    z = ctan(1.8237*z-vec2(0.5*iTime,0));
  }
  //z = cdiv(ci-z,ci+z);
  bool flip = dot(z,z) > 1.0;
  if (flip) {
    if (key(CHAR_D)) return vec3(0);
    else z /= dot(z,z);
  }
  //return texture(iChannel1,(z+1.0)/2.0).xyz; // For comparison
  int maxiter = MAXITER;
  int xflips = 0;
  int yflips = 0;
  int zflips = 0;
  for ( ; zflips < maxiter; zflips++) {
    xflips += int(z.x < 0.0);
    yflips += int(z.y < 0.0);
    z = abs(z);
    if (!tryinvert(z,C,R2)) break;
    if (fundamental) return vec3(0);
    //assert(zflips < 20);
  }
  if (zflips == maxiter) return vec3(0);
  vec2 z0 = z;
  vec2 dx = dFdx(z0);
  vec2 dy = dFdy(z0);

  float ldist = 1e8;
  if (!key(CHAR_0+1)) ldist = min(ldist,abs(length(C-z)-R));
  if (key(CHAR_0+2)) ldist = min(ldist,abs(z.x));
  if (key(CHAR_0+3)) ldist = min(ldist,abs(z.y));
  if (key(CHAR_P)) {
    if (xflips%2 == 1) z.x = -z.x; //p4m again P = 2n
  }
  if (key(CHAR_Q)) {
    if (yflips%2 == 1) z.y = -z.y; //and p4m again Q = 2n
  }
  if (key(CHAR_R)) {
    if (zflips%2 == 1) z = -z; // Rotation by 180
  }
  vec2 uv = z;
  uv = rotate(uv,0.25*PI);
  float k = 2.0/(1.0+dot(uv,uv)); // Transform to Beltrami-Klein
  k *= 0.5; // Extra scaling
  uv *= k; dx *= k; dy *= k; 
  if (iMouse.x > 0.0 && !key(CHAR_A)) {
    uv += 2.0*(iMouse.xy-0.5*iResolution.xy)/iResolution.y;
  } else {
    float t = 0.5*iTime;
    uv += 0.5*vec2(cos(t),sin(0.618*t));
  }
  vec3 col = textureGrad(iChannel0,uv,dx,dy).zyx;
  if (!flip) col = col.yzx;
  col = pow(col,vec3(2.2));
  col *= 1.5;
  col = smoothstep(vec3(0),vec3(1),col);//pow(col,vec3(0.8));
  col = mix(vec3(0),col,smoothstep(0.0,0.02,ldist)); // Gridlines
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec3 col = getcolor(fragCoord);
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1.0);
}

