////////////////////////////////////////////////////////////////////////////////
// common
////////////////////////////////////////////////////////////////////////////////

bool alert = false;
void assert(bool t) {
  if (!t) alert = true;
}

const float PI = 3.141592654;
const float TWOPI = 2.0*PI;

/// Complex arithmetic ///

// Normal vec2 operations work for
// addition, subtraction and
// multiplication by a scalar.

// Multiplication
vec2 cmul(vec2 z0, vec2 z1) {
  float x0 = z0.x; float y0 = z0.y; 
  float x1 = z1.x; float y1 = z1.y;
  return vec2(x0*x1-y0*y1,x0*y1+x1*y0);
}

// Reciprocal
vec2 cinv(vec2 z) {
  float x = z.x; float y = z.y;
  float n = 1.0/(x*x + y*y);
  return vec2(n*x,-n*y);
}

// Division
vec2 cdiv(vec2 z0, vec2 z1) {
  return cmul(z0,cinv(z1));
}

// Exponentiation - e^ix
vec2 expi(float x) {
  return normalize(vec2(cos(x),sin(x)));
}

vec2 clog(vec2 z) {
  return vec2(log(length(z)),atan(z.y,z.x));
}

vec2 clog(float x) {
  return clog(vec2(x,0));
}

// e^iz
vec2 cexp(vec2 z) {
  return exp(z.x) * expi(z.y);
}

vec2 cpow(vec2 z, vec2 w) {
  return cexp(cmul(clog(z),w));
}

vec2 cpow(vec2 z, float x) {
  return cexp(x*clog(z));
}

vec2 cexp(float k, vec2 z) {
  // return k^z
  return cexp(cmul(clog(k),z));
}

vec2 csqrt(vec2 z) {
  float r = length(z);
  return vec2(sqrt(0.5*(r+z.x)),sign(z.y)*sqrt(0.5*(r-z.x)));
}

vec2 csin(vec2 z) {
  float x = z.x, y = z.y;
  return cdiv(cexp(vec2(-y,x))-cexp(vec2(y,-x)), vec2(0,2.0));
}

vec2 ccos(vec2 z) {
  float x = z.x, y = z.y;
  return 0.5*(cexp(vec2(-y,x))+cexp(vec2(y,-x)));
}

vec2 ctan(vec2 z) {
  return cdiv(csin(z),ccos(z));
}

vec2 csinh(vec2 z) {
  return 0.5*(cexp(z)-cexp(-z));
}

vec2 ccosh(vec2 z) {
  return 0.5*(cexp(z)+cexp(-z));
}

vec2 ctanh(vec2 z) {
  return cdiv(csinh(z),ccosh(z));
}

vec2 catan(vec2 z) {
  vec2 iz = vec2(-z.y,z.x);
  vec2 t = clog(cdiv(vec2(1,0)-iz,vec2(1,0)+iz));
  return 0.5*vec2(-t.y,t.x);
}

vec2 catanh(vec2 z) {
  return 0.5*clog(cdiv(vec2(1,0)+z,vec2(1,0)-z));
  return 0.5*(clog(vec2(1,0)+z)-clog(vec2(1,0)-z)); // Alternative - invertable?
}

// Taken from NR, simplified by using a fixed number of
// iterations and removing negative modulus case.
// Modulus is passed in as k^2 (_not_ 1-k^2 as in NR).
void sncndn(float u, float k2,
            out float sn, out float cn, out float dn) {
  float emc = 1.0-k2;
  float a,b,c;
  const int N = 4;
  float em[N],en[N];
  a = 1.0;
  dn = 1.0;
  for (int i = 0; i < N; i++) {
    em[i] = a;
    emc = sqrt(emc);
    en[i] = emc;
    c = 0.5*(a+emc);
    emc = a*emc;
    a = c;
  }
  // Nothing up to here depends on u, so
  // could be precalculated.
  u = c*u; sn = sin(u); cn = cos(u);
  if (sn != 0.0) {
    a = cn/sn; c = a*c;
    for(int i = N-1; i >= 0; i--) {
      b = em[i];
      a = c*a;
      c = dn*c;
      dn = (en[i]+a)/(b+a);
      a = c/b;
    }
    a = 1.0/sqrt(c*c + 1.0);
    if (sn < 0.0) sn = -a;
    else sn = a;
    cn = c*sn;
  }
}

// Complex sn. uv are coordinates in a rectangle, map to
// the upper half plane with a Jacobi elliptic function.
// Note: uses k^2 as parameter.
vec2 sn(vec2 z, float k2) {
  float snu,cnu,dnu,snv,cnv,dnv;
  sncndn(z.x,k2,snu,cnu,dnu);
  sncndn(z.y,1.0-k2,snv,cnv,dnv);
  float a = 1.0/(1.0-dnu*dnu*snv*snv);
  return a*vec2(snu*dnv, cnu*dnu*snv*cnv);
}

vec2 cn(vec2 z, float k2) {
  float snu,cnu,dnu,snv,cnv,dnv;
  sncndn(z.x,k2,snu,cnu,dnu);
  sncndn(z.y,1.0-k2,snv,cnv,dnv);
  float a = 1.0/(1.0-dnu*dnu*snv*snv);
  return a*vec2(cnu*cnv,-snu*dnu*snv*dnv);
}

vec2 dn(vec2 z, float k2) {
  float snu,cnu,dnu,snv,cnv,dnv;
  sncndn(z.x,k2,snu,cnu,dnu);
  sncndn(z.y,1.0-k2,snv,cnv,dnv);
  float a = 1.0/(1.0-dnu*dnu*snv*snv);
  return a*vec2(dnu*cnv*dnv,-k2*snu*cnu*snv);
}

