// Invert z in circle radius r, centre w
vec2 invert(vec2 z, vec2 w, float r2) {
  vec2 z1 = z - w;
  float k = r2/dot(z1,z1);
  return z1*k+w;
}

// Invert z in circle p, r2, if it is inside
bool tryinvert(inout vec2 z, vec2 p, float r2) {
  vec2 z1 = z - p;
  float d2 = dot(z1,z1);
  if (d2 >= r2) return false;
  z = z1*r2/d2 + p;
  return true;
}

bool tryreflect(inout vec2 z, vec2 norm) {
  float k = dot(z,norm);
  if (k <= 0.0) {
    return false;
  } else {
    z -= 2.0*k*norm;
    return true;
  }
}

