#define GAMMA 2.2

#define MAX_STEPS 120
#define STEP_FAC 0.6
#define MAX_DIST 1.
#define MIN_DIST .01

#define GLOW_INT 0.5
#define PP_ACES 0.5
#define PP_CONT 0.5
#define PP_VIGN 1.5
#define AO_OCC 0.3
#define AO_SCA 0.8

#define AA_ENAB true
#define AA_THRE .01

#define iTime iTime
#define PI 3.14159265
#define TAU 6.28318531
#define S(x,y,t) smoothstep(x,y,t)


vec2 Map(in vec3 p) 
{    
    float d = MAX_DIST, col = 0., l = length(p);

    //TAMA
    vec3 tamp = p - vec3(0,.0745 + Move(iTime)*.06,0);
    float taml = length(tamp);
    if (taml < .1)
    {
        float tama = length(tamp) - .03;
        tama = smax(tama, -sdCone(tamp + vec3(0.,.016,0.), vec2(0.7), .02), .001);
        tama = max(tama, -sdCylinder(tamp + vec3(0,.01,0), vec2(.007, .03)));
        tama = max(tama, -sdCylinder(tamp - vec3(0,.01,0), vec2(.001, .03)));
        tama = max(tama, -(length(tamp - vec3(0,.0325,0))-0.0035));

        col = tamp.y > -.015 && taml > .029 ? 
                mix(col, 3.85, step(tama, d)) : 
                mix(col, Wood(tamp.yzx), step(tama, d));

        d = min(tama, d);
    }
    else d = .1;

    //KEN
    vec3 kenp = p;
    if (length(kenp) < .3)
    {
        float ken = sdCappedCone(p, 0.075, .015, .0001) - (kenp.y*kenp.y)/1.5;

        if (kenp.y > -.02) 
        {
            ken = smin(ken, length(kenp - vec3(0,0.080,0)) - .0008, .0045);

            float cups = sdEllipsoid(kenp - vec3(0.012, .035, 0.), vec3(.1, .025, .025));
            cups = max(cups, -sdTorus(kenp.zxy - vec3(0., .0, .035), vec2(.04, .031))); 
            cups = max(cups, sdBox(kenp - vec3(0.001, .035, 0.), vec3(.038)));
            cups = max(cups, -(length(kenp - vec3(0.059, .035, 0.)) - .03));
            cups = max(cups, -(length(kenp - vec3(-0.059, .035, 0.)) - .03));

            col = mix(col, Wood(kenp.zxy), step(cups, d));
            d = min(cups, d);
        }
        else
        {
            ken = smin(ken, sdCylinder(kenp + vec3(0,.0715,0), vec2(.02, .006)), .003);
            ken = smin(ken, sdCylinder(kenp + vec3(0,.035,0), vec2(.017, .002)), .01);
            float bcup = length(kenp + vec3(0.,.1,0.)) - .029; 
            bcup = min(bcup, sdCylinder(kenp + vec3(0,.1,0), vec2(.007, .05)));
            ken = max(ken, -bcup);
        }

        col = mix(col, Wood(kenp), step(ken, d));
        d = min(ken, d);
    }
    else d = .1;

    return vec2(d, col);
}

float String(in vec3 ro, in vec3 rd)
{
    float m = Move(iTime);

    vec3 p0 = vec3(0., 0.04, -.0085);
    vec3 p1 = vec3(-.08*cos(iTime*1.3), -.7 + m*.03, -.14);
    vec3 p2 = vec3(-.03*sin(iTime*0.5), .105 + m*.06, .15);
    vec3 p3 = vec3(0, .105 + m*.06, 0);

    float t = iBezier( ro, rd, p0, p1, p2, p3, 0.0005);

    return t > 0.0 ? t : MAX_DIST;
}

float SurfDis(float depth) { return depth / iResolution.y / 2.; }

vec3 Normal(in vec3 p, in float depth)
{
    float h = SurfDis(depth);
    vec2 k = vec2(1.,-1.);

    return normalize(   k.xyy * Map(p + k.xyy * h).x + 
                        k.yyx * Map(p + k.yyx * h).x + 
                        k.yxy * Map(p + k.yxy * h).x + 
                        k.xxx * Map(p + k.xxx * h).x );
}

vec3 RayMarch(vec3 ro, vec3 rd, vec2 fc) 
{
    float col = 0.;
	float dO = MIN_DIST;
    int steps = 0;

    dO += (fract(1e3* sin( dot(fc,vec2(114,211.1) )))-.5)*MIN_DIST; //fabrice like

    for(int i = 0; i < MAX_STEPS; i++) 
    {
        steps = i;

    	vec3 p = ro + rd*dO;
        vec2 dS = Map(p);
        col = dS.y;
        dO += dS.x * mix(STEP_FAC, 1., dO/MAX_DIST);

        if (dO > MAX_DIST || dS.x < SurfDis(dO)) break;
    }

    return vec3(dO, steps, col);
}

float softshadow( in vec3 ro, in vec3 rd, in float dis, float w) //iq mk
{
    float res = 1.;
    float t = .01;
    float d = (MAX_DIST-dis)*.5;

    for( int i=0; i<12 && t<d; i++ )
    {
        float h = Map(ro + t*rd).x;
        res = min( res, h/(w*t) );
        t += clamp(log(h+1.), .001, .1);
        if( res<-1.0 || t>d) break;
    }
    res = clamp(res*2., -1., 1.);
    return 0.25*(1.0+res)*(1.0+res)*(2.0-res);
}

float CalcAO(const in vec3 p, const in vec3 n) //iq
{
    float occ = AO_OCC;
    float sca = AO_SCA;

    for( int i = 0; i < 5 ; i++ )
    {
        float h = 0.001 + 0.150 * float(i) / 4.0;
        float d = Map(p + h * n).x;
        occ += (h - d) * sca;
        sca *= 0.95;
    }
    return S(0.0, 1.0 , 1.0 - 1.5 * occ);    
}

const vec3 ambCol = vec3(.03,.05,.1) * 2.0;
const vec3 sunCol = vec3(1., .8, .6) * 1.0;
const vec3 skyCol = vec3(.3, .5, 1.) * 1.0;
const float specExp = 5.;

vec3 Sky(vec3 rd, vec3 lPos, float depth)
{
    float gnd = smoothstep(-.2, .3, dot(vec3(0,1,0), rd));
    vec3 col = mix((ambCol + skyCol + sunCol) / 8., skyCol, gnd);

    return S(vec3(0), vec3(1), col);
}

vec3 Shade(vec3 col, float mat, vec3 p, vec3 n, vec3 rd, vec3 lp, float dis) 
{

    vec3    lidi = normalize(lp - p);
    float   mafa = max(mat, .0),
            amoc = CalcAO(p, n),
            shad = softshadow(p, normalize(lp), dis, 0.5),
            diff = max(dot(n, lidi), 0.) * shad,
            spec = pow(diff, max(1., specExp * mafa)),
            refl = pow(max(0., dot(lidi, reflect(rd, n))), max(1., specExp * 3. * mafa)) * shad;

    return  col * (amoc * ambCol +                                       
                   (1. - mafa) * diff * sunCol +                         
                   mafa * (spec + refl) * sunCol +                       
                   max(-mat, 0.) * smoothstep(0., 1., amoc * amoc + .5));
}

vec3 Palette(int index)
{
    switch (index)
    {
        case 0: return vec3(1.);
        case 1: return vec3(1., .6, .3) *.7;
        case 2: return vec3(1., .02, .01) *.3 * S(0.,1.,cos(iTime/21.));
        case 3: return hsv2rgb_smooth(vec3(fract(iTime/36.+.3), .8, .85));
    }
    return vec3(0.);
}

vec3 R(in vec2 uv, in vec3 p, in vec3 l)
{
    vec3   f = normalize(l - p),
           r = normalize(cross(vec3(0,1,0), f)),
           u = cross(f,r),
           c = p + f,
           i = c + uv.x*r + uv.y*u;
    return normalize(i - p);
}

void Render( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5 * iResolution.xy) / iResolution.y;
	vec2 m = iMouse.xy / iResolution.xy;
    if (length(m) <= 0.) m = vec2(.5);

    vec3 ro = vec3(0., 0., -.3);
    ro.yz *= Rot(-m.y * PI + PI*.5);
    ro.xz *= Rot(-m.x * PI*2. - PI);
    vec3 rd = R(uv, ro, vec3(0., 0.03, 0.));

    vec3 lPos = vec3(sin(iTime*2.*PI/10.)*5., sin(iTime*2.*PI/20.)*5.+00., cos(iTime*2.*PI/30.)*5.-10.);

    vec3 p = vec3(0.);
    vec3 rmd = RayMarch(ro, rd, fragCoord);

    vec3 bg = Sky(rd, lPos, rmd.x/MAX_DIST);
    vec3 col = bg;

    float str = String(ro, rd);

    if (str < rmd.x)
    {
        col = Palette(2) * S(1.,-1., dot(rd, normalize(lPos))) + vec3(.02);
        //col *= softshadow( ro + rd*str, normalize(lPos), str, 0.5);
        rmd.x = str;
        rmd.y = 1.;
    }
    else if(rmd.x < MAX_DIST) 
    {
        p = ro + rd * rmd.x;
        vec3 n = Normal(p, rmd.x);
        if (rmd.z < 2. && rmd.z > 1.) 
            n *= 1. - (rmd.z-1.5) * .5;

        float shine = fract(rmd.z)*abs(rmd.z)/rmd.z;
        col = Palette(int(floor(abs(rmd.z))));
        col = Shade(col, shine, p, n, rd, lPos, rmd.x);   

        //col = vec3(1) * CalcAO(p, n);
    }

    float disFac = S(0., 1., pow(rmd.x / MAX_DIST, 2.));

    col = mix(col, bg, disFac);
    col += pow(rmd.y / float(MAX_STEPS), 2.) * normalize(ambCol) * GLOW_INT;

    fragColor = vec4(col * S(0., 10., iTime*iTime),1.0);
}

vec4 PP(vec3 col, vec2 uv)
{
    col = mix(col, (col * (2.51 * col + 0.03)) / (col * (2.43 * col + 0.59) + 0.14), PP_ACES); 
    col = mix(col, S(vec3(0.), vec3(1.), col), PP_CONT);    
    col *= S(PP_VIGN,-PP_VIGN/5., dot(uv,uv)); 
    col = pow(col, vec3(1) / GAMMA);

    return vec4(col, 1.);
}

void mainImage(out vec4 O, vec2 U) //Fabrice
{
    Render(O,U);

    if (AA_ENAB && fwidth(length(O)) > AA_THRE)
    {
        vec4 o;
        for (int k=0; k < 9; k+= k==3?2:1 )
          { Render(o,U+vec2(k%3-1,k/3-1)/3.); O += o; }
        O /= 9.;
        //O.r++; //Show sampled area
    }

    O = PP(vec3(O), (U-.5 * iResolution.xy) / iResolution.y);
}

