// Virgill/Alcatraz 4k intro for Deadline Demoparty Berlin 2023

// Xor rox, Sointu rox, Las rox


#define ITER 60
#define BOUNCES 2
#define PI 3.14159265
#define PHI 1.618033988749

vec3 hashHs(vec3 n, uint seed)
{
	float a = (float((seed * 0x73493U) & 0xfffffU) / float(0x100000))*2. - 1.;
	float b = 6.283*(float((seed * 0xAF71fU) & 0xfffffU) / float(0x100000));
	float c = sqrt(1. - a * a);
	vec3 r = vec3(c*cos(b), a, c*sin(b));
	return dot(r, n) > 0. ? r : -r;
}

float noise(vec3 p)
{
	vec3 ip = floor(p);
	p -= ip;
	vec3 s = vec3(7, 157, 113);
	vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
	p = p * p*(3. - 2.*p);
	h = mix(fract(sin(h)*43758.5), fract(sin(h + s.x)*43758.5), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

// Lasball by Mercury
float fBlob(vec3 p) {
	p = abs(p);
	if (p.x < max(p.y, p.z)) p = p.yzx;
	if (p.x < max(p.y, p.z)) p = p.yzx;
	float b = max(max(max(
		dot(p, normalize(vec3(1, 1, 1))),
		dot(p.xz, normalize(vec2(PHI + 1., 1)))),
		dot(p.yx, normalize(vec2(1, PHI)))),
		dot(p.xz, normalize(vec2(1, PHI))));
	float l = length(p);
	return l - 1.5 - .5 *sin(0.33*iTime)* (1.5 / 2.)* cos(min(sqrt(1.01 - b / l)*(PI / 0.25), PI));
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r)
{
	vec3 pa = p - a, ba = b - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
	return length(pa - ba * h) - r;
}

float sdBox(vec3 p, vec3 b)
{
	vec3 d = abs(p) - b;
	return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.));

}

vec2 pMod(inout vec2 p, vec2 size)
{
	vec2 hz = size / 2.;
	vec2 c = floor((p + hz) / size);
	p = mod(p + hz, size) - hz;
	p *= mod(c, vec2(2))*2. - vec2(1);
	p -= hz;
	if (p.x > p.y) p.xy = p.yx;
	return floor(c / 2.);
}

void pR(inout vec2 p, float a)
{
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float sdSphere(vec3 p, float s)
{
