// alcatraz logo by slerpy/lj/gopher/xtr1m
float atz(vec3 p)
{
	vec3 a=abs(p);
	return max(min(max(max(
		min(length(vec2(mod(p.x+.067,.134)-.067,p.z)),max(a.x-.02,a.y)-.1),
		-min(length(p.xy-vec2(0,.03))-.06,max(a.x,abs(p.y+.02)-.03)-.04)
    ),abs(a.x+a.y)-.4),abs(abs(a.x+a.y)-.41)*.707-.01),a.z)-.03;
}

float smin(float a, float b, float k)
{
	float h = clamp(0.5 + 0.5*(b - a) / k, 0.0, 1.0);
	return mix(b, a, h) - k * h*(1.0 - h);
