float tunnel(vec3 p)
{
    float time = iTime+22.5;
	p = p.xzy; p.z -= time;
	p.xy = vec2((atan(p.x, p.y) + 1.5*sin(0.9*sqrt(dot(p.xy, p.xy)) - 0.1*time))*1.0187, 1. - length(p.xy));
	vec2 ku = pMod(p.xz, vec2(.8));
	pR(p.xz, time*.16);
	pMod(p.xz, vec2(.7));
	float box = sdBox(p - vec3(0), vec3(.4));
	return box;
}

