vec3 map(vec3 p)
{
	vec3 d = vec2(0, 1e+31).yxx;
	float wireframe = 0.;
	if (fract(0.25*iTime) < 0.3125&&fract(4.*iTime) < 0.25&&iTime > 8.) wireframe = 1.;
	dmin(d, sdCapsule(p, vec3(-5., -2.5, 0), vec3(5., -2.5, 0), .5), 3., 0.);

    if (iTime < 32.)
	{
		// noise floor
		vec3 q = p;
		q.x += iTime;
		float floornoise = .9*noise(.7*q);
		dmin(d, smin(5. - p.z, 1.5 - p.y, 8.) + floornoise, 0.9, 0.0);
		// wireframe cube
		if (iTime > 16.)pR(p.yz, 0.25*(iTime - 16.));
		if (iTime > 20.)pR(p.xz, 0.5*(iTime - 20.));
		wireframe = (.98 + length(max(abs(p) - 0.8802, 0.0)))*wireframe;
		dmin(d, sdBox(p, vec3(.9)), .9, wireframe);  //;
	}


	if (iTime > 32.&&iTime < 48.)
	{
		// tunnel1
		vec3 q = p;
		pR(q.yz, PI*.5);
		dmin(d, tunnel(q), .7, 0.);
		dmin(d, sdSphere(q + vec3(0., 1. + sin(iTime), 0.), .3), 4., wireframe);
	}

	if (iTime > 48. && iTime<72.)
	{
		// walls
		vec3 q = p;
		pR(q.zx, 0.5 + 0.5*sin(0.2*iTime));
		pR(q.yz, .2 + 0.2*sin(0.5*iTime));
		dmin(d, 2. - q.y, 0.5 + 0.5*sin(4.*q.x), 0.);
		dmin(d, 7.9 + q.x, .7, 0.);
		dmin(d, 8. - q.z, 2., 1. - sin(4.*q.x));
		// lasball
		q = p;
		pR(q.xy, 0.4*iTime);
		pR(q.zy, 0.25*iTime);
		dmin(d, fBlob(q*1. + 0.1*sin(iTime)), 0.5, 0.);
	}

    if (iTime >72.)
    {
        // logo
        vec3 q = p;
        float moise=0.25-0.5*noise(6.*q+iTime*0.5)-0.1*noise(30.*p+iTime);
        dmin(d, sdCapsule(p, vec3(-10., moise, -1.9), vec3(10.,moise , -1.9), .23), 1.01, 1.);
        q=p+vec3(0.,0.,2.1);
        pR(q.zy,PI+0.25*sin(iTime));
        dmin(d, atz(q),.99,0.);
    }    
    return d;
}

vec3 normal(vec3 p, float k)
{
	float m = map(p).x;
	vec2 e = vec2(0.0, k);
	return normalize(m - vec3(map(p - e.yxx).x, map(p - e.xyx).x, map(p - e.xxy).x));
}

vec3 tracer(vec3 ro, vec3 rd)
{
	vec3 m;
	float t = .0001;
	for (int i = 0; i < ITER; i++)
	{
		m = map(ro + rd * t);
		t += m.x;
	}
	return vec3(t, m.yz);
}

void camera(out vec3 ro, out vec3 rd, in vec2 p)
