		vec4 buffer = texture(iChannel0, uv);		  
		float amplify = .1, blur = .8;
		float zoom = 3.;
		float shift = 0.;
		if (iTime > 16.) shift = 0.25 + .25*-cos((iTime*PI*0.25));
		if (fract(iTime*0.0625) < 0.5) shift = 0.;
		if (iTime > 72.) shift = 0.;
		if (uv.y > 0.5 - shift && uv.y < 0.5 + shift)
		{
			zoom = 1.;
		}
        // calculate first seed
		uint seed = uint((fragCoord.x / zoom)) ^ uint(fragCoord.y / zoom) ^ uint(floor(iTime+2.)); 
		camera(ro, rd, fragCoord);

		for (int i = 0; i < BOUNCES; i++)
		{
			t = tracer(ro, rd);
			sp = ro + rd * t.x;
			sn = normal(sp, 5e-5);
			if (t.y > 1.&& i > 0) emit = t.y * vec3(0.7, 1., 0.7); 
             // wireframe 
			if (t.z >= 1.&&i == 0) emit = t.z*vec3(1.4, 2., 1.4);


			if (uv.y > 0.5 - shift && uv.y < 0.5 + shift)
			{
                // calculate new seed
				seed += uint(iTime*30.); 
				if ((uv.y > 0.5 - shift && uv.y < 0.505 - shift) || (uv.y > 0.495 + shift && uv.y < 0.5 + shift)) emit = vec3(1., 2., 1.);
				amplify += .05;
			}
            // reflect ray over hemisphere. dependent on material
			rd = mix(reflect(rd, sn), hashHs(sn, seed), clamp(t.y, 0., 1.));  
			ro = sp;
		}

		emit *= amplify;
		float fade = min(iTime*0.2, 1.);
		float fadeout = clamp(1. - min((iTime - 80.)*0.1, 1.), 0., 1.);
		fragColor = (vec4(emit.x, emit.y, emit.z, 1.) + buffer * blur)*fadeout*fade;
}

