//Controls: push the left mouse button, drag it around

//Inspired by https://www.shadertoy.com/view/dtySDy

//Amazing resource explaining cube coordinates of hexagon grids:
//https://www.redblobgames.com/grids/hexagons/more-pixel-to-hex.html#justin-pombrio
mat2 skewed_to_grid = mat2(1.0f, 0.0f, 0.5f, sqrt(3.0f) / 2.0f);

vec2 GridToSkewed(vec2 grid_pos)
{
    return inverse(skewed_to_grid) * grid_pos;
}

vec2 SkewedToGrid(vec2 skewed_pos)
{
    return skewed_to_grid * skewed_pos;
}

vec3 SkewedToCube(vec2 skewed_pos)
{
    return vec3(skewed_pos.xy, -skewed_pos.x - skewed_pos.y);
}

//Brachless version that achieves the same thing a bunch of if's do in the article above:
vec3 CubeToHex(vec3 cube_pos)
{
    vec3 i = round(cube_pos);
    vec3 d = abs(i - cube_pos);
    return mix(i, -i.yzx - i.zxy, step(d.yzx, d.xyz) * step(d.zxy, d.xyz));
}


//An alternative formulation involves 3x floor + 3x round:
//https://justinpombrio.net/programming/2020/04/28/pixel-to-hex.html
vec3 CubeToTri(vec3 cube_pos)
{
    return floor(cube_pos.xyz - cube_pos.zxy);
}

vec3 TriToHex(vec3 tri_index)
{
    return round((tri_index.xyz - tri_index.yzx) / 3.0f);
}

float max3(vec3 v)
{
    return max(max(v.x, v.y), v.z);
}
float HexOuterDist(vec3 cube_delta)
{
    return max3(abs(cube_delta.xyz - cube_delta.yzx));
}

float HexInnerDist(vec3 cube_delta)
{
    return length(cube_delta) / (sqrt(2.0f) / sqrt(3.0f)); //(sqrt(2.0f) / 2.0f) for inscribed normalization
}

vec2 Rotate2(vec2 vec, float ang)
{
    mat2 m = mat2(cos(ang), sin(ang), -sin(ang), cos(ang));
    return m * vec;
}

float SmoothFract(float val)
{
    float f = fract(val);
    return f * clamp((1.0f - f) * 10.0f, 0.0f, 1.0f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //inputs
    bool mouse_was_pressed = texelFetch(iChannel0, ivec2(0), 0).r > 0.5f;
    float mouse_pressed_time = texelFetch(iChannel0, ivec2(0), 0).g;
    vec2 grid_pos = fragCoord.xy/iResolution.x * 15.0f;
    float straight_fade = mouse_was_pressed ? mouse_pressed_time : clamp(0.5f - cos(iTime * 0.5f) * 4.0f, 0.0f, 1.0f);
    vec2 skewed_pos = mix(GridToSkewed(grid_pos), grid_pos, straight_fade);
    float width = 0.05f;
    vec2 grid = step(width, fract(skewed_pos.xy + vec2(width * 0.5f)));

    vec2 wiggle_pos = vec2(-sin(0.3f * iTime), -sin(0.42f * iTime)) * 0.4f + vec2(0.5f);
    vec2 quad_index2f = step(mouse_was_pressed ? iMouse.xy : (iResolution.xy * wiggle_pos), fragCoord.xy);
    int quad_index = int(quad_index2f.y * 2.0f + quad_index2f.x);

    //hex stuff
    vec3 cube_pos = SkewedToCube(skewed_pos);
    vec3 tri_index = CubeToTri(cube_pos); //this is an ivec2
    vec3 hex_index = TriToHex(tri_index); //this is an ivec2
    //vec3 hex_index2 = CubeToHex(cube_pos); //same result, calculated in a different way
    //note that distances are calcualted based in cube coordinates, so they're independent of whether the grid is skewed or not
    float outer_dist = HexOuterDist(hex_index.xyz - cube_pos.xyz); //0..1
    float inner_dist = HexInnerDist(hex_index.xyz - cube_pos.xyz); //0..1
    vec2 hex_skewed_pos = hex_index.xy;
    vec2 hex_grid_pos = mix(SkewedToGrid(hex_skewed_pos), hex_skewed_pos, straight_fade);


    if(quad_index == 3)
    {
        fragColor = vec4(fract((hex_index + vec3(-0.5f)) / 6.0f).xyz * (1.0f - inner_dist) * SmoothFract(outer_dist * 4.0f) * 1.5f, 1.0f);    
    }
    if(quad_index == 0)
    {
        fragColor = vec4(fract((tri_index + vec3(-0.5f)) / 6.0f).xyz * grid.x * grid.y, 1.0f);    
        //fragColor = vec4(vec3(fract(tri_index / 6.0f)[int(iTime / 3.0f) % 3]) * grid.x * grid.y, 1.0f);    
    }
    if(quad_index == 1)
    {
        fragColor = vec4(vec3(fract((hex_index + vec3(-0.5f)) / 6.0f).xyz) * inner_dist * grid.x * grid.y, 1.0f);
        //fragColor = vec4(vec3(fract(hex_index / 6.0f)[int(iTime / 3.0f) % 3]) * inner_dist * grid.x * grid.y, 1.0f);
    }    
    if(quad_index == 2)
    {
        fragColor = vec4(fract(Rotate2(grid_pos - hex_grid_pos, iTime)) * grid.x * grid.y * (1.0f - pow(outer_dist, 5.0f)), 0.0f, 1.0f);
    }
    fragColor.rgb = pow(fragColor.rgb, vec3(1.0f / 2.2f));
}

