#define PI 3.141592654

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;

    float rem = col.r + col.g + col.b;
    float simRed = (2.0 - length(col - vec3(1.0, 0.0, 0.0) + sin(uv.x * 2.0 - iTime * 0.8)));
    rem -= simRed;
    float simBlue = (2.0 - length(col - vec3(0.0, 0.0, 1.0) - cos(uv.y * 2.0 + iTime)));
    rem -= simBlue;
    rem += sin(iTime);

    float diff = (simRed - simBlue) * uv.y * 20.0;
    float dir = (1.0 - pow(2.0, -abs(diff))) * sign(diff) * 0.5 + 0.5;

    vec3 colA = vec3(
        sin(iTime) * 0.5 + 0.5,
        sin(iTime + PI * 0.333) * 0.5 + 0.5,
        sin(iTime + PI * 0.666) * 0.5 + 0.5
    ) * vec3(1.0, 0.1, 0.15);
    vec3 colB = vec3(
        cos(iTime * 1.42) * 0.5 + 0.5,
        cos(iTime * 1.42 + PI * 0.333) * 0.5 + 0.5,
        cos(iTime * 1.42 + PI * 0.666) * 0.5 + 0.5
    ) * vec3(0.1, 0.15, 1.0);


    fragColor = vec4(mix(vec3(rem), mix(colA, colB, dir), (simRed + simBlue) / rem), 1.0);
    //fragColor = vec4(vec3(dir, diff, rem), 1.0);
}

const float PI = 3.1415927;

void mainImage(out vec4 O, vec2 I) {
    vec2 R = iResolution.xy;
    vec2 p = (2.0 * I - R) / R.y * 6.0;
    float t = 1.0 - abs(1.0 - mod(iTime / 2.0, 2.0));
    float a = (smoothstep(0.0, 1.0, smoothstep(0.0, 1.0, t)) - 0.5) * PI;
    float b = iMouse.z > 0.0 ? mix(0.2, 1.5, iMouse.x / R.x) : 0.6;
    float c = cos(a);
    float s = sin(a);
    vec3 o = vec3(0.0, 1.0, -1.0);
    mat2 A = mat2((c * o.yzyy + s * o.yyzy) * b + o.yzyy);
    mat2 B = inverse(A);
    vec2 P = B * p;
    vec4 Q = 2.0 * round((P.xyxy + o.xxyy) / 2.0) - o.xxyy;
    vec4 d = abs(vec4(A * Q.xy - p, A * Q.zw - p));
    mat2 C = mat2(c, -s, s, c);
    vec4 f = abs(vec4(C * (A * Q.xw - p), C * (A * Q.zy - p)));
    float g = b - min(max(f.x, f.y), max(f.z, f.w));
    float h = 1.0 - min(max(d.x, d.y), max(d.z, d.w));
    O = g >= 0.0
        ? vec4(1.000,0.302,0.533,1.0)
        : h >= 0.0
        ? vec4(0.204,0.490,0.957,1.0)
        : vec4(1.0);
    float i = min(abs(g), abs(h));
    O *= smoothstep(0.0, length(vec2(dFdx(i), dFdy(i))), i - 0.02);
}

#define time mod(iTime, 100.)

mat2 rot(float a)
{
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float smin(float a, float b, float h)
{
    float k = clamp((a-b)/ h * .5 + .5, 0., 1.);
    return mix(a,b,k) - k * (1.-k) * h;
}

vec3 smin(vec3 a, vec3 b, float h)
{
    vec3 k = clamp((a-b)/ h * .2 + .5, 0., 1.);
    return mix(a,b,k) - k * (1.-k) * h;
}

vec3 kifs(vec3 p, float t)
{
    float s = 0.1 + sin(time * 0.3) * 0.1;
    for(float i = 0.; i < 5.; ++i)
    {
        float t2 = t + i;
        p.xz *= rot(t2);
        p.xy *= rot(t2 * .3);
        p = smin(p, -p, -1.);
        p -= s;
        //p -= s + sin(t- length(p));
        s *= 0.7;
    }

    return p;
}

float map(vec3 p, inout vec3 col)
{
    float d;

    vec3 p1 = p;
    p1 = kifs(p, time * 1.);
    float d1 = length(p1) - 0.01;

    p.xy *= rot(time * 0.5);
    p.xz *= rot(time * 0.3);

    float y = sin(time)*.5+2.;
    float x = sqrt(y*y + y*y);

    float sph = length(p) - 10.;

    float sph1 = length(vec3(-x,-y,0.) - p) - sin(time * 10.)*0.015-.3;
    vec3 sph1col = vec3(1., 3., 0.) * (1. / sph1);
    float sph2 = length(vec3(x,-y,0.) - p) - sin(time * 8.)*0.015-.3;
    vec3 sph2col = vec3(0., 0., 1.) * (1. / sph2);
    float sph3 = length(vec3(0.,y,0.) - p) - sin(time * 6.)*0.015-.3;
    vec3 sph3col = vec3(1., 1., 0.) * (1. / sph3);


    d = smin(sph1, sph2, (sin(time*4.)*.2+ 6.2) * 0.8);
    d = smin(d, sph3, (sin(time*4.)*.5+ 7.0) * 0.8);
    d = smin(d, d1, 6.);
    col = smin(sph1col, sph2col, -.5);
    col = smin(col, sph3col, -.5);

    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1.);

    vec3 s = vec3(0.,0.,-10.);
    vec3 r = normalize(vec3(uv, 1.));
    vec3 col = vec3(0);
    vec3 acol = vec3(0);
    float at = 0.;
    vec3 p = s;
    float i = 0.;
    for(i = 0.; i < 100.; ++i)
    {
        float d = abs(map(p, acol));
        at += 0.09 / (0.015 + d);
        d *= 0.1;
        if(d < 0.01)
        {
            break;
        }
        col += pow(at * 0.026, 3.);
        p += r * d;
    }
    col *= acol;
    //col += pow(1.-i/101., 6.);

    fragColor = vec4(col, 1.);
}

////////////////////////////////////////////////////////////////////
//
// Aperiodic Wang Tiling, mla, 2023
//
// Wang tilings, introduced by Hao Wang (çæµ©) in 1961, are
// square tiles with colored edges. A valid tiling is one where
// the colors on the edges of adjacent tiles match. The first set of
// aperiodic tiles discovered, by Robert Berger in 1964, was a set of 
// 20476 Wang tiles. This number has been reduced since & in fact the
// smallest aperiodic set of Wang tiles now known contains 11
// tiles and it has been proved that no smaller set is possible.
//
// This shader uses the substitution rules for the 16 tile tiling 
// described in GrÃ¼nbaum & Shephard. The grid is NxN where N is a 
// Fibonacci number and the recurrence splits the grid into two squares
// and two rectangles, which are then treated as parts of a larger square.
//
// <mouse>: pan image
// <up/down>: zoom in/out
// m: disable mouse
// r: rotate by 45Â°
// x,y: grid line display
//
////////////////////////////////////////////////////////////////////

const float phi = 0.618033989; // NB, not 1.618..

// 6 colors, 16 tile types
const int X = -1, A = 10, B = 11, C = 12, D = 13, E = 14, F = 15;
ivec4 transitions[] =
  ivec4[](ivec4(X,X,X,2),ivec4(0,9,C,3),ivec4(0,B,E,3),ivec4(0,8,D,1),
          ivec4(0,B,C,3),ivec4(0,8,C,7),ivec4(0,9,E,3),ivec4(0,9,D,5),
          ivec4(X,X,D,1),ivec4(X,X,F,6),ivec4(X,X,C,7),ivec4(X,X,F,2),
          ivec4(X,A,X,4),ivec4(X,8,X,1),ivec4(X,A,X,2),ivec4(X,9,X,5));

ivec4 colors[] =
  ivec4[](ivec4(1,2,1,2),ivec4(3,4,3,4),ivec4(4,5,4,5),ivec4(6,3,6,3),
          ivec4(4,5,3,4),ivec4(6,3,3,4),ivec4(3,4,4,5),ivec4(3,4,6,3),
          ivec4(5,1,2,3),ivec4(4,1,2,6),ivec4(5,1,1,4),ivec4(3,2,2,6),
          ivec4(2,6,4,1),ivec4(2,3,5,1),ivec4(2,6,3,2),ivec4(1,4,5,1));

const vec3 palette[] =
  // "Party Pastels"
  vec3[](vec3(182,230,189),vec3(172,154,241),vec3(247,200,238),
         vec3(255,239,176),vec3(255,202,150),vec3(245,154,142));

vec3 getcol(int i) {
  vec3 col = palette[i-1]/255.0;
  col = pow(col,vec3(2.2));
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  float time = mod(iTime,40.0);
  vec2 z = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  z *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  float k = pow(phi,time);
  if (!key(CHAR_Z)) z *= 10.0*k;
  if (!key(CHAR_M) && iMouse.x > 0.0) z -= (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
  if (key(CHAR_R)) z *= mat2(1,1,-1,1);
  if (key(CHAR_C)) z = abs(z);
  else z = (z+1.0);
  z *= 0.5;
  vec3 col = vec3(0.1,0,0);
  int M = 1, N = 0;
  for (int i = 0; i < int(time); i++) {
    M = M+N; N = M-N; // I could just have a table of fib(i)
  }
  if (z.x >= 0.0 && z.x < 1.0 && z.y >= 0.0 && z.y < 1.0) {
    int state = 1; // 1-7 are valid start states
    z *= float(N+M);
    float pwidth = fwidth(length(z));
    ivec2 p = ivec2(z);
    vec2 dx = dFdx(z), dy = dFdy(z);
    z = fract(z);
    while (N != 0) {
      // Split region into (N+M)x(N+M) regions
      // and recurse into the region our point is in,
      // after applying the relevant state transition.
      // Non-square subregions are treated as part of
      // a larger square.
      int sector = 2*int(p.x >= N)+int(p.y >= N);
      state = transitions[state][sector];
      assert(state >= 0);
      if (p.x < N) p.x += M-N;
      else p.x -= N;
      if (p.y < N) p.y += M-N;
      else p.y -= N;
      N = M-N; M = M-N; // Think about it!
    }
    int icol = colors[state][int(z.x+z.y >= 1.0) + 2*int(z.x>=z.y)];
    col = getcol(icol);
    vec2 uv = 0.25*(vec2(state/4,state%4)+z);
    col *= 2.0*textureGrad(iChannel0,uv,0.25*dx,0.25*dy).xyz;
    if (!key(CHAR_X)) {
      vec2 z1 = min(z,1.0-z);
      float d = min(z1.x,z1.y);
      col *= smoothstep(0.0,pwidth,d-0.0125);
    }
    if (!key(CHAR_Y)) {
      float d = min(abs(z.x-z.y),abs(z.x+z.y-1.0));
      col *= smoothstep(0.0,pwidth,d-0.01);
    }
