void mainImage(out vec4 O, vec2 u)
{
    float t = fract(iTime * .13);

    vec2 R = iResolution.xy, 
         U = 20. * exp(-t * 2.2) * (R-u-u) / R.y - t / 22.,
         v,
         z = fract(U / 3.6 + .5) * 3.6 - 1.8,
         w = sign(z);

    O = 0.*O;

    for (int x; x < 5; x++)
    for (int y; y < 5; y++)
    {
        int I = x * int(w.x),
            J = y * int(w.y),
            K = 4*J*J,
            L = 4*I*I,
            m = I > 0 && I == -J     ? K - 4*J + 3 :     // squares diagonal - bottom right
                I > 0 && I >= abs(J) ? L - 3*I + J + 1 : // right region
                J > 0 && J >= abs(I) ? K - I - J + 1 :   // top region
                I < 0 && J >= I      ? L - I - J + 1 :   // left region
                                       K + I - 3*J + 3;  // bottom region

        // inject for -2 (scary)
        v = mix(v = vec2(I, J) * .1 - .05,
                v * 4. + .15,
                clamp(vec2(m) / 6. - 16. + 18.*t - length(U-z)/4., 0., 1.));

        O += smoothstep(3./R.y, 0., length(max(abs(z-v)-.05, 0.)));
    }
}
