    vec2 t = getAO(uv, 0.5);
    vec2 x[count*4];
    int n = 0;
    fragColor = vec4(0);
    for (int i = 0; i < count; i += 1) {
        float theta = 2.0*PI*(float(i)/float(count));
        vec3 pos = vec3(vec2(sin(theta), cos(theta*1.5))*0.5, 0.04);
        //if (i == 0) pos = vec3(2.0*(iMouse.xy-iResolution.xy*0.5)/iResolution.y, 0.4);
        vec2 ao = getAO(uv-pos.xy, pos.z);
        x[n++] = vec2(max(ao.x, 0.0), 1); //start
        x[n++] = vec2(min(ao.y, PI2), -1); //end
        if (ao.x < 0.0) {
            x[n++] = vec2(ao.x+PI2, 1) ;
            x[n++] = vec2(PI2, -1);
        }
        if (ao.y > PI2) {
            x[n++] = vec2(0, 1);
            x[n++] = vec2(ao.y-PI2, -1);
        }
    }
    vec2 tmp;
    for (int r = 0; r < n; r += 1) {
        bool swaped = false;
        for (int m = 0; m < n-1; m += 1) {
            if (x[m].x > x[m+1].x) {
                tmp = x[m];
                x[m] = x[m+1];
                x[m+1] = tmp;
                swaped = true;
            }
        }
        if (!swaped) break;
    }
    int state = 0;
    float k = 0.0;
    float occ = 0.0;
    bool set = true;
    for (int i = 0; i < n; i += 1) {
        state += int(x[i].y);
        if (state == 1 && set) {
            k = x[i].x;
            set = false;
        }
        if (state == 0) {
            occ += x[i].x-k;
            set = true;
        }
    }
    vec3 col = 1.-vec3(occ)/PI2;
    fragColor += vec4(sqrt(col), 1);
}

