// SDFs: https://iquilezles.org/articles/distfunctions2d/
// Inspiration: Just Passing Icon - Jonathan Quintin
#define PI 3.14159265359

float sdCircle(vec2 p, float r)
{
    return (length(p) - r);
}

float sdBox(vec2 p, vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdTrapezoid( in vec2 p, in float ra, float rb, float he)
{
    float sy = (p.y<0.0)?-1.0:1.0;

    p.x = abs(p.x);

    vec4 res;

    // bottom and top edges
    {
        float h = min(p.x,(p.y<0.0)?ra:rb);
        vec2  c = vec2(h,sy*he);
        vec2  q = p - c;
        float d = dot(q,q);
        float s = abs(p.y) - he;
        res = vec4(d,q,s);
    }

    // side edge
    {
        vec2  k = vec2(rb-ra,2.0*he);
        vec2  w = p - vec2(ra, -he);
        float h = clamp(dot(w,k)/dot(k,k),0.0,1.0);
        vec2  c = vec2(ra,-he) + h*k;
        vec2  q = p - c;
        float d = dot(q,q);
        float s = w.x*k.y - w.y*k.x;
        if( d<res.x ) { res.xyz = vec3(d,q); }
        if( s>res.w ) { res.w = s; }
    }

    // distance and sign
    float d = sqrt(res.x)*sign(res.w);

    return d;
}

float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec4 lighthouse(vec2 p)
{
    vec3 red = vec3(.839,.318,.188);
    vec3 dred = vec3(.45,.133,.1);

    float shape = sdTrapezoid(p+vec2(0.,.1), .25, .15, .4);    
    shape = smoothstep(.02,.0, shape);
    vec4 col = vec4(shape * mix(red, vec3(1.), smoothstep(-.01, .01,fract((p.y+.1)* 2.4)*2.-1.)), shape);

    shape = sdBox(p+vec2(0.,-.42), vec2(.15,.1));
    shape = smoothstep(.01,.0, shape);
    col = vec4(mix(col.xyz, vec3(.4,.77,.82), shape), max(col.w, shape * .5));

    shape = abs(sdBox(p+vec2(0.,-.42), vec2(.15,.1)));
    shape = min(shape,sdBox(p+vec2(0.,-.31), vec2(.225,.01)));
    shape = min(shape,sdBox(p+vec2(0.,-.42), vec2(.15,.001)));
    shape = min(shape,sdBox(p+vec2(.05,-.42), vec2(.001,.1)));
    shape = min(shape,sdBox(p+vec2(-.05,-.42), vec2(.001,.1)));
    shape = min(shape,sdTriangle(p+vec2(.0,-.52), vec2(0.,.2), vec2(.2,0.),vec2(-.2,0.)));
    shape = smoothstep(.01, .0, shape);
    col = vec4(mix(col.xyz, red, shape), max(col.w, shape));

    shape = sdBox(p+vec2(0.,-.29), vec2(.225,.005));
    shape = min(shape, sdCircle(p + vec2(0.,.1), .04));
    shape = min(shape, sdCircle(p + vec2(0.,-.1), .04));
    shape = min(shape, sdCircle(p + vec2(0.,.35), .08));
    shape = min(shape, sdBox(p+vec2(0.,.435), vec2(.08,.07)));    
    shape = smoothstep(.01, .0, shape);    
    col = vec4(mix(col.xyz, dred, shape), max(col.w, shape));

    col.xyz = col.xyz - smoothstep(-.01, .01, p.x) *.2;        // Shadow    
    return col;
}

vec4 ice1(vec2 p)
{
    vec3 ice = vec3(.871,.871,.906);
    vec3 dice = vec3(.686,.784,.847);

    float shape = sdTriangle(p + vec2(.18,.0), vec2(0.,.2), vec2(.15,0.), vec2(-.15,0.));
    shape = min(shape,sdTriangle(p + vec2(.0,.0), vec2(0.,.25), vec2(.15,0.), vec2(-.3,0.)));
    shape = min(shape,sdTriangle(p + vec2(-.14,.0), vec2(0.,.2), vec2(.15,0.), vec2(-.15,0.)));
    shape = smoothstep(.01, .0, shape);

    vec4 col = vec4(mix(vec3(1.), ice, shape), shape);

    shape = sdTriangle(p + vec2(.18,.0), vec2(.0,.2), vec2(.15,0.), vec2(-.02,0.));
    shape = min(shape,sdTriangle(p + vec2(.18,.0), vec2(0.,.1), vec2(.35,0.), vec2(-.0,0.)));
    shape = min(shape,sdTriangle(p + vec2(.0,.0), vec2(0.,.25), vec2(.15,0.), vec2(-.0,0.)));
    shape = min(shape,sdTriangle(p + vec2(-.14,.0), vec2(0.,.2), vec2(.15,0.), vec2(-.0,0.)));    
    shape = smoothstep(.01, .0, shape);

    col = vec4(mix(col.xyz, dice, shape), max(col.w,shape));

    return col;
}

