vec4 boat(vec2 p)
{
    vec3 white = vec3(.98,.98,.945);
    vec3 dwhite = vec3(.929,.902,.859);
    vec3 yel = vec3(1.,.725,.063);
    vec3 dyel = vec3(.871,.529,.075);
    vec3 brown = vec3(.278, .165, .122);

    float shape = sdTriangle(p + vec2(.05,.05), vec2(0.,.15), vec2(.14,.02), vec2(-.14,.02));
    shape = min(shape,sdBox(p + vec2(.0,-.05), vec2(.05,.05)));
    shape = smoothstep(.01, .0, shape);

    vec4 col = vec4(mix(vec3(.5), white, shape), shape);

    shape = sdBox(p + vec2(-.045,-.02), vec2(.03,.08));
    shape = min(shape,sdBox(p + vec2(-.11,.005), vec2(.08,.035)));
    shape = smoothstep(.01, .0, shape);

    col = vec4(mix(col.xyz, dwhite, shape), max(col.w,shape));

    shape = sdTriangle(p + vec2(.18,.0), vec2(0.,-.15), vec2(.14,.02), vec2(-.14,.02));
    shape = min(shape,sdTriangle(p + vec2(.04,.0), vec2(0.,.02), vec2(.14,-.15), vec2(-.14,-.15)));
    shape = min(shape,sdBox(p + vec2(-.121,-.076), vec2(.022,.075)));
    shape = smoothstep(.01, .0, shape);

    col = vec4(mix(col.xyz, yel, shape), max(col.w,shape));

    shape = sdTriangle(p + vec2(.18,.0), vec2(0.,-.155), vec2(.14,-.05), vec2(-.085,-.05));
    shape = min(shape,sdTriangle(p + vec2(-.23,.0), vec2(0.,-.155), vec2(.03,-.05), vec2(-.13,-.05)));
    shape = min(shape,sdBox(p + vec2(-.025,.1025), vec2(.2,.053)));
    shape = smoothstep(.01, .0, shape);

    col = vec4(mix(col.xyz, dyel, shape), max(col.w,shape));

    shape = sdSegment(p, vec2(-.055,.105), vec2(.08,.105));
    shape = min(shape,sdSegment(p, vec2(.095,.085), vec2(.146,.085)));
    shape = min(shape,sdSegment(p, vec2(.095,.075), vec2(.146,.075)));
    shape = min(shape,sdBox(p + vec2(.025,-.06), vec2(.012,.025)));
    shape = min(shape,sdBox(p + vec2(-.015,-.06), vec2(.012,.025)));
    shape = min(shape,sdBox(p + vec2(-.055,-.06), vec2(.012,.025)));
    shape = min(shape,sdSegment(p, vec2(.015,-.04), vec2(.075,-.04)) - .005);
    shape = min(shape,sdSegment(p, vec2(.105,-.04), vec2(.165,-.04)) - .005);
    shape = min(shape,sdSegment(p, vec2(.195,-.04), vec2(.255,-.04)) - .005);
    shape = min(shape,sdSegment(p, vec2(-.24,-.085), vec2(.125,-.085)) - .002);
    shape = min(shape,sdCircle(p+ vec2(.1,.02), .005)); 
    shape = min(shape,abs(sdCircle(p+ vec2(.1,.04), .02) - .003));
    shape = min(shape,sdCircle(p+ vec2(.21,.02), .005)); 
    shape = min(shape,abs(sdCircle(p+ vec2(.21,.04), .02) - .003));  
    shape = smoothstep(.01, .0, shape);    
    col = vec4(mix(col.xyz, brown, shape), max(col.w,shape));

    return col;
}

vec4 cloud(vec2 p)
{
    vec3 cloud = vec3(.718,.863,.906);

    p *= vec2(1., 1.5);

    float shape = sdCircle(p+ vec2(.1,.0), .4);
    shape = min(shape,sdCircle(p + vec2(.4,.1), .3));
    shape = min(shape,sdCircle(p + vec2(-.2,.05), .35));
    shape = min(shape,sdCircle(p + vec2(-.4,.1), .3));
    shape = smoothstep(.2, .0, shape);

    vec4 col = vec4(mix(vec3(0.9), cloud, shape), shape);
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor -= fragColor;
    fragCoord = (fragCoord+fragCoord-iResolution.xy)/iResolution.y;
    float duration = .1;
    float time = iTime + 5.;

    float center = smoothstep(-.001, .001, fragCoord.y);
    vec4 sky = mix(vec4(.969, 1., .969,1.), vec4(.737, .804,.8,1.), length(fragCoord));
    vec4 water = mix(vec4(.094,.286,.286,1.), vec4(.063,.063,.125,1.), pow(length(fragCoord + vec2(.3,0.)), .5));
    vec4 image = mix(water, sky, center);    

    float prog = fract(time * duration);
    vec4 item = cloud((fragCoord + vec2(mix(.2, -.2, prog),-.2)) * 10.);
    image = mix(image, item, item.w);

    item = cloud((fragCoord + vec2(mix(.6, .2, prog),-.4)) * 10.);
    image = mix(image, item, item.w);

    item = cloud((fragCoord + vec2(mix(-.2, -.6, prog),-.5)) * 10.);
    image = mix(image, item, item.w);

    item = ice1(fragCoord + vec2(-.28,-.002));
    image = mix(image, item, item.w * center);

    item = ice2(fragCoord + vec2(.6,.01));
    image = mix(image, item, item.w);

    vec2 lhuv = (fragCoord + vec2(.3,-.169)) * 3.;
    item = lighthouse(lhuv);
    image = mix(image, item, item.w * center);

    item = ice3(fragCoord + vec2(.2,.03));
    image = mix(image, item, item.w);

    prog = smoothstep(0.,1.,fract(time*duration));
    vec2 boatuv = mix(fragCoord + vec2(-1.1,.0), fragCoord + vec2(1.1,.0), prog);
    boatuv.y += sin(time*4.) * .01;
    vec2 wuv = (fragCoord+vec2(-time*.2,.15)) * 4.;
    wuv.y *= 2.;
    float wave = wuv.y - pow(2.*abs(wuv.x-(floor(wuv.x+.5))),2.) * .5;
    item = boat(boatuv);
    image = mix(image, item, item.w * smoothstep(0.,.05, wave));

    prog = smoothstep(0.,1.,abs(sin(time*PI*.1)) * 2.);
    float mask = smoothstep(.01, .0,sdCircle(fragCoord, .75*prog));
    float shadow = smoothstep(.05, .0,sdCircle(fragCoord+vec2(-.05,.05), .75*prog));
    fragColor = vec4(.941,.945,.933,1.);
    fragColor = mix(fragColor, image, mask);
    fragColor -= (shadow - mask) * .25;    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = vec2(1.0-fragCoord.x/iResolution.x, fragCoord.y/iResolution.y);
    vec4 c = texture(iChannel0, pos);
    switch (int(iTime)/5%6) {
    case 0: fragColor = c.grba; break;
    case 1: fragColor = c.gbra; break;
    case 2: fragColor = c.rbga; break;
    case 3: fragColor = c.brga; break;
    case 4: fragColor = c.bgra; break;
    case 5: fragColor = c.rgba; break;
    }
}

////////////////////////////////////////////////////////////////
//
// Infinite Rolodex. mla, 2023
//
// Map unit circle to plane with mapping z -> z/(1-|z|Â²),
// geometrically this is like doing a stereographic projection
// from the circle to a hemisphere, then a gnomonic
// projection from the hemisphere to the entire plane.
//
// The plane is divided into cells, each containing mysterious
// images & writing, every one different, at least up to the
// limitations of the hash function.
//
// <mouse> to scroll
// <up/down> to zoom in and out
// 'm': mouse selection
// 'r': y-rotation
// 't': use tan map
// 'x': transform
//
////////////////////////////////////////////////////////////////

int textchar(ivec2 cindex, ivec2 ix) {
  uint h = ihash(ivec4(cindex,ix));
  return int(256.0*hfloat(h));
}

float text(vec2 pos, ivec2 ix) {
  vec4 metrics = vec4(0.25,0.75,0,1);
  float cwidth = metrics.y-metrics.x;
  float cheight = metrics.w-metrics.z;
  float ar = cheight/cwidth;
  int nrows = 10; 
  int ncols = int(float(nrows)*ar);
  // Grid structure
  vec2 cellsize = 1.0/vec2(ncols,nrows);
  ivec2 cellindex = ivec2(pos/cellsize);
  cellindex.y = nrows-cellindex.y-1; // Reverse order of rows
  int c = textchar(cellindex,ix);
  if (c < 0) return 0.0;
  vec2 p = mod(pos,cellsize)/cellsize.y;
  vec2 dx = dFdx(pos), dy = dFdy(pos);
  // Draw the character
  int charpos = int(p.x/cwidth);
  float charstart = cwidth*float(charpos); // character start
  vec2 uv = char(vec2(p.x-charstart+metrics.x,p.y),c);
  return textureGrad(iChannel1,uv,dx/16.0,dy/16.0).x;  
}

vec3 pcolor(vec2 pos, vec2 mpos) {
  vec3 col = vec3(0,0,0.1);
  float scale = exp(0.1*(float(keycount(KEY_DOWN)-keycount(KEY_UP))));
  if (!key(CHAR_X)) {
    if (length(pos) > 1.0) return col;
    if (!key(CHAR_T)) {
      // Inverse stereographic followed by gnomonic projection.
      pos /= 1.0-dot(pos,pos);
    } else {
      float t = tan(0.5*PI*length(pos));
      pos *= t/length(pos);
    }
    if (dot(mpos,mpos) < 1.0) {
      mpos /= 1.0-dot(mpos,mpos);
    }
  }
  pos *= scale;
  if (!key(CHAR_M)) pos -= mpos;
  //pos *= scale;
  if (!key(CHAR_R) && iMouse.z <= 0.0) pos.y += 10.0*iTime;
  ivec2 ix = ivec2(floor(pos));
  pos = fract(pos);
  col = texture(iChannel0,pos).xyz;
  col *= 1.2;
  col = pow(col,vec3(2.2));
  col *= 0.5+0.5*h2rgb(rand(ix));
  col = mix(col,vec3(0),text(pos,ix));
  float d = min(pos.x,pos.y);
  float px = fwidth(pos.x);
  float py = fwidth(pos.y);
  pos = fract(pos);
  pos = min(pos,1.0-pos);
  col *= 0.25+0.75*smoothstep(0.0,0.05,min(pos.x,pos.y));
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 mpos = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
  float AA = 2.0;
  vec3 aacol = vec3(0);
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 pos = ((2.0*(fragCoord+vec2(i,j)/AA))-iResolution.xy)/iResolution.y;
      aacol += pcolor(pos,mpos);
    }
  }
  aacol /= AA*AA;
  aacol = pow(aacol,vec3(0.4545));
  fragColor = vec4(aacol,1.0);
}

