float checker(vec2 pos){
    vec2 checker = mod(floor(pos), 2.0);
    vec2 dist = min(fract(pos), 1.0 - fract(pos));
    return mix(
        1.0,
        checker.x + checker.y,
        smoothstep(0.0, fwidth(length(pos)), min(dist.x, dist.y))
    );
}

float liner(vec2 pos){
    vec2 grid = abs(fract(pos - 0.25) - 0.25) / fwidth(length(pos));
    return 1.-clamp(min(grid.x, grid.y), 0., 1.);
}

