#define P_NUM 4
#define TXTF1(x) texelFetch(iChannel1, ivec2(x), 0)

mat3 rotate(vec3 v1, vec3 v2){
    if(v1==v2) return mat3(1.);
    float c = dot(v1, v2);
    vec3 A = cross(v1, v2);
    mat3 B = mat3(   0, -A.z,  A.y,
                   A.z,    0, -A.x,
                  -A.y,  A.x,    0);
    A = normalize(A);
    return mat3(c) + (1.-c) * outerProduct(A, A) + B;
}

void mainImage( out vec4 Clr, in vec2 Crd ){

    int id = XY2ID(Crd);
    if( id>=P_NUM ) {Clr=vec4(0); return;}

    if( iFrame==0 && id!=0){
        Clr = vec4(mat3(1)[id-1],1);
        return;
    }

    vec4 m = iMouse;
    if( m.z<0.0 ) {Clr = TXTF1(Crd); return;}

    float sc = min(iResolution.x, iResolution.y);
    vec3 xyz = vu2xyz((m.xy*2.-iResolution.xy)/sc);
    if( id==0 ){    
        Clr = vec4(xyz,1);
        return;
    }
    if( m.w>0.0 ) {Clr = TXTF1(Crd); return;}
    mat3 MR = GETR1*rotate(GET1(0).xyz, xyz);
    Clr = vec4(MR[id-1],1);

}

