// Physicaly based, shmisicaly shmased
float simpleMie(float costh) {
  return ((costh+0.7)*costh*costh*costh+0.2)*2.;
}

vec3 getSky(vec3 ro, vec3 rd, vec3 sun){
    float mouse = getMouse().y;
    float rds = dot(sun, rd);
    vec3 sunset = exp(-0.01*vec3(1,2,4)/mouse-0.4);
    vec3 light = vec3(smoothstep(0.997, 1.2, rds))*100000.*vec3(1,0.8,0.6)*sunset;
	float g = 0.;
    float mid = CLOUD_HEIGHT;
    float delt = 40. ;
    float idelt = 1./delt;
    float start = (mid - delt)/rd.y;
    float end = (mid + delt)/rd.y;
    float j=start;
	vec3 ccol = vec3(0.);
    float m = simpleMie(rds);
    float T = 0.;
	if (rd.y > 0.) {
        float dj = 1.5/rd.y;
		for (; j<end;j+=dj){
            vec3 s = (rd*j+vec3(ro.x,0.,ro.z))*0.01;
            float ddd = 0.2;
            float xj = fbb(s+sun*ddd); 
            float sub = (s.y-mid)*idelt*0.01-0.2;
            float subb = ((s.y+sun.y*ddd)-mid)*idelt*0.01-0.2;
			vec2 d = fb(s)+sub;
			float dd = xj+subb;
            float dir_deriv = (d.y-dd)/ddd;
			g +=  max(0.,d.x*3.);
			vec3 col = sunset*( 
                (max(0.,dir_deriv)*3.+0.2)*m+
                d.y*vec3(0.1,0.2,0.3)*exp((g+2.)*0.1)
            );
			ccol += col*exp(g*-vec3(.3,0.4,0.5));
            if (g > 9.){
                break;
            }
		}
     } 
     light = mix(mix(ccol,light,exp(-1.5*g)),vec3(0.7), smoothstep(0.,-.1,rd.y));
     float at = CLOUD_HEIGHT/rd.y*0.0001;

     return 
         light*exp(-vec3(1,2,4)*1.3*at)+
         7.*(1.-exp(-at*3.5*vec3(1,2,4)*sunset));
}

vec3 render(vec3 ro, vec3 rd){
    vec2 mou = getMouse();
    float mx = -mou.x*10.;
    float my = mou.y*2.;
    vec3 sun = normalize(vec3(sin(mx),max(0.,sin(my-0.3)*2.),cos(mx)));
    return getSky(ro,rd,sun);
}

