void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    int  dataPage;
    vec3 worldPos = WorldPosFromCube(fragCoord, rayDir, dataPage);
    if (dataPage == 1)
    {
        vec2  currentSpin  = texture(iChannel0, rayDir).rg;
        vec2  neighbourSum = texture(iChannel0, CubeFromWorldPos((worldPos+vec3( 1, 0, 0)), dataPage)).rg
                            +texture(iChannel0, CubeFromWorldPos((worldPos+vec3(-1, 0, 0)), dataPage)).rg
                            +texture(iChannel0, CubeFromWorldPos((worldPos+vec3( 0, 1, 0)), dataPage)).rg
                            +texture(iChannel0, CubeFromWorldPos((worldPos+vec3( 0,-1, 0)), dataPage)).rg
                            +texture(iChannel0, CubeFromWorldPos((worldPos+vec3( 0, 0, 1)), dataPage)).rg
                            +texture(iChannel0, CubeFromWorldPos((worldPos+vec3( 0, 0,-1)), dataPage)).rg;
        float energy       = dot(H + J*neighbourSum, currentSpin);
        float flip_rand    = Random(length(worldPos/54.12345 + vec3(1.65546, 0.654323, 0.73243)) + fract(iTime/13.));
        vec2 newSpin       = RandomCircle(flip_rand/DT);
        float energy_flip  = dot(H + J*neighbourSum, (currentSpin-newSpin));
        if      (iFrame <= 5)
        {
            currentSpin = RandomCircle(flip_rand + 1.);
        }
        else if ((flip_rand < DT) && (exp(-max(energy_flip/T, 0.)) > Random(flip_rand*2./DT)))
        {
            currentSpin  = newSpin;
        }
        fragColor = vec4(currentSpin, 1., -dot(neighbourSum, currentSpin)-length(neighbourSum));
    }
    else
    {
        fragColor = vec4(1,0,1,0);
    }
}

