// Given a vec2 in [-1,+1], generate a texture coord in [0,+1]
vec2 Distort(vec2 uv, float power)
{
    float theta  = atan(uv.y, uv.x);
    float radius = length(uv);
    radius = pow(radius, power);
    uv.x = radius * cos(theta);
    uv.y = radius * sin(theta);
    return 0.5 * (uv + 1.0);
}

