float worley(vec3 p, float power)
{
    float finalDist = 9999999.0;
    for(int x = -1; x <= 1; x++)
    {
        for(int y = -1; y <= 1; y++)
        {
            for(int z = -1; z <= 1; z++)
            {
                vec3 offset = vec3(x, y, z);
                float dist = pow(distance(p, floor(p)+offset+hash33(floor(p)+offset)), power);
                finalDist = min(finalDist, dist);
            }
        }
    }
   return finalDist;
}

