bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

float scale = 1.5;
const float PI = 3.1415927;
const float TWOPI = 2.0*PI;

#define map(screen) (scale*(2.0*screen-iResolution.xy)/iResolution.y)

int nselections = 1;
vec2 initselection(int i) {
  if (i == 0) return vec2(0.0);
  return vec2(0);
}

#define getselection(i) (texelFetch(iChannel2,ivec2((i)+1,3),0).xy)
#define setscale() \
  (scale *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP))), \
   scale *= key(CHAR_Z) ? 2.0 : 4.0)

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb);       
  return v * mix(vec3(1.0),rgb,s);
}

vec3 h2rgb(float h) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  return rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
}

float segment(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
}

float line(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  return length(p-b*h);
}

int idiv(int a,int b) {
  return a >= 0 ? a/b : -((-a+b-1)/b);
}

int imod(int a, int b) {
  int d = idiv(a,b);
  return a-d*b;
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint ihash(uint x) {
    x += 0x12345678u;
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

float rand(ivec2 p) {
  uint h = ihash(uint(p.x)^ihash(uint(p.y)));
  return float(h)/exp2(32.0);
}

float rand(ivec3 p) {
  uint h = ihash(uint(p.x));
  h = ihash(uint(p.y)^h);
  h = ihash(uint(p.z)^h);
  return float(h)/exp2(32.0);
}

// Rotate vector p by angle t.
vec2 rotate(vec2 p, float t) {
  return cos(t)*p + sin(t)*vec2(-p.y,p.x);
