float sdf(vec3 p)
{
    p -= vec3(iTime * 0.31, iTime * 0.1, -1.01);
    vec3 idx = round(p / 2.0);
    p -= idx * 2.0;

    float angle = iTime + idx.x + idx.y * 1.2 + idx.z * 2.3;
    vec3 az = vec3(cos(angle), sin(angle), 1) * sqrt(0.5);
    vec3 ax = normalize(cross(vec3(0,1,0), az));
    vec3 ay = cross(az, ax);
    vec3 q = vec3(dot(p, ax), dot(p, ay), dot(p, az));
    vec3 r = abs(q);
    return max(max(r.x, r.y), r.z) - 0.3;
}

