void mainImage(out vec4 o, vec2 u)
{
    float d,        // distance
          acc,      // accumulator
          inc = 1., // accumulator increment size
          i,        // index
          l,        // distance at hit position
          t = iTime;

    vec2 R = iResolution.xy,
         p; // 2.5D position
    u = (u+u-R)/R.y;             

    ivec4 P; // integer positions for 3 (coplanar) axes, and int(d)

    vec4 col, 
         hitcol;

    bool hasHit = false;

    // use > 330. for more depth (and more lag!)
    // alternatively, iterate with d and remove i
    while(i++ < 330.)
    {       
        if ((abs((P.x^P.y^P.z)-P.w*3)) % 128 < int(d)) {
            if (!hasHit) {
                hasHit = true;
                l = d;
                hitcol = getCol(P);
            }
            inc *= .95; // accumulate less after every hit
            acc += inc; 

            // add to color (use weighted average)
            col += inc * getCol(P);

            // less accumulation / iterations / lag
            if (inc < .25)
                break;            
        }
        p = u*d + 5.*t;
        p += d*vec2(cos(.31*t), sin(.2*t));

        d += .15;

