vec2 map(vec3 p){
    vec2 res = vec2(1e5,0.);

    p.yz*=flip; p.xz*=turn;

    vec3 q = p;

    vec3 id = floor((q + hlf)/size)-grid;
    float chk = mod(id.y+mod(id.z+id.x,2.),2.)*2.-1.;

    q = q-size*clamp(round(q/size),-grid,grid);

    float hs = hash21(id.xz+id.y);

    if( hs>.5) q.xz*=r90;
    if(chk>.5) q.zy*=r90;

    float sn = mod(hs,5.)*5.;
    float t1 = lsp(sn,sn+2.,tmod);
    float t2 = lsp(sn+8.,sn+10.,tmod);

    t1 = eoc(t1); t1 = t1*t1*t1;
    t2 = eoc(t2); t2 = t2*t2*t2;

    cam=rot((t1-t2)*1.5707);

    if(chk>.5) { q.zx*=cam; } else { q.xy*=cam; }

    float xhs = fract(2.31*hs+id.y);

    q1 = vec3(q+vec3(0,hlf.x,-hlf.y)).yxz;
    q2 = q+vec3(hlf.x,0,hlf.z);
    q3 = vec3(q-vec3(hlf.xy,0)).yzx;


    trh = trs(q1,vec2(hlf.x,thick));
    trx = trs(q2,vec2(hlf.x,thick));
    jre = trs(q3,vec2(hlf.x,thick));

    float cut = box(q,bs*.90);

    trh=max(abs(trh)-bg,trh);
    jre=max(abs(jre)-bg,jre);
    trx=max(abs(trx)-bg,trx);

    trh=max(trh,cut);
    jre=max(jre,cut);
    trx=max(trx,cut);

    if(trh<res.x ) {
        res = vec2(trh,2.);
    } 
    if(jre<res.x ) {
        res = vec2(jre,4.);
    }
    if(trx<res.x ) {
        res = vec2(trx,6.);
    } 

    return res;
}

