vec3 render(in vec2 uv, in vec2 F )
{    
    vec3 C = hsv2rgb(vec3(T*.015,.7,.3))*.25;
    vec3 ro = vec3(0,0,4.25),
         rd = normalize(vec3(uv,-1));

    tspeed = T*.6;
    tmod = mod(tspeed,25.);

    // bounces - set lower if slow 10. to 5.
    float b = 7.;

    vec3  p = ro + rd * .1;
    float atten = 1., k = 1., iv = 1., alpha = 1.;

    // loop inspired/adapted from @blackle's 
    // marcher https://www.shadertoy.com/view/flsGDH
    for(int i=0;i<128;i++)
    {
        vec2 ray = map(p);
        vec3 n = vec3(0);

        float d = i<42? ray.x*.25: ray.x*.65;
        float m = ray.y;

        p += rd * d * k;

        if (d*d < 1e-7) {
            C+=shade(p,rd,d,ray.y,n)*atten;

            b -= 1.;
            if(b<1.) break;

            atten *= .8;
            p += rd* .02;
            k = sign(map(p).x);

            vec3 rf=refract(rd,n,iv>0.?.875:1.1);
            iv *= -1.;

            if(length(rf) == 0.) rf = reflect(rd,n);

            rd=rf;
            p+=-n*.001;
        } 
        if(distance(p,rd)>45.) { break; }
    }

    return C;
}

