////////////////////////////////////////////////////////////////////////////////
//
// Wythoff construction for dual polyhedra, with an exact SDF.
// Matthew Arcus, mla, 2023
//
// https://en.wikipedia.org/wiki/Wythoff_construction
//
// Controls:
// Mouse control is modal: rotation mode applies a rotation,
// configuration mode sets face position. Key 'x' toggles
// between modes. 'a' sets animation mode when the face
// configuration is automatic, so mouse mode is always
// rotation.
// <up>/<down>: zoom
// a: animation mode (ignore mouse setting)
// e: show edges
// f: color edges
// m: mirror faces
// r: rotation
// s: exploded view
// x: toggle mouse mode between rotation & configuration
//
// Uses exact distance to triangular faces (or subdivisions of faces) 
// rather than distance to face planes, enabling, eg. SDF rounding
// to work.
//
////////////////////////////////////////////////////////////////////////////////

float maxdist = 10.0;
float lradius = 0.05;
bool coloredges = true;
bool doedges = false;

// Any P,Q,R with 1/P + 1/Q + 1/R > 1, which means:
// 2:2:2, 3:2:2, 4:2:2, ...
// 3:3:2, 4:3:2, 5:3:2
int P = 2, Q = 3, R = 5;

// Number of iterations of folding loop
// 5 iterations is enough to get up to (5 3 2) or (10 2 2)
int NFOLDS = 5;

// Types of feature
int FACE0 = 0;
int FACE1 = 1;
int EDGE = 2;

// 3 mirror planes (passing through origin) define a fundamental
// region, which intersects the unit sphere in a spherical
// triangle with angles PI/P, PI/Q, PI/R.
mat3 ABC;    // normal vectors for mirror planes
mat3 abc;    // dual vertices
vec3 vertex; // centre point of region
void initgeometry() {
  // Setup folding planes
  float p = PI/float(P);
  float q = PI/float(Q);
  float r = PI/float(R);
  // |A| = |B| = |C| = 1
  // A.B = -cos(p)
  // A.C = -cos(r)
  // B.C = -cos(q) = cos(p)cos(r) + y sin(p)
  vec3 A = vec3(1,0,0);
  vec3 B = vec3(-cos(p),sin(p),0);
  float x = -cos(r);
  float y = -(cos(p)*cos(r)+cos(q))/sin(p);
  float z = sqrt(1.0-x*x-y*y);
  vec3 C = vec3(x,y,z);
  ABC = mat3(A,B,C);
  // Convert trilinear to cartesian coordinates
  vec3 trilinear = vec3(1,1,1);
  vertex = normalize(mat3(cross(B,C),cross(C,A),cross(A,B))*trilinear);

  // abc is basically inverse(ABC), but with normalized columns
  vec3 a = normalize(cross(B,C));
  vec3 b = normalize(cross(C,A));
  vec3 c = normalize(cross(A,B));
  vec2 params = 1.0+sin(0.1*iTime*PI*vec2(1,0.618));
  if (!key(CHAR_A)) {
    vec2 mouse = store(CHAR_X,2).zw; // Default setting
    if (mouse.x > 0.0) params = 4.0*mouse.xy/iResolution.xy;
  }
  b *= params.x; c *= params.y;
  abc = mat3(a,b,c);
}

// Adapted from function by iq: https://www.shadertoy.com/view/ttfGWl
// Return square of the distance to the triangle.
float triangleDistance2(vec3 p, mat3 abc, out bool isedge) {
  vec3 v0 = abc[0], v1 = abc[1], v2 = abc[2];
  vec3 v10 = v1-v0; vec3 p0 = p-v0;
  vec3 v21 = v2-v1; vec3 p1 = p-v1;
  vec3 v02 = v0-v2; vec3 p2 = p-v2;
  vec3 n = cross(v10,v02);
  // If we are outside the triangle (one of the triple products is negative)
  // then the closest point might be on any of the edges.
  isedge = doedges || triple(p0,v10,n) < 0.0 || triple(p1,v21,n) < 0.0 || triple(p2,v02,n) < 0.0;
  if (isedge) return min(segment2(p0,v10),min(segment2(p1,v21),segment2(p2,v02)));
  return length2(n*dot(n,p0)/dot(n,n));
}

float scene(vec3 pos, int parity, out int type, out vec2 uv) {
  uv = pos.xy + pos.yz;
  if (key(CHAR_S)) pos -= (1.0+sin(iTime))*vertex;
  bool isedge;
  float d = sqrt(triangleDistance2(pos,abc,isedge))-lradius;
  type = parity%2 == 0 ? FACE0 : FACE1;
  if (coloredges && isedge) type = EDGE;
  return d;
}

vec3 fold(vec3 pos,out int parity) {
  vec3 A = ABC[0], B = ABC[1], C = ABC[2];
  parity = 0;
  for (int i = 0; i < NFOLDS; i++) {
    float k;
    k = dot(pos,A);
    int parity0 = 0;
    parity0 += int(k < 0.0);
    pos -= 2.0*min(0.0,k)*A;
    k = dot(pos,B);
    parity0 += int(k < 0.0);
    pos -= 2.0*min(0.0,k)*B;
    k = dot(pos,C);
    parity0 += int(k < 0.0);
    pos -= 2.0*min(0.0,k)*C;
    if (parity0 == 0) break;
    parity += parity0;
  }
  return pos;
}

float map(vec3 pos, out int type, out int parity, out vec2 uv) {
  pos = fold(pos,parity);
  return scene(pos,parity,type,uv);
}

float map(vec3 pos) {
  int type, parity; vec2 uv;
  return map(pos,type,parity,uv);
}

// Tetrahedron technique, from:
// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(vec3 p) {
  float h = 0.001;
  vec2 k = vec2(1,-1);
  return normalize( k.xyy*map( p + k.xyy*h ) + 
                    k.yyx*map( p + k.yyx*h ) + 
                    k.yxy*map( p + k.yxy*h ) + 
                    k.xxx*map( p + k.xxx*h ) );
}

float march(in vec3 ro, in vec3 rd) {
  const float precis = 0.001;
  float t = 0.0;
  float res = -1.0;
  for (int i = 0; i < 200; i++) {
    //assert(i < 30);
    if (t > maxdist) break;
    float h = map(ro+rd*t);
    if (key(CHAR_Z) && h < precis) break;
    t += h;
    if (h < precis) break;
  }
  return t;
}

vec3 transform(in vec3 p) {
  vec2 mouse = iMouse.xy;
  if (!key(CHAR_A)) mouse = store(CHAR_X,2).xy;
  if (mouse.x > 0.0) {
    float theta = (2.0*mouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*mouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (!key(CHAR_R)) {
    //p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.2);
  }
  return p;
}

vec3 basecolor(int type) {
  if (type == FACE0) return vec3(1,1,0.5);
  if (type == FACE1) return vec3(1,0.25,0.25);
  if (type == EDGE) return vec3(0.2);
  return vec3(1,0,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;
  float camera = 4.0;
  camera *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  doedges = key(CHAR_E);
  coloredges = key(CHAR_F);
  vec3 ro = vec3(0,0,camera);
  vec3 rd = vec3(uv, -2);
  vec3 light = vec3(0.5, 0.8, 3.0);
  ro = transform(ro);
  rd = transform(rd);
  light = transform(light);
  light = normalize(light);
  rd = normalize(rd);
  initgeometry();
  vec3 col = vec3(0);
  //col = texture(iChannel1,uv).xyz; col = pow(col,vec3(2.2));
  float t = march(ro, rd);
  if (t < maxdist) {
    vec3 pos = ro + t * rd;
    vec3 n = calcNormal(pos);
    int type, parity;
    vec2 uv;
    map(pos,type,parity,uv);
    if (key(CHAR_M)) {
      col = texture(iChannel0,reflect(rd,n)).xyz;
    } else {
      col = basecolor(type);
      col *= texture(iChannel1,uv).xyz;
      float diffuse = clamp(dot(n, light), 0.0, 1.0);
      col *= 0.2 + 0.8*diffuse;
      float specular = pow(max(0.0,dot(reflect(light,n),rd)),10.0);
      col += 0.3*specular*vec3(1);
    }
  }
  col = pow(col,vec3(0.4545));
  if (alert) col.x = 1.0;
  fragColor = vec4(col, 1.0);
}

