const float PI	= 3.14159265359;

bool alert = false;
void assert(bool test) {
  if (!test) alert = true;
}

vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

float length2(vec3 p) {
  return dot(p,p);
}

// Perpendicular distance from p to segment from origin to b
float segment2(vec3 p, vec3 b) {
  float t = dot(p,b)/dot(b,b);
  t = clamp(t,0.0,1.0);
  return length2(p-t*b);
}

// Perpendicular distance from p to segment ab
float segment2(vec3 p, vec3 a, vec3 b) {
  return segment2(p-a,b-a);
}

// Triple product
float triple(vec3 p,vec3 q, vec3 r) { return dot(cross(p,q),r); }

