void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 accumulator = vec3(0.0);
    vec3 divisor = vec3(0.0);

    float r = 1.0;
    // ç§»è½´æ¨¡ç³åååæ¨¡ç³çåç§»
    //float my = 1.0-2.0*iMouse.y/iResolution.y;
    vec2 mxy = 1.0-2.0*iMouse.xy/iResolution.xy;

    // ç§»è½´æ¨¡ç³åååæ¨¡ç³çè§åº¦
    //vec2 angle = vec2(0.0, _Radius * saturate(TiltShiftMask(uv, my)));
    vec2 angle = vec2(0.0, _Radius * saturate(IrisMask(uv, mxy)));
    for (int j = 0; j < _Iteration; j ++)
    {
        r += 1.0 / r;
        angle = angle * _Rot;
        vec3 bokeh = texture(iChannel0, uv + (r-1.0)*angle/vec2(iResolution.x, iResolution.y)).rgb;
        accumulator += bokeh * bokeh;
        divisor += bokeh;
    }
    vec3 col = accumulator / divisor;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

