mat2 _Rot = mat2(cos(2.39996323), sin(2.39996323), -sin(2.39996323), cos(2.39996323));
float _Offset = 0.0;
float _Area = 1.0;
float _Spread = 1.2f;
int _Iteration = 32;
float _Radius = 2.0;


float TiltShiftMask(vec2 uv, float offset)
{
    float centerY = uv.y * 2.0 - 1.0 + offset;
    return pow(abs(centerY * _Area), _Spread);
}

float IrisMask(vec2 uv, vec2 offset)
{
    vec2 center = uv * 2.0 - 1.0 + offset;
    return dot(center, center)*_Area;
}

float saturate(float r)
{
    if(r<0.0){
        return 0.0;
    }
    if(r>1.0){
        return 1.0;
    }
    return r;
}

