/// by Dom Mandy in 2023


/*
Complex arithmetic.
*/
vec2 power(vec2 a, float b) { 
    float angle = atan(a.y, a.x);
    vec2 direction = vec2(
        cos(b * angle), 
        sin(b * angle) 
    ); 

    return direction * pow(dot(a, a), b / 2.);
}
vec2 divide(vec2 a, vec2 b)  { 
    vec2 dividend = vec2( 
        dot(a, b), 
        dot(vec2(a.y, -a.x), b)
    );

    return dividend / dot(b, b);
}
vec2 multiply(vec2 a, vec2 b) { 
    return vec2( 
        a[0] * b[0] - a[1] * b[1], 
        a[0] * b[1] + a[1] * b[0]
    ); 
}


/*
Differential multiplication.
*/
mat4x2 multiply(mat4x2 a, mat4x2 b) {
    mat4x2 products;
    int degree, i, j;
    // Wedge product.
    for (; degree < 4; degree++) {
        for (i = 0; i <= degree; i++) {
            j = degree - i;
            // Differential product rule.
            products[degree] += multiply(a[i], b[j]);
        }
    }

    return products;
}


/*
BÃ¶ttcher's Map
*/
vec2 bottcher(vec2 a, vec2 b, float count) {
    vec2 base = divide(a, a - b);
    float exponent = pow(.5, count);
    return power(base, exponent);        
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Zoom pixel space to view space
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.x;
    vec2 uvStart = 2.2 * (2. * fragCoord - iResolution.xy) / iResolution.x;
    vec2 uvEnd = (uvStart * .03) + vec2(.4, .5);
    vec2 uvMouse = 2. * iMouse.xy - iResolution.xy;
    uv = mix(uvStart, uvEnd, .5 - .5 * cos(iTime / 4.));

    // 4th order differential supernumbers as higher order differential forms
    mat4x2 orbit, offset;
    vec2 mapped;

    // Rules for mandelbrot set
    orbit[0] = mapped = uv;
    // Image space is a linear grid, so it has constant 1st order derivatives.
    // No higher order derivatives (those grow while orbiting).
    orbit[1] = vec2(1.);

    float time = 1.3 + iTime / 2.;
    float r = .618;
    offset[0] = vec2(
        r * cos(time),
        r * sin(time)
    );

    // Simulate the trajectory for this pixel relative to the center of the image.
    vec2 trapped;
    float radius = 9e9;
    for (float i; i < 47.; i++) {
        // This recursion generates chaotic behavior
        orbit = multiply(orbit, orbit) + offset;
        // Track coordinate deformation
        mapped = multiply(mapped, bottcher(orbit[0], offset[0], i));
        // We want to record the nearest approach as the pixel orbits around the image center.
        float flyby = dot(orbit[0], orbit[0]);
        radius = min(radius, flyby);
        // The closest point is called the orbit trap.
        trapped = trapped * float(radius != flyby) + float(radius == flyby) * orbit[0];
        // Stop in case of overflow
        if(flyby > 9e7) break;
    }

    // The derivatives need to be scaled by their binomial taylor coefficients
    vec4 differentials = vec4(
        length(orbit[0]),
        length(orbit[1]),
        2. * length(orbit[2]),
        6. * length(orbit[3])
    );

    // The solution curve is everywhere flat.
    // This means that the ratios of the derivatives should be 
    // the same for all degrees when we are close to the solution.
    vec3 estimates = vec3(
        differentials[0] / differentials[1], 
        differentials[1] / differentials[2], 
        differentials[2] / differentials[3]
    );
    // Weight by relative convergence rate
    vec3 weights = vec3(2., 3., 4.);
    // Combine for smoother distance estimate.
    float est = dot(estimates, weights) / dot(weights, vec3(1.));
    // Distance = log|f| |f|/|f'|
    float dist = est * log(differentials[0]);
    float lightness = smoothstep(1.3, 0., pow(dist, .1));
    // Grid
    float approach = length(trapped);
    float scale = 64.;
    float grid = .5 + .5 * pow(clamp(max(cos(scale * mapped.x), cos(scale * mapped.y)), 0., 1.), 99.);
    float outside = float(lightness > 0.);
    lightness = outside * mix(lightness, 2.2 * lightness, grid * smoothstep(0., 1.4, log(approach)));
    // Highlight the chaotic tree structure inside the fractal
    float inside = float(dist < 0. || lightness <= 0.);
    lightness += inside * smoothstep(.8, .0, approach);
    lightness = clamp(lightness, 0., 1.);
    // Find the angle of closest approach of the orbit trap
    float angle = atan(trapped.y, trapped.x);
    // Match the angle to the color wheel
    float hue = angle - atan(uvMouse.y, uvMouse.x) + iTime;
    // The pixel gets greyer the farther the orbit trap
    float chroma = mix(.95, .3, smoothstep(0., 3., approach));

    // Paint the pixel
    fragColor.rgb = oklch2rgb(lightness, chroma, hue);
    fragColor.a = 1.;
}


