float _BlurRadius = 1.0;
int _Iteration = 32;
mat2 _Rot = mat2(cos(2.39996323), sin(2.39996323), -sin(2.39996323), cos(2.39996323));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 accumulator = vec3(0.0);
    vec3 divisor = vec3(0.0);

    float r = 1.0;
    vec2 angle = vec2(0.0, _BlurRadius);
    for(int j = 0; j < _Iteration; j++)
    {
        r += 1.0 / r;
        angle = angle*_Rot;
        vec3 bokeh = texture(iChannel0, uv + (r-1.0)*angle/vec2(iResolution.x,iResolution.y)).rgb;
        accumulator += bokeh * bokeh;
        divisor += bokeh;
    }
    vec3 col = accumulator / divisor;

    // Output to screen
    fragColor = vec4(col,1.0);
}

