float PI = 3.14159265358979323846;
float TWOPI = 6.28318530718;
float PIONTWO = 1.57079632679;

float atan2(float y, float x)
{
    //this computes arctan(y/x)
    float angle = atan(y,x);
    if(angle < 0.0){angle+= TWOPI;}
    return(angle);
}

float norm(vec2 uv)
{
    return(float(uv.x*uv.x + uv.y*uv.y));
}

vec2 ix(vec2 uv)
{
    //this computes i*uv;
    return vec2(-uv.y,uv.x);
}

vec2 times (vec2 z1, vec2 z2)
{
    return vec2(z1.x*z2.x - z1.y*z2.y , z1.x*z2.y + z1.y*z2.x);
}

vec2 oneoverz(vec2 uv)
{
    float squaremodulus = norm(uv);
    return vec2(uv.x/squaremodulus, -uv.y/squaremodulus);
}

vec2 log2d(vec2 uv)
{
    return vec2(log(length(uv)) , atan2(uv.y,uv.x));
}

vec2 exp2d(vec2 uv)
{
    return vec2( exp(uv.x)*vec2(cos(uv.y),sin(uv.y)) );
}

vec2 cos2d(vec2 uv)
{
    vec2 expo = exp2d(ix(uv));
    return 0.5*( expo + oneoverz(expo) );
}


vec2 sin2d(vec2 uv)
{
    vec2 expo = exp2d(ix(uv));
    return ix(0.5*( expo - oneoverz(expo) ) );
}

vec2 square(vec2 uv)
{
    return vec2(uv.x*uv.x - uv.y*uv.y , 2.0*uv.x*uv.y);
}

vec2 cube(vec2 uv)
{
    return vec2( uv.x*uv.x*uv.x - 3.0*uv.x*uv.y*uv.y, 3.0*uv.x*uv.x*uv.y - uv.y*uv.y*uv.y );
}

