void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //Offset
    uv = uv - 0.5;
    uv.x += 0.0;

    //Scale
    uv *= 4.0;
    uv.x *= iResolution.x / iResolution.y;

     //Choosing the Zoom
    vec2 zoomPoint = vec2(0.0);

    //Zoom
    float zoomAmount = min(1.0,1.0/pow(max(iTime-3.0,1.0),3.0));
    //uv = uv*zoomAmount;
    uv += zoomPoint;

    // Algorithm to color the pixel
    float tol = pow(10.0,-6.0);


    float DistanceToPrevious = 1.0;
    float currentModulus = length(uv);
    vec2 previousIteration;
    int iterationAmount;
    int testAmount = 1000;

    vec2 c = 2.0*(iMouse.xy/iResolution.xy - 0.5);
    //float residue = mod(iTime,15.0)/15.0;
    //vec2 c = uv;

    for (iterationAmount = 0; iterationAmount < testAmount; iterationAmount++)
    {
        previousIteration = uv;
        uv = function(uv,c);
        currentModulus = length(uv);
        DistanceToPrevious = length(uv - previousIteration);

        if(DistanceToPrevious < tol){break;}
        if(currentModulus > 300000000.0){break;}
    }



    if(currentModulus < 300000000.0)
    {
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }

    else
    {
            iterationAmount = int(log(float(iterationAmount)));
            int periodFast = 5;
            int residueFast = iterationAmount%periodFast;
            float indexFast = float(residueFast)/float(periodFast);
            vec3 colorFast = palette4(indexFast);

            int periodMedium = 20;
            int residueMedium = iterationAmount%periodMedium;
            float indexMedium = float(residueMedium)/float(periodMedium);
            vec3 colorMedium = palette4(indexMedium);

            int periodSlow = 50;
            int residueSlow = iterationAmount%periodSlow;
            float indexSlow = float(residueSlow)/float(periodSlow);
            vec3 colorSlow = palette4(indexSlow);

            float finalIndex = (indexSlow + indexMedium + indexFast)*0.333333333;
            vec3 finalColor = (colorSlow + colorMedium + colorFast)*0.3333333333;
            fragColor = vec4(finalColor,1.0);
    }
}

