#define R (iResolution.xy)
#define T (iTime)
#define ZERO min(iFrame, 0)
#define M_PI 3.1415
#define TAU (M_PI*2.0)
#define adot(v1, v2) abs(dot(v1, v2))

#define STEPS 100
#define SHADOW_STEPS 24
#define NEAR 0.003
#define FAR 128.0
#define AMBIENT 0.3
#define FX 1
#define FX_AMOUNT cos(T*0.5)*0.1


vec3 noise(in vec2 p, in float s) {
    p += s * 256.0;
    return textureLod(iChannel3, p / 256.0, 0.0).rgb;
}

vec3 noise(in vec2 p, in float s, in float freq, const in int oct) {
    float div = 0.0;
    float amp = 1.0;
    vec3 n = vec3(0.0);

    for (int i = 0; i < oct; i++) {
        n += amp * noise(p*freq, s);
        div += amp;
        amp *= 0.5;
        freq *= 2.0;
    }

    return n / div;
}

vec3 snoise(in vec2 p, in float s) {
    vec2 id = floor(p);
    vec2 lv = fract(p);
    lv = lv * lv * (3.0 - 2.0 * lv);
    return mix(
        mix(noise(id, s), noise(id+vec2(1, 0), s), lv.x),
        mix(noise(id+vec2(0, 1), s), noise(id+vec2(1, 1), s), lv.x),
        lv.y
    );
}

vec3 snoise(in vec2 p, in float s, in float freq, const in int oct) {
    float div = 0.0;
    float amp = 1.0;
    vec3 n = vec3(0.0);

    for (int i = 0; i < oct; i++) {
        n += amp * snoise(p*freq, s);
        div += amp;
        amp *= 0.5;
        freq *= 2.0;
    }

    return n / div;
}


struct Translation {
    vec3 p;
    vec3 r;
};

#define NEW_TRANSLATION Translation(vec3(0.0), vec3(0.0))

mat2 rot(in float a) { float c = cos(a); float s = sin(a); return mat2(c, s, -s, c); }

vec3 pointRot(in vec3 p, in vec3 r) {
    p.yz *= rot(r.x);
    p.xz *= rot(r.y);
    p.xy *= rot(r.z);
    return p;
}

vec3 pointRotInv(in vec3 p, in vec3 r) {
    p.yz *= inverse(rot(r.x));
    p.xz *= inverse(rot(r.y));
    p.xy *= inverse(rot(r.z));
    return p;
}

struct Light {
    vec3 p;
    vec3 d;
    vec3 c;
    int t;
    float s;
};

#define LIGHT_POINT 0
#define LIGHT_DIR 1
#define LIGHT_AMB 2

vec3 getLightDir(in Light light, in vec3 p) {
    return light.t == LIGHT_POINT ? normalize(light.p - p) : 
        light.t == LIGHT_DIR ? normalize(light.d) : normalize(light.p);
}

vec3 getLightAtt(in Light light, in vec3 p, in vec3 N) {
    vec3 L = getLightDir(light, p);
    float NdotL = max(0.0, dot(N, L));

    switch (light.t) {
        case LIGHT_POINT: {
            return light.c * NdotL * (pow(light.s, 2.0) / max(0.001, pow(distance(light.p, p), 2.0)));
        }; break;
        case LIGHT_DIR: {
            light.s * light.c * NdotL * smoothstep(0.8, 1.0, dot(
                normalize(light.p - p),
                L
            ));
        }; break;
        case LIGHT_AMB: {
            return NdotL * light.c * light.s;
        }; break;
    }

    return vec3(0.0);
}

struct Data {
    vec3 p;
    vec3 n;
    float d;
    int id;
    Translation t;
};

#define NEW_DATA Data(vec3(0.0), vec3(0.0), FAR, -1, NEW_TRANSLATION)

#define SAMPLE(D, TRANS, T, dist, t, trans) if (D < dist) { dist = D; t = T; trans = TRANS; }

#define ID_GROUND 0
#define ID_BOX 1
#define ID_STONE 2

float groundSDF(in vec3 p) {
    return p.y;
}

float boxSDF(in vec3 p, in vec3 s) {
    p = abs(p)-s;
    return length(max(p, 0.0)) + min(max(p.x, max(p.y, p.z)), 0.0);
}

float stoneSDF(in vec3 p, in float s) {
    vec3 n = snoise(p.xz*2.2+p.y, s, 1.1, 4);

    float d = distance(p, vec3(0.0)) - n.x;

    return d;

}

float sdf(in vec3 p, inout Data data) {
    float dist = FAR;
    int t = -1;
    Translation tr = NEW_TRANSLATION;

    float ground = groundSDF(p);
    SAMPLE(ground, tr, ID_GROUND, dist, t, tr);

    if (p.y < 0.5 && p.x > 1.0) {
        float stone = stoneSDF(p - vec3(2.3, 0.1, 0), 0.03921);
        SAMPLE(stone, tr, ID_STONE, dist, t, tr);
    }

    Translation boxTrans = Translation(vec3(0, -0.69 - 0.333*(0.5+0.5*cos(T)), 0), vec3(sin(T)*0.25, T, 0));
    float box = boxSDF(pointRot(p + boxTrans.p, boxTrans.r), vec3(0.5))-0.06;
    vec3 boxP = pointRot(p + boxTrans.p, boxTrans.r);
    float boxInside = boxSDF(boxP, vec3(1.0, 0.3, 0.3))-0.05;
    float boxInside2 = boxSDF(boxP, vec3(0.3, 0.3, 1.0))-0.05;
    boxInside = min(boxInside, boxInside2);
    box = max(box, -boxInside);
    SAMPLE(box, boxTrans, ID_BOX, dist, t, tr);



    data.id = t;
    data.t = tr;
    return dist;
}

bool march(in vec3 ro, in vec3 rd, inout Data data) {
    float d = 0.0;

    for (int i = ZERO; i < STEPS; i++) {
        vec3 p = ro+rd*d;
        float next = sdf(p, data);

        if (abs(next) <= NEAR) break;
        if (abs(next) >= FAR) return false;

        d += next;
    }

    d = abs(d);
    vec3 p = ro+rd*d;
    data.p = p;
    data.d = d;
    vec2 e = vec2(0.001, 0.0);
    Data tmp = NEW_DATA;
    data.n = normalize(sdf(p, tmp) - vec3(
        sdf(p - e.xyy, tmp),
        sdf(p - e.yxy, tmp),
        sdf(p - e.yyx, tmp)
    ));

    return true;

}

// Modified version of iq's https://www.shadertoy.com/view/lsKcDD
float getShadow(in vec3 ro, in vec3 rd, in vec3 n, in float near, in float far, in int skip) {
    Data data = NEW_DATA;
    float NdotL = dot(n, rd);
    float nl = max(0.0, NdotL);
    float res = 1.0;
    float ep = 1e20;
    float t = NEAR+0.03;
    float ss = (far/FAR)+0.2;
    float tot = 0.0;
    int lastId = -1;
    float jumps = 0.0;
    for(int i = ZERO; i < SHADOW_STEPS && t < far; i++){
        vec3 p = ro + rd*t;
        float j = float(i);

        // Move point away from surface
        float jump = 0.5*clamp(pow(max(0.0, dot(normalize(rd+p), n)), 2.)*clamp(t/far, 0.5, 1.0), 0.0, 0.56)*
             (1.0 + 0.5*float(lastId == skip)) * (1.0 + 1.5*max(0.0, dot(n, vec3(0, 1, 0))));
        p += n*jump;
        jumps += jump;


        float h = sdf(p, data);
        lastId = data.id;
        tot += h;
        float y = h*h / (2.9 * ep);
        float d = sqrt(h*h-y*y);
        res = min(res, d/(ss*max(0.0, t-y)));
        ep = h;
        t += h;

        if (t <= -0.5 || h <= -0.5 || d <= -0.5) break;
    }
    jumps /= float(SHADOW_STEPS);
    float ra = far*0.5; // radius
    float fe = ra*0.9; // feather
    res = max(res, 0.0002);
    res = max(mix(res*res,
        (1.0-smoothstep(ra-fe, ra+fe, far-tot)),
        0.8*smoothstep(0.0, 0.63, res+smoothstep(0.0, 0.9, tot / (1.0+tot*6.)))
    ), 0.);

    res += (((0.5*res)*res*res) + (max(0.0, (t/far)*0.25*res*res)))*(1.0+(res*0.1));
    res += max(0.0, 1.0-(res*res))*(res / (1.0 + res));
    res /= (1.0 + ((jumps*(0.2+(res*res*2.5))) / (1.0+(nl*nl*2.*(1.0+nl)))));
    res = mix(res, res / (1.0+res), 0.5*jumps*jumps);
    res += 0.25*(0.5*(0.45+(res*res)));
    res /= (1.0+((res+0.05)*max(0.0, 0.5-(nl*nl*2.))));
    return clamp(res, AMBIENT, 1.);
}


vec2 boxUv(in vec3 p, in vec3 n) {
    return mix(mix(p.xy, p.yz, round(adot(n, vec3(1, 0, 0)))), p.xz, round(adot(n, vec3(0, 1, 0))));
}

float checkerTexture(in vec2 uv, in float s) {
    vec2 d = max(abs(dFdx(uv)), abs(dFdy(uv))) + s;
    vec2 t1 = vec2(1.-2.0*abs(fract((uv.x+.5*d.x)*0.5)-0.5), 1.-2.0*abs(fract((uv.y+.5*d.y)*.5)-0.5));
    vec2 t2 = vec2(1.-2.0*abs(fract((uv.x-.5*d.x)*0.5)-0.5), 1.-2.0*abs(fract((uv.y-.5*d.y)*.5)-0.5));
    return 0.5 - 0.5*((t1.x-t2.x)/d.x)*(t1.y-t2.y)/d.y;
}

vec3 grassTexture(in vec2 uv) {
    vec3 col = vec3(0.2, 0.8, 0.1);
    vec3 hf = noise(uv, 0.03214, 32.0, 2);
    vec3 lf = noise(uv, 0.19882, 1.0, 3);
    vec3 lf2 = noise(uv+0.04, 0.0012455, 1.5, 3);

    col *= hf.x;
    col = mix(col, vec3(0.5, 0.2, 0.3), hf.y*lf.z*0.8);
    col = mix(col, vec3(0.8, 0.5, 0.2), smoothstep(lf.x, 0.02, 0.3+0.1*hf.z));
    col = mix(col, col*col, smoothstep(0.25, 0.5, lf2.z*hf.y));

    return col;
}

vec3 stoneTexture(in vec2 uv) {
    vec3 col = vec3(0.89, 0.81, 0.91);
    vec3 hf = noise(uv, 1.49818, 32.0, 2);

    col *= hf.x;
    return col;
}

vec3 getAlbedo(in vec3 p, inout Data data) {
    vec3 n = data.n;

    switch (data.id) {
        case ID_BOX: {


            p = pointRot(p + data.t.p, data.t.r);
            n = pointRot(n, data.t.r);
            vec2 uv = boxUv(p, n);
            return checkerTexture(uv*9., 0.05) * vec3(1.0);
        }; break;
        case ID_GROUND: {
            vec2 uv = p.xz;
            return grassTexture(uv);
        }; break;
        case ID_STONE: {
            vec2 uv = boxUv(p, n);
            return stoneTexture(uv);
        }; break;
    }

    return vec3(0.0);
}

vec3 render(in vec3 ro, in vec3 rd) {
    vec3 col = vec3(0.0);

    Light light = Light(vec3(1, 2, -3), vec3(0.0), vec3(0.96, 0.87, 0.59), LIGHT_AMB, 1.6);

    Data data = NEW_DATA;
    float up = max(0.0, dot(rd, vec3(0, 1, 0)));
    if (march(ro, rd, data)) {
        vec3 albedo = getAlbedo(data.p, data);
        vec3 diffuse = albedo / M_PI;
        vec3 L = getLightDir(light, data.p);
        vec3 att = getLightAtt(light, data.p, data.n);
        float shadow = getShadow((data.p+L*0.0001)+data.n*0.0001, L, data.n, data.d, FAR, -1);
        col += albedo * att * shadow;
    } else {
        vec3 sky = vec3(0.02, 0.6, 0.89);
        sky = pow(sky, 3.0*up*vec3(up));
        col += sky;
    }

    float depth = data.d / FAR;
    col += light.c*depth*smoothstep(0.15, 0.0, up)*(0.5+0.5*depth);

    return col;
}


void mainImage( out vec4 O, in vec2 fc )
{
    vec2 uv = (fc - R.xy * 0.5) / R.y;
    vec3 col = vec3(0.0);

    vec3 ro = vec3(0.0, 1., -4.0);
    vec3 rd = normalize(vec3(uv, 1.0));
    vec4 m = vec4((iMouse.xy - R.xy * 0.5)/R.y, iMouse.zw);

    if (m.z > 0.01) {
        rd.yz *= rot(m.y*TAU);
        rd.xz *= rot(m.x*TAU);
    }


    #if FX > 0
    vec3 rd2 = rd;
    rd2.yx *= rot(FX_AMOUNT);
    col += (render(ro, rd) + render(ro, rd2))*0.5;
    #else
    col += render(ro, rd);
    #endif

    col += col*col*0.5;

    col = pow(col, vec3(1.0 / 2.2));

    O = vec4(col, 1.0);
}

float possin(float val) {
    return sin(val) / 2. + 0.5;
}


float poscos(float val) {
    return cos(val) / 2. + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv0 = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
    float pi0 = 0.9;
    uv0 *= pi0;

    vec2 uv = uv0;

    vec3 color;
    float myTime = iTime / 1.;

    float closeness = 30.;

    uv.y += sin(myTime) * pi0 / closeness;
    uv.x += cos(myTime) * pi0 / closeness;
    for (float i = 0.0; i < 2.0; i++) {
        uv = fract(uv) * 2. - 1.;

        color.z = length(uv);

        color.z = smoothstep(0.5, 1., color.z);
        color.z = 0.1 / color.z;

        if (abs(uv.x) + abs(uv.y) < 0.1) {
            color.z = 0.0;
        }
    }

    uv = uv0;
    uv.y -= sin(myTime) * pi0 / closeness;
    uv.x -= cos(myTime) * pi0 / closeness;
    for (float i = 0.0; i < 2.0; i++) {
        uv = fract(uv) * 2. - 1.;

        color.x = length(uv);

        color.x = smoothstep(0.5, 1., color.x);
        color.x = 0.1 / color.x;


        if (length(uv) < 0.1) {
            color.x = 0.0;
        }
    }

    uv = uv0;

    uv.y += sin(-myTime) * pi0 / closeness;
    uv.x -= myTime / 10.;

    uv.x += sin(-myTime) * pi0 / closeness;
    uv.y += myTime / 10.;
    for (float i = 0.0; i < 2.0; i++) {
        uv = fract(uv) * 2. - 1.;
        uv *= possin(myTime) / 7. + 0.9;

        if (i < 1. || abs(uv.x) + abs(uv.y) > 0.7) {
            continue;
        }

        color = 1.0 - color;
        color = 1. / color;

        color /= 5.;

        if (abs(uv.x) > 0.3 || abs(uv.y) > 0.3) {
            continue;
        }

        float cap = 0.5;
        float av = (color.x + color.y) / round((abs(uv.x) + abs(uv.y)) * 10.) / 10.;

        if (av > cap) {
            cap = av;
        }

        color.x = av;
        color.y = av;
        color.xy *= 5.;

        color.x = color.x > cap ? cap : color.x;
        color.y = color.y > cap ? cap : color.y;
    }
    fragColor = vec4(color, 0.1);
}

// Ray marching constants
#define MAX_STEPS 250
#define SURF_DIST 0.0001
#define MAX_DIST 100.0
#define PI 3.141592653592
#define TAU 6.283185307185


////////////////////////////////////////////////////////////////
// Signed Distance Functions
////////////////////////////////////////////////////////////////
float sdPlane( vec3 p, vec3 n, float h )
{
  return dot(p,normalize(n)) + h;
}

float sdSphere( vec3 p, vec3 c, float r )
{
    return length(p-c) - r;
}

float sdEllipsoid( vec3 po, vec3 c, vec3 r )
{
    vec3 p = po-c;

    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdCutSphere( vec3 po, vec3 c, float r, float h )
{
    vec3 p = po-c;
    float w = sqrt(r*r-h*h);

    vec2 q = vec2( length((p).xz), p.y );

    float s = max( (h-r)*q.x*q.x+w*w*(h+r-2.0*q.y), h*q.x-w*q.y );

    return (s<0.0) ? length(q)-r :
           (q.x<w) ? h - q.y     :
                     length(q-vec2(w,h));
}


////////////////////////////////////////////////////////////////
// SDF Operations
////////////////////////////////////////////////////////////////

float opUnion(float d1, float d2 ) { return min(d1,d2); }

float opSubtraction(float d1, float d2) {
    //NOTE: Flipped order because it makes more sense to me
    return max(-d2, d1);
}
float opIntersection(float d1, float d2) {
    return max(d1, d2);
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}
float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d1, -d2, h ) + k*h*(1.0-h);
}
float opSmoothIntersection(float d1, float d2, float k){
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}

////////////////////////////////////////////////////////////////
// Rotations
////////////////////////////////////////////////////////////////

vec3 rotX(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        1., 0., 0.,
        0., c, -s,
        0., s, c
        );
    return m * p;
}

vec3 rotY(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, 0., s,
        0., 1., 0.,
        -s, 0., c
        );
    return m * p;
}

vec3 rotZ(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, -s, 0.,
        s, c, 0.,
        0., 0., 1.
        );
    return m * p;
}

vec3 orbitControls(vec3 po) {
    vec2 m = (vec2(iMouse.x, iMouse.y) / iResolution.xy) + 0.5;
    vec3 p = po;
    p = rotY(po, -m.x * 2.*TAU);
    //p = rotX(p, -m.y*PI - PI / 2.0);
    return p;
}


////////////////////////////////////////////////////////////////
// Ray Marching Functions
////////////////////////////////////////////////////////////////

vec2 map(vec3 po) {
    // Mouse
    vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y;  
    vec3 p = orbitControls(po);
    //vec3 p = po;
    vec3 pSym = vec3(abs(p.x), p.y, p.z);
    vec2 res;

    // body + head
    float s1 = sdEllipsoid(p, vec3(0., -0.3, 0.),vec3(0.65, 0.54, 0.7));
    float s2 = sdEllipsoid(p, vec3(0., -0.4, 0.4),vec3(0.4, 0.3, 0.4));
    res = vec2(s1, 1.0); // id to track color
    float h = sdCapsule(p, vec3(0.0, 0., -0.35), vec3(0.0, 0.7, -0.4), 0.3);
    h += 0.07 * p.y;
    res.x = opSmoothUnion(s1, h, 0.2);

    // tail
    float s31 = sdEllipsoid(p, vec3(0., -0.4, 1.),vec3(0.15, 0.06, 0.4));

    res.x = opSmoothUnion(res.x, s2, 0.2);

    // wings
    vec3 psw = vec3(pSym.x, pSym.y * (1.0 + 0.6 * pSym.z) + 0.2 * pSym.z, pSym.z);
    vec3 prw = rotZ(rotY(psw, 0.15), 0.4);
    float w = sdEllipsoid(prw, vec3(0.45, -0.48, 0.1), vec3(0.1, 0.35, 0.45));

    res.x = opSmoothUnion(res.x, w, 0.05);

    // eyelids
    float lidAngle = 0.3;
    vec3 pre = rotX(pSym-vec3(0.135, 0.65, -0.6), lidAngle); // translate then rotate
    pre = rotZ(pre, 0.3);
    float el = sdCutSphere(pre, vec3(0.0), 0.08
    , 0.02)-0.01;
    res.x = opSmoothUnion(res.x, el, 0.02);
    if (el < res.x+SURF_DIST) res.y = .0;

    // eyes
    float e = sdSphere(pSym, vec3(0.135, 0.65, -0.6), 0.07);
    res.x = opSmoothUnion(res.x, e, 0.);
    if (e < res.x+SURF_DIST) res.y = 2.0;

    float pupil = sdSphere(pSym, vec3(0.16, 0.66, -0.63), 0.045);
    pupil = opIntersection(pupil, e);
    res.x = opSmoothUnion(res.x, pupil, 0.);
    if (pupil < res.x+SURF_DIST) res.y = 5.0;


    // beak
    float bk = sdEllipsoid(p, vec3(0., 0.55, -0.65), vec3(0.11, 0.06, 0.12));
    float bkb = sdEllipsoid(p, vec3(0., 0.53, -0.65), vec3(0.09, 0.06, 0.07));
    bk = opSmoothUnion(bk, bkb, 0.0);

    res.x = opSmoothUnion(res.x, bk, 0.0);
    if (bk < res.x+SURF_DIST) res.y = 3.0;

    float bkc = sdEllipsoid(p, vec3(0., 0.575, -0.65), vec3(0.09, 0.06, 0.05));
    if (bkc < res.x+SURF_DIST) res.y = 2.0;
    res.x = opSmoothUnion(res.x, bkc, 0.0);



    // foots

    vec3 heel = vec3(0.25, -1.1, -0.);
    vec3 hip = vec3(0.25, -0.75, -0.);
    res.x = opSmoothUnion(res.x, sdSphere(pSym, hip-vec3(0.02,0.,0.), 0.125), 0.06);


    float l = sdCapsule(pSym, hip, heel + vec3(0., 0.02, 0.), 0.04);
    float t1 = sdCapsule(pSym, heel, heel + vec3(0., 0., -0.15), 0.04);
    float t2 = sdCapsule(pSym, heel, heel + vec3(0.1, 0., -0.15), 0.04);
    float t3 = sdCapsule(pSym, heel, heel + vec3(-0.1, 0., -0.15), 0.04);
    float toes = opUnion(t1, opUnion(t2, t3));

    l = opSmoothUnion(l, toes, 0.02);


    res.x = opSmoothUnion(res.x, l, 0.);
    if (l < res.x+SURF_DIST) res.y = 3.0;


    return res;
}

vec2 rayMarch(vec3 ro, vec3 rd) {
    float d = 0.0;
    float mat = 1.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        if (i >= MAX_STEPS ) break;
        vec3 p = ro + rd * d;
        vec2 ds = map(p);
        d += ds.x;
        mat = ds.y;
        if (d >= MAX_DIST || ds.x < SURF_DIST) break;
        i++;
    }
    return vec2(d, mat);
}

vec3 gradient(vec3 p) {
    float epsilon = 0.0001;
    vec3 dx = vec3(epsilon, 0., 0.0);
    vec3 dy = vec3(0., epsilon, 0.0);
    vec3 dz = vec3(0., 0.0, epsilon);

    float ddx = map(p + dx).x - map(p - dx).x;
    float ddy = map(p + dy).x - map(p - dy).x;
    float ddz = map(p + dz).x - map(p - dz).x;

    return normalize(vec3(ddx, ddy, ddz));
}

vec3 rayDirection(vec2 p, vec3 ro, vec3 rt) {

    // screen orientation
    vec3 vup = vec3(0., 1.0, 0.0);
    float aspectRatio = iResolution.x / iResolution.y;

    vec3 vw = normalize(ro - rt);
    vec3 vu = normalize(cross(vup, vw));
    vec3 vv = cross(vw, vu);
    float theta = radians(30.); // half FOV
    float viewport_height = 2. * tan(theta);
    float viewport_width = aspectRatio * viewport_height;
    vec3 horizontal = -viewport_width * vu;
    vec3 vertical = viewport_height * vv;
    float focus_dist = length(ro - rt);
    vec3 center = ro - vw * focus_dist;

    vec3 rd = center + p.x * horizontal + p.y * vertical - ro;

    return normalize(rd);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 2.0; // normalizing

    // Ray Marching

    // Dolly Zoom and re-center by shifting/squeezing ray origin & target
    float my = (2.*iMouse.y-iResolution.y)/iResolution.y;  
    float shift = min(10. * my, 8.5);

    vec3 rt = vec3(0., -0.05, -0.5);
    vec3 ro = vec3(0., -0.05, -10. + shift);


    vec3 rd = rayDirection(uv, ro, rt);
    vec2 d = rayMarch(ro, rd);

    // Background
    float v = length(uv) * .75;
    fragColor = vec4(0.1,0.7,1.,1.);
    fragColor += vec4(mix(0.2, 0.4, smoothstep(0.0, 1.0, uv.y)));
    fragColor += vec4(mix(0.2, 0.4, smoothstep(0.0, 1.0, uv.x)));


    if (d.x <= 100.0) {
        vec3 p = ro + rd * d.x;
        vec3 pr = orbitControls(p);
        vec3 N = gradient(p);
        float light1 = dot(N, normalize(vec3(1, 1,-1)))*.5+.5;
        float light2 = dot(N, normalize(vec3(-1, 1,-1)))*.5+.5;
        vec3 illumination = 0.5 * vec3(light1) + 0.5 * vec3(light2);
        vec3 c1 = vec3(0.2,0.8,1.);
        vec3 c2 = vec3(0.8,0.2,1.);

        vec3 color = c1;

        if (d.y == 2.) color.xyz = vec3(1.);        
        if (d.y == 3.) color.xyz = vec3(1., 0.9, 0.4);        
        if (d.y == 4.) color.xyz = vec3(1.);        
        if (d.y == 5.) color.xyz = vec3(0.);        



        color *= illumination; 

        // Specular highlights
        vec3 R = reflect(normalize(vec3(1.,0.,0.)), N);
        vec3 specular = vec3(1.0) * pow(max(dot(R, rd), 0.0),10.0);

        // fake fresnel
        float nDotV = dot(N, rd) + 1.;
        color += nDotV * nDotV * 0.45 + specular * 0.08;


        fragColor = vec4(color, 1.0);

    }

}

#define SPEED 4.
#define PI 3.14159265

float atan2(vec2 p) {

      return atan(p.y/p.x) + ((p.x >0.) ? 0. : PI);

  }

vec2 c2uv (vec2 coord) {
  float mn = min(iResolution.x, iResolution.y);
  return (coord-iResolution.xy/2.)/mn;
  }




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = c2uv(fragCoord.xy);

  float d=length(uv); //black fog
  float ang=atan2(uv)+iTime;
  float v=sin(4./d + iTime * SPEED);
  v=smoothstep(.9,1.,v);

  float r=.5*sin(ang)+.5;
  float g=.5*sin(ang+(2.*PI/3.))+.5;
  float b=.5*sin(ang+(4.*PI/3.))+.5;
  vec3 ctint=vec3(r,g,b);


  vec3 col=vec3(v);




  col *= d*ctint;

  fragColor = vec4(col, 1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Wythoff construction for convex polyhedra, with an exact SDF.
// Matthew Arcus, mla, 2023
//
// https://en.wikipedia.org/wiki/Wythoff_construction
//
// Controls:
// Mouse changes orientation
// up/down: zoom
// 1-3: show face types 1-3
// a: AA
// e: just show edges
// m: mirror faces
// r: rotation
// t: texture faces
// z: slice at z = 0
//
// Uses exact distance to triangular faces (or subdivisions of faces) 
// rather than distance to face planes, enabling, eg. SDF rounding
// to work.
//
// Snubs can be done in the same way, but omitted here as this
// considerably complicates the code.
//
////////////////////////////////////////////////////////////////////////////////

const float precis = 0.0001; // Ray marching precision, scaled by distance
const float attenuation = 0.75; // Ray marching fiddle factor
float maxdistance = 5.0;

// Any P,Q,R with 1/P + 1/Q + 1/R > 1, which means:
// 2:2:2, 3:2:2, 4:2:2, ...
// 3:3:2, 4:3:2, 5:3:2

float P = 2.0, Q = 3.0, R = 5.0;
//float P = 10.0, Q = 2.0, R = 2.0;

// Number of iterations of folding loop
// 5 iterations is enough to get up to (5 3 2) or (10 2 2)
int NFOLDS = 5;

// Types of feature
int FACE0 = 1;
int FACE1 = 2;
int FACE2 = 3;
int EDGE = 4;

// 3 mirror planes (passing through origin) define a fundamental
// region, which intersects the unit sphere in a spherical
// triangle with angles PI/P, PI/Q, PI/R.
// The normal polyhedron has a vertex in the fundamental region,
// edges from the vertex perpendicular to the mirror planes, and
// faces perpendicular to the lines of intersection of the mirror
// planes.
// The dual polyhedron has faces perpendicular to the vector to
// the region vertex, and vertices on the mirror plane intersections.

vec3 A,B,C;    // normal vectors for mirror planes
vec3 D,E,F;    // face centre axes of normal faces
vec3 X;        // the vertex point & face centre of dual
vec3 G,H,I; // vertices of the dual
vec3 trilinear = vec3(1);

void initgeometry() {
  // Setup folding planes
  float p = PI/P, q = PI/Q, r = PI/R;
  // |A| = |B| = |C| = 1
  // A.B = -cos(p)
  // A.C = -cos(r)
  // B.C = -cos(q) = cos(p)cos(r) + y sin(p)
  // It's more convenient to make A,B,C point _out_ of
  // the fundamental region (away from the region vertex)
  A = vec3(-1,0,0);
  B = vec3(cos(p),-sin(p),0);
  float x = cos(r);
  float y = (cos(p)*cos(r)+cos(q))/sin(p);
  float z = -sqrt(1.0-x*x-y*y);
  C = vec3(x,y,z);

  // Convert trilinear to normal coordinates
  D = cross(B,C), E = cross(C,A), F = cross(A,B);
  X = mat3(D,E,F)*trilinear;
  X = normalize(X); // vertex at unit distance

  // Scale so edge centres (for both normal and dual) are on unit sphere.
  float scale = min3(edgedistance(X,A),
                     edgedistance(X,B),
                     edgedistance(X,C));
  X /= scale;

  D = normalize(D);
  E = normalize(E);
  F = normalize(F);

  // Dual vertices
  G = D/dot(X,D);
  H = E/dot(X,E);
  I = F/dot(X,F);
}

vec3 fold(vec3 pos,out int parity) {
  parity = 0;
  if (key(CHAR_F)) return pos;
  for (int i = 0; i < NFOLDS; i++) {
    float k;
    k = dot(pos,A);
    parity += int(k > 0.0);
    pos -= 2.0*max(0.0,k)*A;
    k = dot(pos,B);
    parity += int(k > 0.0);
    pos -= 2.0*max(0.0,k)*B;
    k = dot(pos,C);
    parity += int(k > 0.0);
    pos -= 2.0*max(0.0,k)*C;
  }
  return pos;
}

// Distance to triangular wedge,vertex at origin, sides (normalized) vectors s,t,
// n is normalized normal (ie. normalize(cross(s,t)))
float wedgeDistance(const vec3 p, vec3 s, vec3 t, vec3 n, out bool isedge, vec2 uv) {
  float edgeradius = 0.03;
  isedge = true;
  bool justedges = key(CHAR_E);
  // If p not directly above the wedge, return the distance to edges
  float d = sqrt(min(ray2(p,s),ray2(p,t)))-edgeradius;
  if (justedges || dot(p,cross(s,n)) > 0.0 ||  dot(p,cross(t,n)) < 0.0) return d;
  float d0 = dot(n,p); // Projection onto wedge
  if (!key(CHAR_T)) d0 += 0.01*cos(100.0*uv.x)*cos(100.0*uv.y); // Displacement
  d0 = abs(d0) - 0.005; // Thickness
  if (d0 < d) {
    d = d0; isedge = false;
  }
  return d;
}

float scene(vec3 pos, int parity, out int type, out vec2 uv) {
  float d = 1e8, d0;
  vec2 uv0 = pos.xy;
  bool isedge,isedge0;
  float off = 0.2; //0.5-0.5*cos(0.5*iTime);
  if (!key(CHAR_0+1)) {
    d0 = wedgeDistance(pos-X-off*F,A,B,F,isedge0,uv0);
    if (d0 < d) { d = d0; isedge = isedge0; type = FACE0; uv = uv0; }
  }
  if (!key(CHAR_0+2)) {
    d0 = wedgeDistance(pos-X-off*D,B,C,D,isedge0,uv0);
    if (d0 < d) { d = d0; isedge = isedge0; type = FACE1; uv = uv0; }
  }
  if (!key(CHAR_0+3)) {
    d0 = wedgeDistance(pos-X-off*E,C,A,E,isedge0,uv0);
    if (d0 < d) { d = d0; isedge = isedge0; type = FACE2; uv = uv0; }
  }
  if (isedge) type = EDGE;
  return d;
}

float map(vec3 pos, out int type, out vec2 uv) {
  float d = -1e8;
  if (key(CHAR_Z)) d = pos.z-0.1;
  int parity;
  pos = fold(pos,parity);
  return max(d,scene(pos,parity,type,uv));
}

float map(vec3 pos) {
  int type; vec2 uv;
  return map(pos,type,uv);
}

// Tetrahedron technique, from:
// https://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal(vec3 p) {
  float h = 0.0002;
  vec2 k = vec2(1,-1);
  return normalize( k.xyy*map( p + k.xyy*h ) + 
                    k.yyx*map( p + k.yyx*h ) + 
                    k.yxy*map( p + k.yxy*h ) + 
                    k.xxx*map( p + k.xxx*h ) );
}

float march(in vec3 ro, in vec3 rd) {
  float t = 0.0;
  float res = -1.0;
  for (int i = 0; i < 200; i++) {
    //assert(i < 10);
    if (t > maxdistance) break;
    float h = map(ro+rd*t);
    t += attenuation*h;
    if (h < t*precis) break;
  }
  return t;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (!key(CHAR_R)) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.1);
  }
  return p;
}

vec3 circuit[] = vec3[](
   vec3(1,1,1),vec3(1,1,1),vec3(1,0,0),vec3(1,0,0),
   vec3(1,1,0),vec3(1,1,0),vec3(0,1,0),vec3(0,1,0),
   vec3(0,1,1),vec3(0,1,1),vec3(0,0,1),vec3(0,0,1),
   vec3(1,0,1),vec3(1,0,1),vec3(1,0,0),vec3(1,0,0),
   vec3(1,1,1)
 );

const int nsteps = circuit.length();

vec3 PQR[] = vec3[](
  vec3(2,3,5), vec3(2,3,4),vec3(2,3,3),vec3(2,2,3),vec3(2,2,10)
);

vec3 gettrilinear(float t) {
  int i = int(floor(t));
  int cycle = (i/nsteps)%PQR.length();
  i %= nsteps;
  P = PQR[cycle][0], Q = PQR[cycle][1], R = PQR[cycle][2];
  return mix(circuit[i],circuit[(i+1)%nsteps],mod(t,1.0));
}

vec3 speccolor(int type) {
  if (type == FACE0) return vec3(1,0,0);
  if (type == FACE1) return vec3(1,1,0);
  if (type == FACE2) return vec3(0,0,1);
  return vec3(1);
}

// softshadow from iq.
float softshadow(vec3 ro, vec3 rd, float mint, float tmax, float w) {
  float t = mint;
  float res = 1.0;
  for (int i = 0; i < 200; i++) {
    float h = map(ro + t*rd);
    res = min(res, h/(w*t));
    t += clamp(h, 0.0, max(0.5,0.1*t));
    if (res < -1.0 || t > tmax) break;
  }
  res = max(res,-1.0); // clamp to [-1,1]
  res = 0.25*(1.0+res)*(1.0+res)*(2.0-res); // smoothstep
  return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  // Trilinear coordinates of vertex point (ie. proportional distance
  // from sides of fundamental region).
  trilinear = gettrilinear(0.5*iTime);
  initgeometry();
  float camera = 3.0;
  camera *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  maxdistance = camera+5.0;
  vec3 ro = vec3(0,0,camera);
  vec3 light = vec3(0.5, 1.0, 2.0);
  ro = transform(ro);
  light = transform(light);
  light = normalize(light);
  vec3 aacol = vec3(0);
  float AA = 1.0;
  if (key(CHAR_A)) AA = 2.0;
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 z = (2.0*(fragCoord.xy+vec2(i,j)/AA) - iResolution.xy) / iResolution.y;
      vec3 rd = vec3(z,-2);
      rd = transform(rd);
      rd = normalize(rd);
      vec3 col = 0.75+0.25*rd; //texture(iChannel0,rd).xyz;
      col = pow(col,vec3(2.2));
      float t = march(ro, rd);
      if (t < maxdistance) {
        vec3 pos = ro + t * rd;
        vec3 n = calcNormal(pos);
        int type;
        vec2 uv;
        map(pos,type,uv); // Get type and uv
        float specpow, specval;
        if (type == EDGE) {
          col = vec3(0);
          specpow = 2.0;
          specval = 0.1;
        } else {
          col = vec3(0.5);
          specpow = 10.0;
          specval = 1.0;
        }
        //col = vec3(0.5);
        if (key(CHAR_M) && type < EDGE) {
          col = texture(iChannel1,reflect(rd,n)).xyz;
        } else {
          float diffuse = clamp(dot(n, light), 0.0, 1.0);
          float shadow = softshadow(pos,light,0.1,4.0,0.1);
          diffuse *= shadow;
          col *= 0.2 + 0.8*diffuse;
          float specular = pow(max(0.0,dot(reflect(light,n),rd)),specpow);
          col += specval*specular*speccolor(type);
        }
      }
      aacol += col;
    }
  }
  //assert(keystate(KEY_ALT,0).x == 0.0);
  aacol /= AA*AA;
  aacol = 2.0*aacol/(1.0+aacol);
  aacol = clamp(aacol,0.0,1.0);
  aacol = pow(aacol,vec3(0.4545));
  if (alert) aacol.x = 1.0;
  fragColor = vec4(aacol, 1.0);
}

