#define speed 1.5
#define sc .75
#define offset .35
#define blend .1

mat2 rotate(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, s, -s, c);
}

float smin(float a, float b, float k)
{
    float h = max(k-abs(a-b), 0.0)/k;
    return min(a,b)-h*h*k*(1.0/4.0);
}

vec2 target(float stage, float ring)
{
    vec2 o = vec2(0.);
    float p = 0.;

    if (stage <= 0.)
    {
        p = (mod(ring, 2.)*2.-1.)*offset;
        o = vec2(0.,p);
    }
    else if (stage <= 1.)
    {
        p = (step(1.5,mod(ring,4.))*2.-1.)*offset;
        o = vec2(p,p*.5);
    }
    else if(stage <= 2.)
    {
        p = (step(3.5,mod(ring,8.))*2.-1.)*offset;
        o = vec2(-p, p*.5);
    }
    else if(stage <= 3.)
    {
        p = (step(1.5,mod(ring,4.))*2.-1.)*offset;
        o = vec2(-p,-p*.5);
    }
    else if (stage <=4.)
    {
        p = (step(3.5,mod(ring,8.))*2.-1.)*offset;
        o = vec2(p, -p*.5);
    }
    else if (stage <=5.)
    {
        p = (mod(ring,2.)*2.-1.)*offset;
        o = vec2(0.,-p);
    }

    return o;
}

void motion(float stage, float prog, inout float scale, float ring, out float t, out vec2 p)
{
    float change = sc;

    if(stage >= 3.)
    {
        change = 1./sc;
    }

    p = target(stage,ring);
    t = smoothstep(0.,1.,prog - stage);
    scale = mix(scale, scale * change, t);
    p = p * t;
}

void steps(float prog, float ring, out vec2 cur, out float val)
{
    float scale = .35;

    cur = vec2(0.);
    val = scale;
    for(float i = 0.; i < 6.; i++ )
    {
        float t = 0.;
        vec2 p = vec2(0.);
        motion(i, prog, scale, ring, t ,p);
        cur += p;
        val = mix(val, scale, t);
    } 

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor -= fragColor;
    fragCoord = (fragCoord+fragCoord-iResolution.xy)/iResolution.y;

    fragCoord = fragCoord*rotate(iTime*.25);

    float prog = mod(iTime * speed, 6.);
    float c = 1.;

    for(float i = 0.; i < 8.; i++)
    {
        vec2 cur = vec2(0.);
        float val = 0.;
        steps(prog, i, cur, val);
        val = length(fragCoord+cur)-val;
        c = smin(c, val, blend);        
    }

    c = pow(0.01 / abs(c), 1.2);
    vec3 col = c * (cos(vec3(0,1,2) + iTime)+1.2);

    fragColor = vec4(col,1.);
}

