vec3 specialBlur(in sampler2D originTex, in sampler2D borderTex, in vec2 uv, in vec2 offset)
{
    float f = texture(borderTex, uv).r;
    vec3 ansColor = vec3(0.0);
    if(f >= 0.5)
        ansColor = texture(originTex, uv).rgb;
    else{
    	ansColor = texture(originTex, uv).rgb * weight[0];
        float norm = weight[0];
        for(int i = 1; i < R; i++){
            float f_t = texture(borderTex, uv + float(i) * offset).r;
            if(f_t > 0.5)	break;
        	ansColor += texture(originTex, uv + float(i) * offset).rgb * weight[i];
            norm += weight[i];
        }

        for(int i = 1; i < R; i++){
        	float f_t = texture(borderTex, uv - float(i) * offset).r;
            if(f_t > 0.5)	break;
        	ansColor += texture(originTex, uv - float(i) * offset).rgb * weight[i];
            norm += weight[i];
        }

        ansColor /= norm;

    }

    return   ansColor; 
}

