//soble border detection
//guassian blur can remove Noise
//then we apply soble operator on image
void make_kernel(inout vec4 n[9], sampler2D tex, vec2 coord)
{
	float w = 1.0 / iResolution.x;
	float h = 1.0 / iResolution.y;

	n[0] = texture(tex, coord + vec2( -w, -h));
	n[1] = texture(tex, coord + vec2(0.0, -h));
	n[2] = texture(tex, coord + vec2(  w, -h));
	n[3] = texture(tex, coord + vec2( -w, 0.0));
	n[4] = texture(tex, coord);
	n[5] = texture(tex, coord + vec2(  w, 0.0));
	n[6] = texture(tex, coord + vec2( -w, h));
	n[7] = texture(tex, coord + vec2(0.0, h));
	n[8] = texture(tex, coord + vec2(  w, h));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
   	vec4 n[9];
    make_kernel(n, iChannel0, uv);
    // sobel
    vec4 gx = (n[6] + 2.0 * n[7] + n[8])  - (n[0] + 2.0 * n[1] + n[2]);
    vec4 gy = (n[0] + 2.0 * n[3] + n[6]) - (n[2] + 2.0 * n[5] + n[8]);

    vec4 finalColor = vec4(0.0);
    //finalColor = sqrt(gx * gx) + sqrt(gy * gy);
    finalColor = sqrt(gx * gx + gy * gy);

    fragColor = vec4(clamp(finalColor.rgb, 0.0, 1.0), sqrt(length(gy) / length(gx)));
}

