const float threshold = 0.5;
//NMS
bool NMS(in vec2 fragCoord){
	vec2 uv = fragCoord / iResolution.xy;
    vec4 c = texture(iChannel0, uv);
    float gp = length(c.rgb);
    float xita = c.a;
    float p1 = 0.0, p2 = 0.0,p3 = 0.0, p4 = 0.0;
    float step_x = 1.0 / iResolution.x;
    float step_y = 1.0 / iResolution.y;
    if(xita > 1.0){
    	p1 = length(texture(iChannel0, uv + vec2(0.0, -step_y)).rgb);
        p2 = length(texture(iChannel0, uv + vec2(step_x, -step_y)).rgb);
        p3 = length(texture(iChannel0, uv + vec2(0.0, step_y)).rgb);
        p4 = length(texture(iChannel0, uv + vec2(-step_x, step_y)).rgb);
    }else if(xita > 0.0){
    	p1 = length(texture(iChannel0, uv + vec2(step_x, -step_y)).rgb);
        p2 = length(texture(iChannel0, uv + vec2(step_x, 0.0)).rgb);
        p3 = length(texture(iChannel0, uv + vec2(-step_x, step_y)).rgb);
        p4 = length(texture(iChannel0, uv + vec2(-step_x, 0.0)).rgb);
    }else if(xita > -1.0){
    	p1 = length(texture(iChannel0, uv + vec2(step_x, 0.0)).rgb);
        p2 = length(texture(iChannel0, uv + vec2(step_x, step_y)).rgb);
        p3 = length(texture(iChannel0, uv + vec2(-step_x, 0.0)).rgb);
        p4 = length(texture(iChannel0, uv + vec2(-step_x, -step_y)).rgb);
    }else{
    	p1 = length(texture(iChannel0, uv + vec2(step_x, step_y)).rgb);
        p2 = length(texture(iChannel0, uv + vec2(0.0, step_y)).rgb);
        p3 = length(texture(iChannel0, uv + vec2(-step_x, -step_y)).rgb);
        p4 = length(texture(iChannel0, uv + vec2(0.0, -step_y)).rgb);
    }

    float gp1 = xita * p2 + (1.0 - xita) * p1;
    float gp2 = xita * p4 + (1.0 - xita) * p3;
    //float gp1 = xita * p1 + (1.0 - xita) * p2;
    //float gp2 = xita * p3 + (1.0 - xita) * p4;

    if(gp >= gp1 && gp >= gp2){
        if((c.r + c.b + c.g) / 3.0 > threshold)
            return true;
    }
    return false;
}

