#define ROTATE 1
#define SCALE 2
const vec2 center = vec2(0.5);

vec3 RadialBlur(in sampler2D mulriple_tex, in sampler2D origin_tex, in vec2 uv, int type, float R, vec2 stepp)
{    
    vec2 offset = uv - center;
    offset = normalize(offset);
    vec2 direction = offset;
    if(type == ROTATE){
    	direction = vec2(-offset.y,offset.x);
    }

    float mulriple = texture(mulriple_tex, uv).r;
    float r = R * mulriple;
    vec3 sum_c = vec3(0.0);
    float norm = 0.0;
    for(float i = -r; i < r; i++){
    	vec2 uv2 = uv + direction * i * stepp;
        sum_c += texture(origin_tex, uv2).rgb;
        norm += 1.0;
    }
    sum_c /= norm;
    return sum_c;
}

