vec4 init(vec2 u) {
    //u -= vec2(-1.0,0.62);
    //return vec4(1) * exp(-1000.*dot(u,u)) * (u.x * u.y) * 3000.;
    return vec4(1,1,1,1) * exp(-10000.*dot(u,u));
    //return 0.1 * vec4(1) * exp(-5000.*u.x*u.x);
}

Main {
    Q = A(U);

    // This is the value of (c * Ît^2/Îx^2)
    float dt2 = 0.5;
    // Any number between zero and 0.5 may be used.
    // Smaller values mean slower simulation, but do not make the simulation more accurate.
    // On the contrary, the most accurate value to use is 0.5, for which
    // the dispersion of the 2nd order scheme is minimal in the four diagonal directions
    // (notice the directional pattern of the ripples).

    // Basic FDTD
    {
        float n = A(U+vec2(0,1)).z;
        float s = A(U-vec2(0,1)).z;
        float w = A(U-vec2(1,0)).z;
        float e = A(U+vec2(1,0)).z;
        float pn = Q.z;
        float pnm1 = Q.w;


        float p_next = 2.*pn - pnm1 + dt2 * (n+s+w+e - 4.*Q.z);
        Q.z = p_next;
        Q.w = pn;
    }

    // Fourth-order FDTD
    // as described in
    // A Modified Equation Approach to Constructing Fourth Order Methods for Acoustic Wave Propagation
    // Shubin and Bell
    // SIAM 1987
    // https://doi.org/10.1137/0908026
    // It is based on a modified equation technique to compensate terms of the Taylor expansion
    // up to the fourth order. 
    {
        float n = A(U+vec2(0,1)).x;
        float s = A(U-vec2(0,1)).x;
        float w = A(U-vec2(1,0)).x;
        float e = A(U+vec2(1,0)).x;
        float pn = Q.x;
        float pnm1 = Q.y;

        float nn = A(U+vec2(0,2)).x;
        float nw = A(U+vec2(-1,1)).x;
        float ne = A(U+vec2(1,1)).x;
        float ww = A(U+vec2(-2,0)).x;
        float ee = A(U+vec2(2,0)).x;
        float sw = A(U+vec2(-1,-1)).x;
        float se = A(U+vec2(1,-1)).x;
        float ss = A(U+vec2(0,-2)).x;

        float D2xp = w - 2.*pn + e;
        float D2yp = s - 2.*pn + n;
        float D4xp = ww - 4.*w + 6.*pn - 4.*e + ee;
        float D4yp = ss - 4.*s + 6.*pn - 4.*n + nn;
        float D2xD2yp = sw + se + nw + ne - 2.*(s + w + n + e) + 4.*pn;
        float p_next = 2.*pn - pnm1 + dt2 * (D2xp + D2yp) 
                      - dt2/12. * ( (1.-dt2) * (D4xp+D4yp) - 2.*dt2*D2xD2yp);


        Q.y = pn;
        Q.x = p_next;
    }

    // Initial conditions or resolution change
    if(iFrame == 0 || A(vec2(0)).xy != R) {
        Q = vec4(0);
    }

    if(iMouse.z > 0.0)
    Q += init(uu(U) - uu(iMouse.xy)) * 0.05;

    Q *= isInDomain(uu(U));

    if(floor(U) == vec2(0)) {
        Q.xy = R;
    }
}

