void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy - 0.5;
    uv.x /= iResolution.y / iResolution.x;
    uv += 0.5;

    vec2 warp = uv * 4.0;

    float t = iTime / 4.0;

    for (int i=0; i<3; i++) {

        float tmul = float(i) * 0.75 + 1.0;
        warp.x += sin(warp.y * tmul + sin(t));
        warp.y += sin(warp.x * tmul + cos(t));

    }

    float val = abs(mod(warp.x, 2.0) - 1.0);

    val *= smoothstep(0.0, 1.0, length(uv - 0.5) * 2.0);

    vec3 col = mix(
        mix(vec3(1.0, 0.1, 0.7), vec3(0.0, 0.05, 0.1), length(uv - 0.4) * 0.6),
        mix(vec3(1.0, 0.7, 0.9), vec3(1.0, 0.0, 0.3), length(uv - 0.6) * 0.6),
        smoothstep(0.8, 0.9, val) + val
    );

    fragColor = vec4(col, 1.0);


}

