const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}
//hash by IQ https://www.shadertoy.com/view/XlXcW4
vec2 circleSample(uvec3 x){
    vec2 v,z;
    for(int i = 0;v=hash(x+uint(v*1e9)).xy, z=v*2.-1., i++<9 && length(z) > 1.;);
    return z;
}

mat2 rot( float rad ){
    return mat2(
        cos(rad), sin(rad),
        -sin(rad), cos(rad)
    );
}

vec2 decrypt(float d, vec2 r){
    return vec2(mod(d, r.x), floor(d/r.x))/r;
}

float encrypt(vec2 uv, vec2 r){
    return uv.x*r.x + floor(uv.y*r.y)*r.x;
}

void push(inout vec4 O, vec2 uv, vec2 res, float me, float new){
    ivec2 iuv = ivec2(floor(uv*res));
    ivec2 ires = ivec2(floor(res));

    float e = encrypt(uv, res);
    if (new < me && e != O.x && e != O.y && e != O.z && e != O.w){
        O.w = O.z; O.z = O.y; O.y = O.x;
        O.x = e;
    }

}

