const uint IDEAL_MAX_PARTICLES = 1500U; // how many particles to create if there's enough room for them
const float CANDY_R = .015; // pappermint radius
const float PI = 3.141592653589793;

#define R iResolution.xy

// PERSISTENT STATE

struct fxState
{
    vec2 cam;   // camera pos
    float zoom; // camera zoom
    float res;  // tracks resolution changes
    float att;  // timer to resume attract mode (automatic zoom)
    float avgDt;
} g_State;

#define fxGetState() fxGetStateImpl(iChannel1)
void fxGetStateImpl(sampler2D sampler)
{
    vec4 data0 = texelFetch(sampler, ivec2(0), 0);
    vec4 data1 = texelFetch(sampler, ivec2(0, 1), 0);

    g_State.cam = data0.xy;
    g_State.zoom = data0.z;
    g_State.res = data0.w;
    g_State.att = data1.x;
    g_State.avgDt = data1.y;
}

vec4 fxPutState(vec2 u)
{
    if (ivec2(u) == ivec2(0))
        return vec4(g_State.cam, g_State.zoom, g_State.res);
    else
        return vec4(g_State.att, g_State.avgDt, 0, 0);
}

bool fxIsStatePixel(vec2 u)
{
    return ivec2(u) == ivec2(0) || ivec2(u) == ivec2(0, 1);
}

// returns the ids of the four closest particles from the input
#define fxGetClosest(X) fxGetClosestImpl(iChannel1, X)
uvec4 fxGetClosestImpl(sampler2D sampler, ivec2 xy)
{
    return floatBitsToUint(texelFetch(sampler, xy, 0));
}

// PARTICLE STRUCTURE

#define UL_NEIGHBORS 0U
#define UR_NEIGHBORS 1U
#define LL_NEIGHBORS 2U
#define LR_NEIGHBORS 3U
#define POS_VEL 4U
#define SDAV 5U
#define NUM_PARTICLE_DATA_TYPES 6U

uint g_MaxParticles = 0U;

// must be called in each buffer before g_MaxParticles is correct
#define computeMaxParticles() computeMaxParticlesImpl(R)
void computeMaxParticlesImpl(vec2 res)
{
    g_MaxParticles = min(IDEAL_MAX_PARTICLES, uint(res.x * res.y) / NUM_PARTICLE_DATA_TYPES);
}

//returns the location of the particle within the particle buffer corresponding with the input id 
#define fxLocFromID(A, B) fxLocFromIDImpl(int(R.x), A, B)
ivec2 fxLocFromIDImpl(uint width, uint id, uint dataType)
{
    uint index = id * NUM_PARTICLE_DATA_TYPES + dataType;
    return ivec2(index % width, index / width);
}

struct fxParticle
{
    uvec4 nbs[4]; // neighbors
    vec2 pos;     // position
    vec2 vel;     // velocity
    vec2 sd;      // shock direction
    float r;      // rotation
    float av;     // angular velocity
};

//get the particle corresponding to the input id
#define fxGetParticle(X) fxGetParticleImpl(iChannel0, uint(R.x), X)
fxParticle fxGetParticleImpl(sampler2D sampler, uint resolutionWidth, uint id)
{
    vec4 particleData0 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, UL_NEIGHBORS), 0);
    vec4 particleData1 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, UR_NEIGHBORS), 0);
    vec4 particleData2 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, LL_NEIGHBORS), 0);
    vec4 particleData3 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, LR_NEIGHBORS), 0);
    vec4 particleData4 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, POS_VEL), 0);
    vec4 particleData5 = texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, SDAV), 0);

    fxParticle particle;
    particle.nbs[0] = floatBitsToUint(particleData0);
    particle.nbs[1] = floatBitsToUint(particleData1);
    particle.nbs[2] = floatBitsToUint(particleData2);
    particle.nbs[3] = floatBitsToUint(particleData3);
    particle.pos = particleData4.xy;
    particle.vel = particleData4.zw;
    particle.sd = particleData5.xy;
    particle.r = particleData5.z;
    particle.av = particleData5.w;

    return particle;
}

vec4 fxSaveParticle(fxParticle p, uint dataType)
{    
    switch(dataType)
    {
    case UL_NEIGHBORS:
        return uintBitsToFloat(p.nbs[0]);
    case UR_NEIGHBORS:
        return uintBitsToFloat(p.nbs[1]);
    case LL_NEIGHBORS:
        return uintBitsToFloat(p.nbs[2]);
    case LR_NEIGHBORS:
        return uintBitsToFloat(p.nbs[3]);
    case POS_VEL:  
        return vec4(p.pos, p.vel);
    case SDAV:  
        return vec4(p.sd, p.r, p.av);
    }
}

#define fxGetParticleData(X, Y) fxGetParticleDataImpl(iChannel0, uint(R.x), X, Y)
vec4 fxGetParticleDataImpl(sampler2D sampler, uint resolutionWidth, uint id, uint dataType)
{
    return texelFetch(sampler, fxLocFromIDImpl(resolutionWidth, id, dataType), 0);
}

// UTILITIES

#define keyClick(ascii)   ( texelFetch(iChannel3,ivec2(ascii,1),0).x > 0.)
#define keyDown(ascii)    ( texelFetch(iChannel3,ivec2(ascii,0),0).x > 0.)

#define KEY_SHIFT 16
#define KEY_SPACE 32
#define KEY_UP 38
#define KEY_DOWN 40

// Integer Hash - II by iq
// https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;  // GLIB C

vec3 hash3( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

float length2(vec2 v) { return dot(v, v); }
float sqr(float x) { return x * x; }
float ramp(float a, float b, float t) { return clamp((t - a) / (b - a), 0., 1.); } // linear smoothstep

// https://iquilezles.org/articles/distfunctions2d/
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

mat2 rot2(float a)
{
    vec2 sc = vec2(sin(a), cos(a));
    return mat2(sc.y, sc.x, -sc.x, sc.y);
}

// insert new particle at distance d_ with index i_ into i, d arrays, sorting by d
void insertionSort(inout uvec4 i, inout vec4 d, uint i_, float d_)
{	
    if(any(equal(uvec4(i_),i))) return;
    if     (d_ < d[0])             
        i = uvec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             
        i = uvec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])            
        i = uvec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])           
        i = uvec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

// SCENE FUNCTIONS

// 2D SDF of walls
vec2 sdShelf(vec2 p, vec2 res)
{
    float d1 = sdBox((p - vec2(-.12 * res.x / res.y,  .3)) * rot2(-.2), vec2(.6, .01));
    float d2 = sdBox((p - vec2(-.45 * res.x / res.y,  .51)), vec2(.01, .1));
    if (d1 < d2)
        return vec2(d1, 0);
    else
        return vec2(d2, 1);
}

// scene (walls) signed distance field, for rendering and collision
float sdScene(vec2 p, vec2 res)
{
    // slanted walls
    p.y = mod(p.y, .8);
    float d = sdShelf(p, res).x;
    p.y = mod(p.y - .4, .8);
    p.x = -p.x;
    d = min(d, sdShelf(p, res).x);

    return d;
}

/// the outward normal of the scene at a point
vec2 normScene(vec2 p, vec2 res)
{
	const vec2 d = vec2(.001, 0);

   	float x1 = sdScene(p + d.xy, res); 
    float x0 = sdScene(p - d.xy, res); 
    float dx = x1 - x0;

    float y1 = sdScene(p + d.yx, res); 
    float y0 = sdScene(p - d.yx, res); 
    float dy = y1 - y0;

	return normalize(vec2(dx, dy));
}

// PARTICLE SIM

const vec2 GRAV = vec2(0., -.003);  // gravity
const float COLLISION_STIFFNESS = .3; // how fast we correct position errors
const float BOUNDARY_ELAS = 1.5;      // elasticity of boundary
const float PARTICLE_ELAS = 1.;       // elasticity of particles
const float FRIC_COEF = .25;          // Coulomb friction coefficient

// resolve velocity errors against another particle or the boundary
//
//  rv   - relative velocity
//  n    - normal
//  nav  - neighbor angular velocity
//  elas - elasticity
//  lv   - linear velocity
//  av   - angular velocity
void constraint(vec2 rv, vec2 n, float nav, float elas, inout vec2 lv, inout float av)
{
    vec2 t = vec2(n.y, -n.x); // tangent
    float nv = dot(rv, -n); // normal velocity
    float ni = elas * max(0., nv); // normal impulse
    float tv = dot(rv, t) - (av + nav) * CANDY_R; // tangent velocity
    float fi = clamp(tv, -ni * FRIC_COEF, ni * FRIC_COEF); // friction impulse

    lv += ni * n - fi * t; // apply linear impulse
    av += fi / CANDY_R;    // apply angular impulse
}

#define updateParticle(A, B, C) updateParticleImpl(iChannel0, R, iFrame, iMouse, A, B, C)
vec4 updateParticleImpl(sampler2D par, vec2 res, int iFrame, vec4 iMouse, fxParticle p, uint id, uint dataType)
{
    if (iFrame == 0 || g_State.res != res.x * res.y)
    {
        vec3 h1 = hash3((id + uvec3(iFrame)) * uvec3(3, 6, 9)) - .5;
        p.pos = vec2(h1.x, 4. * h1.y );
        if (id == 0U) p.pos = vec2(0);
        p.vel = vec2(0);
        p.sd = vec2(0, 1);
        p.r = 0.;
        p.av = 0.;
    }
    else
    {
        p.vel = p.vel + GRAV * g_State.avgDt; // integrate velocity        
        p.pos += p.vel; // integrate position
        p.r += p.av; // integrate rotation
        p.sd *= .5; // dissipate shock direction

        vec2 imp = vec2(0); // collision linear impulse
        vec2 nsd = p.sd; // new shock direction
        for(int i = 0; i < 4; i++)
        {
            uvec4 nbs = p.nbs[i];
            for (int j = 0; j < 4; ++j)
            {
                uint cid = nbs[j];
                if(cid==id || cid >= g_MaxParticles) continue;

                fxParticle n = fxGetParticleImpl(par, uint(res.x), cid);

                vec2 dp = p.pos - n.pos;
                float d2 = length2(dp) + CANDY_R * 1e-4;

                if (d2 < sqr(CANDY_R * 5.))
                {
                    nsd += n.sd; // diffuse shock direction

                    if (d2 < sqr(CANDY_R * 2.))
                    {
                        vec2 dir = dp / sqrt(d2);

                        // position correction (ignore if shock direction disagrees)
                        if (dot(p.sd, dir) > -.2)
                            p.pos = mix(p.pos, n.pos + 2. * CANDY_R * dir, COLLISION_STIFFNESS);

                        constraint(p.vel - n.vel, dir, n.av, PARTICLE_ELAS, imp, p.av);
                    }
                }
            }
        }

        float sd2 = length2(nsd);
        if (sd2 > 1e-9)
            p.sd = mix(p.sd, nsd / sqrt(sd2), .9); // record new shock direction

        if (dot(p.sd, imp) < -.2) // resist impulses against the shock direction
            imp *= .5;

        p.vel += imp; // apply collision impulse

        // handle mouse input
        if (iMouse.z > 0.)
        {
            vec2 m = g_State.zoom * (iMouse.xy - .5 * res) / res.y + g_State.cam;
            const float MOUSE_RANGE = .1;
            if (distance(m, p.pos) < MOUSE_RANGE)
            {
                const float MOUSE_STRENGTH = .005;
                p.vel += (p.pos - m) * MOUSE_STRENGTH;
            }
        }

        // handle boundary
        float ds = sdScene(p.pos, res);
        if (ds < CANDY_R * 2.)
        {
            vec2 n = normScene(p.pos, res);
            p.sd = n; // seed shock direction

            if (ds < CANDY_R) // collide with boundary
            {
                p.pos -= n * (ds - CANDY_R);

                constraint(p.vel, n, 0., BOUNDARY_ELAS, p.vel, p.av);
            }            
        }
    }

    return fxSaveParticle(p, dataType);
}

// PARTICLE NEIGHBOR UPDATE

bool iscoincidence(uvec4 bestIds, uint currentId, uint id)
{
    return id >= g_MaxParticles || id == currentId || any(equal(bestIds,uvec4(id)));
}

void sort0(sampler2D par, uint wd, inout uvec4 bestIds, inout vec4 bestDists, uint currentId, uint searchId, uint dataType, vec2 myPos)
{
    if(iscoincidence(bestIds, currentId, searchId)) return; //particle already sorted

    vec2 nbX = fxGetParticleDataImpl(par, wd, searchId, POS_VEL).xy; 

    vec2 dx = nbX - myPos;
    uint dir = uint(2.*(atan(dx.y, dx.x)+PI)/PI); 

    if(dir != dataType) return; //not in this sector

    float t = length2(dx);

    insertionSort(bestIds, bestDists, searchId, t);
}

#define voronoiParticle(A, B, C) voronoiParticleImpl(iChannel0, iChannel1, R, iFrame, A, B, C)
vec4 voronoiParticleImpl(sampler2D par, sampler2D vor, vec2 res, int iFrame, fxParticle p, uint id, uint dataType)
{
    uvec4 clo = uvec4(-1); // closest particles to screen pixel (if on screen)
    ivec2 sp = ivec2((p.pos - g_State.cam) * res.y + .5 * res); // screen pixel address
    if (all(lessThan(sp, ivec2(res))) && all(not(lessThan(sp, ivec2(0)))))
        clo = fxGetClosestImpl(vor, sp);

    uvec4 bestIds = uvec4(-1);  // new closest particles
    vec4 bestDists = vec4(1e6); // distances of new closest particles

    if (iFrame > 0)
    {
        for (int i = 0; i < 4; ++i)
        {
            sort0(par, uint(res.x), bestIds, bestDists, id, p.nbs[0][i], dataType, p.pos); // re-sort previous frame's neighbors
            sort0(par, uint(res.x), bestIds, bestDists, id, p.nbs[1][i], dataType, p.pos);
            sort0(par, uint(res.x), bestIds, bestDists, id, p.nbs[2][i], dataType, p.pos);
            sort0(par, uint(res.x), bestIds, bestDists, id, p.nbs[3][i], dataType, p.pos);
            sort0(par, uint(res.x), bestIds, bestDists, id, clo[i], dataType, p.pos);  // check the screen-space voronoi

            fxParticle n = fxGetParticleImpl(par, uint(res.x), p.nbs[i][0]); // check the closest neighbors of each closest neighbor
            sort0(par, uint(res.x), bestIds, bestDists, id, n.nbs[0][0], dataType, p.pos);
            sort0(par, uint(res.x), bestIds, bestDists, id, n.nbs[1][0], dataType, p.pos);
            sort0(par, uint(res.x), bestIds, bestDists, id, n.nbs[2][0], dataType, p.pos);
            sort0(par, uint(res.x), bestIds, bestDists, id, n.nbs[3][0], dataType, p.pos);
        }

        // search randomly a few times too
        uint iter = 8U;
        for(uint k = 0U; k < iter; k++)
        {
            float h = hash3(uvec3(id + k, id * k, k * uint(iFrame))).x;
            uint s = uint(mod(h*34534.0, float(g_MaxParticles)));
            sort0(par, uint(res.x), bestIds, bestDists, id, s, dataType, p.pos);  //sort this
        }
    }

    return uintBitsToFloat(bestIds);
}

// PARTICLE MAIN

#define particleMain(A, B) particleMainImpl(iChannel0, iChannel1, R, iFrame, iMouse, A, B)
void particleMainImpl(sampler2D par, sampler2D vor, vec2 res, int iFrame, vec4 iMouse, out vec4 O, vec2 u)
{
    uvec2 ufc = uvec2(u);

    computeMaxParticlesImpl(res);
    uint index = ufc.x + ufc.y * uint(res.x); // "1D array" index
    uint id = index / NUM_PARTICLE_DATA_TYPES; // which particle
    uint dataType = index - id * NUM_PARTICLE_DATA_TYPES; // which field of particle structure
    if (id >= g_MaxParticles) return;

    fxParticle p = fxGetParticleImpl(par, uint(res.x), id);
    fxGetStateImpl(vor);

    if (dataType >= POS_VEL)
        O = updateParticleImpl(par, res, iFrame, iMouse, p, id, dataType);
    else
        O = voronoiParticleImpl(par, vor, res, iFrame, p, id, dataType);
}

