// ---------------------------------------------------------------------------------------
//	Voronoi tracking buffer and persistent state
// ---------------------------------------------------------------------------------------

vec4 voronoiScreen(vec2 u);
vec4 updateState(vec2 u);

void mainImage(out vec4 O, vec2 u)
{
    fxGetState();
    computeMaxParticles();

    if (fxIsStatePixel(u))
        O = updateState(u);
    else
        O = voronoiScreen(u);
}

vec4 updateState(vec2 u)
{
    if (iFrame == 0 || R.x * R.y != abs(g_State.res) || keyDown(KEY_SPACE))
    {
        g_State.cam = vec2(0);
        g_State.zoom = 1.;
        g_State.res = -R.x * R.y;
        g_State.att = 0.;
        g_State.avgDt = 1./60.;
    }
    else
    {
        g_State.res = abs(g_State.res); // track resolution changes
        fxParticle p = fxGetParticle(0U);

        if (p.vel.y < -.6 * g_State.avgDt || p.pos.y < -20.) // reset if particle zero falls
        {
            g_State.res = -R.x * R.y;
            g_State.cam = vec2(0);
        }
        else
        {
            vec2 c = p.pos; // track particle zero ...
            c.x = mix(c.x, 0., g_State.zoom); // ... but keep the view centered when zoomed out
            g_State.cam = mix(g_State.cam, c, .03); // blend to desired camera position
        }

        g_State.avgDt = mix(g_State.avgDt, iTimeDelta, .05);

        // manual zoom
        if (keyDown(KEY_UP))
            g_State.zoom *= .99, g_State.att = 5.; 
        if (keyDown(KEY_DOWN))
            g_State.zoom /= .99, g_State.att = 5.;

        // attract mode
        g_State.att = max(0., g_State.att - iTimeDelta);
        if (g_State.att == 0.)
            g_State.zoom = mix(g_State.zoom, cos(iTime * .2) * .35 + .65, .02);
        g_State.zoom = min(1., g_State.zoom);
    }

    return fxPutState(u);
}

// find the squared distance from the screen position to a particular particle
float distance2Particle(uint id, vec2 u)
{
    fxParticle v = fxGetParticle(id);

    return min(length2(v.pos - u - g_State.cam), length2(v.pos - v.vel - u - g_State.cam));
}

// get all the existing neighbors of a cell and consider them as possible improvements for ourselves
void sortNbs(uvec4 old, vec2 p, inout uvec4 new, inout vec4 dis)
{
    for (int j = 0; j < 4; j++)
    {
        uint id = old[j];
        if (id == -1U || id >= g_MaxParticles) break;
        float dis2 = distance2Particle(id, p);
        insertionSort(new, dis, id, dis2);
    }
}

vec4 voronoiScreen(vec2 u)
{
	vec2 p = (u - .5 * R.xy) / R.y;
    uvec4 new = uvec4(-1); // the four new closest particles
    vec4 dis = vec4(1e6);  // the distances of the four newest particles
    fxGetState();

    if (iFrame > 0 && !keyClick(KEY_SPACE))
    {
        uvec4 old = fxGetClosest(ivec2(u));

        sortNbs(old, p, new, dis); // start with our previous frame's values, and look up the distances

        const float SEARCH_RANGE = 20.;
        const int SEARCH_COUNT = 8;
        for (int i = 0; i < SEARCH_COUNT; ++i)
        {
            vec3 h = hash3(uvec3(u, iFrame * 4 + i));
            sortNbs(fxGetClosest(ivec2(u + vec2((h.xy - .5) * SEARCH_RANGE))), p, new, dis);
        }

        // random searching to kick start the process
        vec3 h = hash3(uvec3(u, iFrame * 4));
        uint id = uint(h.z * float(g_MaxParticles));
        insertionSort(new, dis, id, distance2Particle(id, p));
    }

    return uintBitsToFloat(new);
}

