/** 

    License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

    Year of Truchets #055
    10/05/2023  @byt3_m3chanic
    Truchet Core \M/->.<-\M/ 2023 

    see all https://www.shadertoy.com/user/byt3_m3chanic/sort=newest

*/


#define ZERO (min(iFrame,0))
#define AA 2

#define R           iResolution
#define T           iTime
#define M           iMouse

#define P9          1.57078
#define PI          3.14159265359
#define PI2         6.28318530718

#define MIN_DIST    1e-5
#define MAX_DIST    50.

// globals
vec3 hit=vec3(0),hitpoint=vec3(0);
vec4 FC=vec4(vec3(.05),0);

mat2 turn,spin;
float glow,px,stime,gid=0.,sid=0.;

// standard bag of tricks
mat2 rot(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float hash21(vec2 p){return fract(sin(dot(p,vec2(23.43,84.21)))*4832.3234);}

//@iq cylinder & hsv2rgb
float cylinder(vec3 p,float h,float r){vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);return min(max(d.x,d.y),0.) + length(max(d,0.));}
vec3 hsv2rgb(in vec3 c){vec3 rgb = clamp(abs(mod(c.x*6.+vec3(0,4,2),6.)-3.)-1.,0.,1.);return c.z * mix(vec3(1),rgb,c.y);}

float tile(vec3 hp, float type){
    //@Fabrice - based off https://www.shadertoy.com/view/sdtGRn
    vec2 uv   = vec2(atan(hp.z,hp.x)/PI2,hp.y);

    vec2 scale = vec2(26.,1);
    float px = fwidth(uv.x); 

    vec2 id = floor(uv*scale);
    vec2 q = fract(uv*scale)-.5;
    float hs = hash21(id);

    float m = floor(mod(id.y,3.)+1.);
    float n = floor(mod(id.x,5.)+1.);

    float ck = mod(id.x+id.y,m)*2.-1.;
    float rk = mod(id.x+id.y,n)*2.-1.;

    float wd = .075;

    if(rk>.5) q *= rot(1.5707);

    vec2 p = length(q-.5)<length(q+.5)? q-.5 : q+.5;

    // main pattern
    float d = abs(length(p)-.5)-wd;

    if (ck>.85) d = min(length(q.x)-wd,length(q.y)-wd);
    hs = fract(hs*415.372);

    // accents
    float b = length(abs(q)-.5)-.325;
    d=min(d,b);

    return d;
}

vec2 map(vec3 p, float sg) {
    vec2 res = vec2(1e5,0.);
    vec3 q = p;

    gid=1.;
    q.zy*=spin;
    q.zx*=turn;
    float d1 = cylinder(q,4.5,2.1);
    float dx = cylinder(q,3.5,2.2);
    float d2=max(d1,-dx);

    float t1 = tile(q,1.);

    d2=max(d2,-t1);

    if(d2<res.x) {
        res=vec2(d2,d1<d2?3.:1.);
        hit=q;
    }


    float d3 = cylinder(q,.1,3.55)-.2;
    if(sg==1.) { glow += .0025/(.001+d3*d3);}
    if(d3<res.x) {
        res=vec2(d3,4.);
        hit=q;
    }

    return res;
}

vec3 normal(vec3 p, float t) {
    float e = MIN_DIST*t;
    vec2 h =vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e,0.).x+
             h.yyx * map(p+h.yyx*e,0.).x+
             h.yxy * map(p+h.yxy*e,0.).x+
             h.xxx * map(p+h.xxx*e,0.).x;
    return normalize(n);
}

vec2 marcher(vec3 ro, vec3 rd) {
    float d = 0., m = 0.;
    for(int i=0;i<100;i++){
        vec2 ray = map(ro + rd * d,1.);
        if(ray.x<MIN_DIST*d||d>MAX_DIST) break;
        d += i<32?ray.x*.35:ray.x;
        m  = ray.y;
    }
    return vec2(d,m);
}

vec4 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, float last, inout float d, vec2 uv) {

    vec3 C = vec3(0);
    vec2 ray = marcher(ro,rd);
    float m =ray.y; d=ray.x;

    hitpoint=hit;
    sid=gid;
    float aph = 0.;
    if(d<MAX_DIST)
    {
        aph=1.;
        vec3 p = ro + rd * d;
        vec3 n = normal(p,d);
        // light
        vec3 lpos =vec3(-25.,15.,10.);
        vec3 l = normalize(lpos-p);
        // difused
        float diff = clamp(dot(n,l),.09,.99);

        // color
        vec3 h = vec3(.0);
        vec3 hp = hitpoint;

        if(m==1.) {
            float d = tile(hp,1.);
            d=smoothstep(px,-px,abs(abs(d-.16)-.0725)-.025);
            h = mix(vec3(.45),vec3(.85),d);
            ref = h*.1;
        }
        if(m==2.) {
            float d = tile(hp*.5,2.);
            d=smoothstep(px,-px,abs(abs(d)-.05)-.025);
            vec3 clr = hsv2rgb(vec3((hp.x-hp.z)*.1,1.,.5));
            h = mix(vec3(.01),clr,d);
            ref = vec3(d*.9);
        }
        if(m==3.) {
            h = hsv2rgb(vec3((p.y*.65+T*.25+sid)*.2,.9,.4));
            ref = h*.5;
        }
        if(m==4.) {h = vec3(1);ref=vec3(.5);}

        C = diff*h;
        ref=h;
        ro = p+n*.005;
        rd = reflect(rd,n);
    } 
    if(last>0.) C = mix(FC.rgb,C,exp(-.0008*d*d*d));
    return vec4(C,aph);
}

vec3 renderALL( in vec2 uv, in vec2 F )
{   

    // standard setup uv/ro/rd
    //vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(0,0,8.5);
    vec3 rd = normalize(vec3(uv,-1));

    // mouse
    float x = M.xy==vec2(0) || M.z <1. ? 0. :  (M.y/R.y*1.-.5)*PI;
    float y = M.xy==vec2(0) || M.z <1. ? 0. : -(M.x/R.x*1.-.5)*PI;

    mat2 rx = rot(x), ry = rot(y);
    ro.zy *= rx; ro.xz *= ry; 
    rd.zy *= rx; rd.xz *= ry;

    vec3 C = vec3(0), ref=vec3(0), fil=vec3(.95);
    float d =0.,a=0.;

    // reflection loop (@BigWings)
    for(float i=0.; i<2.; i++) {
        vec4 pass = render(ro, rd, ref, i, d, uv);
        C += pass.rgb*fil;
        fil*=ref;
        if(i==0.&&pass.w>0.)a=1.;
    }

    float mask = smoothstep(.1,.75,length(uv)-.45);
    vec3 clr = mix(vec3(.5),vec3(.0),mask);

    uv += vec2(T*.25,0);
    uv *= rot(.785);

    float f = clamp(mod(floor(uv*8.).x,2.)*1.-.5,0.,1.);
    C = mix(mix(clr*.5,clr*.2,f),C,a);
    C = mix(C,vec3(.89),clamp(glow*.5,0.,1.));

    return C;
}

// AA from @iq https://www.shadertoy.com/view/3lsSzf
void mainImage( out vec4 O, in vec2 F )
{
    spin = rot(.1*sin(T*.5));
    turn = rot(T*.075);
    px  = 10./R.x;
    stime=T*.1;

    vec3 C = vec3(0.0);
#if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 uv = (-R.xy + 2.0*(F+o))/max(R.x,R.y);
#else    
        vec2 uv = (-R.xy + 2.0*F)/max(R.x,R.y);
#endif
        glow=0.;
        vec3 color = renderALL(uv,F);
        // compress        
        color = 1.35*color/(1.0+color);
        // gamma
        color = pow( color, vec3(0.4545) );

        C += color;

#if AA>1
    }
    C /= float(AA*AA);
#endif
    // Output to screen
    O = vec4(C,1.);
}
//end

// Simplex 2D noise
//
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float noice = snoise(vec2(uv.x * 3.4 - 2.0, uv.y - iTime) * 2.3);


    vec2 circleParams = vec2(cos(noice) + 4.0, abs(sin(noice) + 2.5));

    float circleRatio = circleParams.y/circleParams.x;

    float circle = pow(circleParams.y,-abs(length((fragCoord+fragCoord-iResolution.xy)/iResolution.y)-circleRatio)*20.) * atan(iTime) * 1.3;

    circle += 2. * pow(circleParams.y,-abs(length((fragCoord+fragCoord-iResolution.xy)/iResolution.y -circleRatio*vec2(cos(iTime),sin(iTime))))*circleParams.x); 


    // Output to screen
    fragColor.rgb = circle * vec3(circleParams*.1,.5);
}

/*

This shader visualises the relative solid angle of the texels
of the face of a cubemap (compared to the center texel).

A very simple approximation is proposed. The approximation is
too crude for very low resolutions, but beyond 8x8 it seems
to result in under 1% error.

Maybe a fitting or offset could correct the error for the
very low resolutions as well?


Top center: texels of the cubemap face.
Bottom left: relative solid angle of the texels.
Bottom right: approximation of the relative solid angle.
Center: (200x exaggerated) error of the approximation.

The bottom histogram shows the error accross the diagonal, on
a squeezed scale. The short dashed graduations indicate the
0.125%, 0.25%, 0.5%, 1% and 2% error. The longer dashed
graduations indicate the 10%, 20%, 40% and 80% error.

License: CC BY 4.0

-- 
Zavie
*/

float approximateRelativeSolidAngle(vec2 uv, float resolution)
{
    vec2 p = uv * 2. - 1.;

#if 0
    // Verbose version:

    // The more off-center the texel, the further the distance.
    // So we scale by the inverse square distance.
    float invSqrDist = 1. / (dot(p, p) + 1.);

    // The more off-center the texel, the more it's facing away.
    // So we scale by the cosine.
    float cos_factor = 1. / sqrt(dot(p, p) + 1.);

    // Combine the two factors:
    return invSqrDist * cos_factor;
#else
    // Short version:

    // After derivation, the whole thing becomes just
    float sqrDist = dot(p, p) + 1.;
    return 1. / sqrt(sqrDist * sqrDist * sqrDist);
#endif
}

//
// areaElement and texelSolidAngle are adapted from
// AMD cubemapgen source code:
// https://code.google.com/archive/p/cubemapgen/
//
// See the derivation here:
// https://www.rorydriscoll.com/2012/01/15/cubemap-texel-solid-angle/
//
float areaElement(float x, float y)
{
    return atan(x * y, sqrt(x * x + y * y + 1.));
}

float texelSolidAngle(vec2 uv, float resolution)
{
    vec2 p = uv * 2. - 1.;

    float invResolution = 1. / resolution;

    // p is the -1..1 texture coordinate on the current face.
    // Get projected area for this texel
    vec2 pmin = p - invResolution;
    vec2 pmax = p + invResolution;
    float solidAngle = (
        areaElement(pmin.x, pmin.y) -
        areaElement(pmin.x, pmax.y) -
        areaElement(pmax.x, pmin.y) +
        areaElement(pmax.x, pmax.y));

    return solidAngle;
}

float exactRelativeSolidAngle(vec2 uv, float resolution)
{
    return texelSolidAngle(uv, resolution) / texelSolidAngle(vec2(0.5), resolution);
}

// ----------------------------------------------------------

vec2 remap(vec2 a, vec2 b, vec2 u)
{
    return (u - a) / (b - a);
}

vec2 getMask(vec2 uv)
{
    vec2 du = 2.*vec2(dFdx(uv.x), dFdy(uv.y));
    return smoothstep(vec2(0.), du, uv) * (1. - smoothstep(vec2(1.) - du, vec2(1.), uv));
}

vec2 getRectangle(vec2 uv, float x, float y, float width, float height)
{
    vec2 p = vec2(x, y);
    vec2 size = vec2(width, height);
    return remap(p - size/2., p + size/2., uv);
}

vec2 getSquare(vec2 uv, float x, float y, float size)
{
    vec2 p = vec2(x, y);
    return remap(p - size/2., p + size/2., uv);
}

vec2 discretise(vec2 uv, float resolution)
{
    return (floor(uv * resolution) + 0.5) / resolution;
}

vec3 errorToColor(float error)
{
    // Colours picked from:
    // https://www.kennethmoreland.com/color-advice/
    // The publication explains how to properly interpolate,
    // but I was too lazy to implement it.
    vec3 midPoint = vec3(0.865, 0.865, 0.865);
    vec3 positive = vec3(0.230, 0.299, 0.754);
    vec3 negative = vec3(0.706, 0.016, 0.150);

    if (error > 0.)
    {
        if (error <= 1.)
            return mix(midPoint, positive, error);
        // Saturate to blue above 1:
        return vec3(0., 0., 1.);
    }
    else
    {
        if (error >= -1.)
            return mix(midPoint, negative, -error);
        // Saturate to red below -1:
        return vec3(1., 0., 0.);
    }
}

float logScale(float x)
{
    float scale = 9.;
    return (exp(scale * x) - 1.) / (exp(scale) - 1.);
}

vec4 errorHistogram(vec2 uvPlot, vec2 uvFace, float error)
{
    float x = uvPlot.x;
    float dx = dFdy(x);
    float y = logScale(uvPlot.y);
    float dy = dFdy(y);
    vec4 color = vec4(errorToColor(0.), 0.1);
    if (error > 0.)
    {
        float e = (error);
        color = mix(color, vec4(errorToColor(200. * error), 1.), smoothstep(y, y + dy, e));
    }
    else if (error < 0.)
    {
        float e = (-error);
        color = mix(color, vec4(errorToColor(200. * -error), 1.), smoothstep(y, y + dy, e));
    }

    // Draw graduations at 0.125%, 0.25%, 0.5%, 1% and 2%
    for (float graduation = 0.00125; graduation <= 0.02; graduation *= 2.)
    {
        float line = smoothstep(y - dy, y, graduation) * (1. - smoothstep(y, y + dy, graduation));
        float dotted = fract(100. * x);
        float dottedLine = line * smoothstep(0., dx, dotted) * (1. - smoothstep(0.75, 0.75 + dx, dotted));
        color = mix(color, vec4(1. - color.rgb, 1.), 0.5*dottedLine);
    }

    // Draw graduations at 10%, 20%, 40% and 80%
    for (float graduation = 0.1; graduation < 1.0; graduation *= 2.0)
    {
        float line = smoothstep(y - dy, y, graduation) * (1. - smoothstep(y, y + dy, graduation));
        float dotted = fract(25. * x);
        float dottedLine = line * smoothstep(0., dx, dotted) * (1. - smoothstep(0.85, 0.85 + dx, dotted));
        color = mix(color, vec4(1. - color.rgb, 1.), 0.5*dottedLine);
    }

    return color;
}

// ----------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float evolution = abs(fract(iTime * 0.05) * 2. - 1.);
    float cubeMapResolution = pow(2., 1. + floor(evolution * 10.));

    vec2 uv = fragCoord/iResolution.x;
    vec3 color = vec3(0.2);

    // Visualise the texels depending on the cubemap resolution
    {
        vec2 uvFace = getSquare(uv, 0.5, 0.45, 0.22);
        vec2 mask = getMask(uvFace);
        uvFace = discretise(uvFace, cubeMapResolution);

        vec3 texels = vec3(uvFace, 0.);
        color = mix(color, texels, mask.x * mask.y);
    }

    // Visualise the texel relative solid angle
    {
        vec2 uvFace = getSquare(uv, 0.2, 0.32, 0.35);
        vec2 mask = getMask(uvFace);
        uvFace = discretise(uvFace, cubeMapResolution);

        float ersa = exactRelativeSolidAngle(uvFace, cubeMapResolution);
        color = mix(color, vec3(ersa), mask.x * mask.y);
    }

    // Visualise the approximated texel relative solid angle
    {
        vec2 uvFace = getSquare(uv, 1. - 0.2, 0.32, 0.35);
        vec2 mask = getMask(uvFace);
        uvFace = discretise(uvFace, cubeMapResolution);

        float arsa = approximateRelativeSolidAngle(uvFace, cubeMapResolution);
        color = mix(color, vec3(arsa), mask.x * mask.y);
    }

    // Visualise the difference between the texel relative
    // solid angle and the approximation
    {
        vec2 uvFace = getSquare(uv, 0.5, 0.22, 0.22);
        vec2 mask = getMask(uvFace);
        uvFace = discretise(uvFace, cubeMapResolution);

        float arsa2 = approximateRelativeSolidAngle(uvFace, cubeMapResolution);
        float ersa2 = exactRelativeSolidAngle(uvFace, cubeMapResolution);
        float error = ersa2 - arsa2;
        color = mix(color, errorToColor(200. * error), mask.x * mask.y);
    }

    // Visualise more precisely the error across the diagonal
    {
        vec2 uvPlot = getRectangle(uv, 0.5, 0.05, 1., 0.1);
        vec2 mask5 = getMask(uvPlot);
        vec2 uvFace = vec2(uvPlot.x);
        uvFace = discretise(uvFace, cubeMapResolution);

        float arsa2 = approximateRelativeSolidAngle(uvFace, cubeMapResolution);
        float ersa2 = exactRelativeSolidAngle(uvFace, cubeMapResolution);
        float error = ersa2 - arsa2;

        vec4 histogram = errorHistogram(uvPlot, uvFace, error);
        color = mix(color, histogram.rgb, mask5.x * mask5.y * histogram.a);
    }

    fragColor = vec4(color, 1.0);
}

#define PERIOD 50.0
#define PIXEL_THICKNESS 1.0
#define AA_SCALE 1.0

#define BG_COLOR vec3(0.059,0.082,0.008)
#define FG_COLOR vec3(0.682,0.969,0.490)

#define smooth(x) (3.0*x*x - 2.0*x*x*x)

mat2 r(float t)
{
    return mat2(cos(t), -sin(t), sin(t), cos(t));
}

vec2 hash22(vec2 p)
{
    vec3 p3 = fract(p.xyx * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float perlinNoise(vec2 p, float t)
{    
    vec2 tlVal = r(t * hash22(vec2(floor(p.x),  ceil(p.y)) + 20.0).x)*(hash22(vec2(floor(p.x),  ceil(p.y))) - 0.5) * 2.0;
    vec2 blVal = r(t * hash22(vec2(floor(p.x), floor(p.y)) + 20.0).x)*(hash22(vec2(floor(p.x), floor(p.y))) - 0.5) * 2.0;
    vec2 trVal = r(t * hash22(vec2( ceil(p.x),  ceil(p.y)) + 20.0).x)*(hash22(vec2( ceil(p.x),  ceil(p.y))) - 0.5) * 2.0;
    vec2 brVal = r(t * hash22(vec2( ceil(p.x), floor(p.y)) + 20.0).x)*(hash22(vec2( ceil(p.x), floor(p.y))) - 0.5) * 2.0;

    float tl = dot(p - vec2(floor(p.x),  ceil(p.y)), tlVal);
    float bl = dot(p - vec2(floor(p.x), floor(p.y)), blVal);
    float tr = dot(p - vec2( ceil(p.x),  ceil(p.y)), trVal);
    float br = dot(p - vec2( ceil(p.x), floor(p.y)), brVal);

    return mix(mix(bl, tl, smooth(fract(p.y))), 
               mix(br, tr, smooth(fract(p.y))),
                           smooth(fract(p.x)))*0.5 + 0.5;
}

float fbm(vec2 p, float t)
{
    float v = 0.0;
    for(float i = 0.0; i < 4.0; i++)
    {
        v += perlinNoise(p * exp(i), t * exp(i)) * exp(-i);
    }
    return v * 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/iResolution.y;

    float v = fbm(uv + iTime*0.03, iTime*0.05);
    float av = abs(sin(v*PERIOD));
    av = smoothstep(0.0, fwidth(av)*AA_SCALE, av - fwidth(v*PERIOD)*0.5*PIXEL_THICKNESS);

    // Output to screen
    fragColor = vec4(sqrt(mix(BG_COLOR * BG_COLOR, FG_COLOR * FG_COLOR, 1.0-av)), 1);
}

const float OPACITY = 0.2;
const vec3 HALO_COL = vec3(0.2, 0.6, 1.0);
const vec3 EDGE1_COL = vec3(1.0, 0.68, 0.66);
const vec3 EDGE2_COL = vec3(1.0, 0.3, 0.2);
const vec3 BACKGROUND_COL = vec3(0, 0, 0.11);

float mod289(float x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec4 mod289(vec4 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec4 perm(vec4 x) { return mod289(((x * 34.0) + 1.0) * x); }

float noise(vec3 p)
{
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}

float rand1d(float n) { return fract(sin(n) * 43758.5453123); }

float noise1d(float p) 
{
	float fl = floor(p);
	float fc = fract(p);
	return mix(rand1d(fl), rand1d(fl + 1.0), fc);
}

vec2 rot(vec2 v, float a) 
{
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, s, -s, c);
	return m * v;
}

vec3 circle(vec2 uv, float off) 
{
    vec3 col = HALO_COL;
    float t = iTime * 0.5 + off;
    float rt = t * 0.4;
    float f = fwidth(uv).y * 4.0;
    uv = rot(uv, 6.2831853 * (noise1d(rt - 6816.6) * 0.5 + noise1d(rt * 1.25 - 3743.16) * 0.4 + noise1d(rt * 1.5 + 1741.516) * 0.3));
    float n = noise(vec3(uv * 1.2, t)) * 0.2 + noise(vec3(-uv * 1.7, t)) * 0.15 + noise(vec3(uv * 2.2, t)) * 0.1;
    float d = dot(uv, uv);
    float hd = d + n;
    col = pow(vec3(hd), vec3(3.5, 3.5, 2.0)) * HALO_COL * smoothstep(1.0, 1.0 - f, hd);
    float cd = d * hd * hd * smoothstep(1.0, 1.0 - f, hd) * 1.25;
    col += cd * cd * mix(EDGE1_COL, EDGE2_COL, pow(hd, 8.0)) - (cd * cd * cd) * col;
    col = mix(BACKGROUND_COL, col, smoothstep(1.0, 1.0 - f, hd));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float min_res = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min_res * 1.1;

    vec3 col = mix(mix(mix(mix(
    circle(uv, 0.0), 
    circle(uv, 1000.0), OPACITY), 
    circle(uv, 2000.0), OPACITY), 
    circle(uv, 3000.0), OPACITY), 
    circle(uv, 4000.0), OPACITY);
    fragColor = vec4(col, 1.0);
}

