#define I iResolution
#define PI 3.1415926
#define T( s ) fract( iTime * s ) * PI * 4.
#define rot( r ) mat2(cos(r), sin(r), -sin(r), cos(r) )

float cg( vec3 p ){
    float py = p.y;

    py = abs( py ) - .5; // equal to each side
    py = abs( py );

    return max(
      max( abs( p.x ) - .5 * py, abs( p.z ) - .5 * py ),
      abs( p.y ) - .5
    );
}

float df(vec3 p)
{

  p.z -= 2.5;
  p.xz *= rot( T( .005 ) );

  float k = .8;

  for( float i = 0.; i < 16.; i++ ){
      p.xz *= rot( i );
      k += i / 5.;
      p.xz = abs( p.xz ) - .01 * i;
      p.y -= ( k * .01 ) * ( round( fract( iTime * .05 + .12345 * i ) * 10. ) * .1 );
      p.zy *= -rot( i );
  }

  return cg( p );
}

vec3 l(in vec3 b) {
  vec2 a = vec2(1, -2) * 2.;
  return normalize(a.xyy * df(b + a.xyy * 5e-4) + a.yyx * df(b + a.yyx * 5e-4) +
                   a.yxy * df(b + a.yxy * 5e-4) + a.xxx * df(b + a.xxx * 5e-4));
}

void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = (V * 2.0 - I.xy) / I.y;

    vec3 c = vec3( 1. - smoothstep( 0., 2., length( u ) - .5 ) ) * vec3( .4, .6, .9 ) * .2, p, n, o = vec3( 0., 0., -3. ), r = vec3( u * .2, 1. );
    float t, d, i, dp, e = sqrt( 1e-4 );
    for( ; i < 32.; i++ )
        p = o + r * t,
        d = df( p ),
        dp = ( 1. - abs( d ) / e ),
        c += ( dp > 0. ) ? .1 * dp / t : 0., // sub but color not rich
        t += max( abs( d ), e * 2. );

    p = vec3( 0. );
    t = 0.,
    i = 0.,
    dp = 0.;

    for( ; i < 16.; i++ )
        p = o + r * t,
        d = df( p ),
        dp = ( 1. - abs( d ) / e ),
        n = dp > 0. ? l( p ) : vec3( 0. ),
        n = normalize( reflect( p, n ) ) * .25,
        n += dot( reflect( p, r ), n ) * .7,
        c += ( dp > 0. ) ? ( n + .25 * ( dp / t ) ) * .6 : vec3(0.),
        t += max( abs( d ) * .88889, e * .6 );

    // Output to screen
    U = vec4(c,1.0);
}

