const float TWO_PI = 6.283185307179586;

float seed = 0.0;
float rand()
{
    seed += .2874365;
    return fract(sin(seed * 475.89756) * 45.487);
}

float luminance(in vec3 rgb)
{
    return dot(rgb, vec3(0.21, 0.72, 0.07));
}

/**
* Intensifies the brightest pixels of the applied kernel based on the luminance() function
* This is a modified dilation filter
*/
vec3 bokeh(in sampler2D tex, in vec2 uv, in float sep)
{
    vec3 cc = texture(tex, uv).rgb;
    vec3 tc = cc;
    float cl = luminance(cc);

    vec2 texSize = sep/vec2(textureSize(tex, 0));

    for (float i = 0.0; i < 64.0; i++)
    {
        vec2 rr = vec2(rand(), rand())*vec2(TWO_PI, 1);
        vec2 xy = vec2(sin(rr.x), cos(rr.x))*rr.y;

        vec2 off = xy*texSize;
        vec3 s = texture(tex, uv + off).rgb;
        float b = luminance(s);

        if (cl < b)
        {
            cl = b;
            tc = s;
        }
    }

    return tc;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    // linear RGB
    vec3 col = bokeh(iChannel0, uv, 12.*pow(length(uv-.5), 3.));

    fragColor = vec4(col, 1.0);
}

