// Rotate a 2D vector
void mkrot(inout vec2 v, in float a)
{
    float s = sin(a);
    float c = cos(a);
    v *= mat2(c,-s,s,c);
}

// SDF of box
float box(in vec3 p, in vec3 s)
{
    vec3 q = abs(p) - s;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// SDF of cylinder
float cylinder(in vec3 p, in float h, in float r)
{
  vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
  return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float map(in vec3 p, in vec3 bp)
{
    float h = max(p.y-5., -box(p-vec3(0,1,0), vec3(12, 3, 11)) );
    h = max(h, -cylinder( (p.xyz*vec3(1,.85,1)-vec3(-.3, 0, 10)).xzy, 8.0, 3.0));
    h = max(h, -(box(p-vec3(1, 1, 23), vec3(1.5,2,5))-1.));

    h = min(h,  box(p-vec3( 3.5,-1.5,10), vec3(2,2,9)));

    h = max(h, -cylinder(p - vec3(0,4,6), 9.0, 1.4) );

    h = max(h, -cylinder(p - vec3(-2,0,6), 3.0, 2.0) );
    h = max(h, -cylinder(p - vec3(-1,0,6), 4.0, 2.0) );

    h = max(h, -box(p - vec3(-1,4,20), vec3(2)) );

    h = min(h, cylinder(vec3(p.x-.5,p.y, p.z - 4.*round(p.z*.25)) - vec3(+3.5,2,0), 12.0, .4) );
    h = min(h, cylinder(vec3(p.x+2.,p.y, p.z - 3.*round(p.z*.33)) - vec3(-3.5,2,0), 12.0, .4) );
    h = min(h, cylinder(vec3(p.x+2.,p.y-2.8, p.z - 3.*round(p.z*.33)) - vec3(-3.5,2,0), 1.0, .6) );
    h = min(h, cylinder(vec3(p.x+2.,p.y+2.8, p.z - 3.*round(p.z*.33)) - vec3(-3.5,2,0), 1.0, .86) );

    h = max(h, -box(p-vec3(0, 2, 18), vec3(6,3,4)));

    h = min(h, length(p - bp)-.5 );

    return h;
}

const float eps = 1e-3;

float water(in vec3 p, in vec3 bp)
{
    return p.y + noise(p.xz * 6. - vec2(iTime*.5, iTime)) * .01
            + sin(length(p.xz - bp.xz) * 10. - iTime * 6.)*.01 / length(p.xz - bp.xz)*2.;
}

const vec3 sun = normalize(vec3(.2, 1, -.1));

float shadow(in vec3 ro, in vec3 rd, in float d, in vec3 bp)
{
    float k = 8.;
    float res = 1.0;
    float t = 1e-1;
    for(int i = 0; i < 128 && t < d; i++)
    {
        float h = map(ro + rd * t, bp);

        if (h < 5e-3)
            return 0.0;

        res = min(res, k*h/t);
        t += h;
    }
    return res;
}

// Compute point light intensitywith soft shadows
float pointlight(in vec3 p, in vec3 n, in vec3 t, in float i, in vec3 bp)
{
    vec3 d = normalize(t - p);
    float l = shadow(p, d, length(p-t), bp);
    return l * dot(-d, n) / length(t-p) * i;
}

// Compute specular reflection with no reflection depth 
vec3 spec(in vec3 p, in vec3 n, in vec3 rd, in vec3 bp)
{
    float t = 1e-2;
    vec3 d = reflect(rd, n);
    for(int i = 0; i < 256; i++)
    {
        float h = map(p + d * t, bp);

        if (h < 1e-3)
            return max(mix(vec3(.7,1,1), vec3(0,.1,.4), rand(floor((p + d * t) * 16.)) ) * clamp(0., 1., t*.1), vec3(0));

        t += h;
    }
    return vec3(5);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord -.5*iResolution.xy)/iResolution.y;

    vec3 ro = vec3(2, 1.8, sin(iTime*2.)*.05 );
    vec3 rd = normalize(vec3(uv, 1));

    // static camera rotation
    mkrot(rd.yz, 0.12);
    mkrot(rd.xz, 0.2);

    // dynamic camera wiggle
    mkrot(rd.xy, sin(iTime*1.)*.02 );
    mkrot(rd.yz, cos(iTime*2.2)*.01 );

    vec3 col = vec3(0);

    vec3 p = ro;
    vec4 wr = vec4(0); // water reflection and cos theta
    bool ref = true;   // if water surface was hit

    // precompute position of water ball
    float bt = iTime * .16;
    vec3 bp = vec3(-2. +cos(bt) , sin(iTime*3.)*.05 + .2, 5. +sin(bt) );

    for (int i = 0; i < 256; i++)
    {
        float h = map(p, bp);
        float wh = water(p, bp);

        if (h < 1e-3)
        {
            // normal of geometry
            vec3 nor = normalize(map(p, bp) - vec3(
                map(p + vec3(eps, 0, 0), bp),
                map(p + vec3(0, eps, 0), bp),
                map(p + vec3(0, 0, eps), bp)
            ));

            // direct sun light
            float lum = shadow(p, sun, 1e3, bp) * 3.;
            float key = lum; // save direct sun light for later

            // atrium
            lum = max(lum, pointlight(p, nor, vec3(0,0,6), 3., bp) );
            lum = max(lum, pointlight(p, nor, vec3(-1,2,6), 2., bp) );

            // back room
            lum = max(lum, pointlight(p, nor, vec3(-2.5,0,20), 2., bp) );
            lum = max(lum, pointlight(p, nor, vec3(-.5,1,23), .5, bp) );

            // albedo for tiles
            vec3 alb = mix(vec3(.7,1,1), vec3(0,.1,.4), rand(floor(p * 12.)) );

            // specular reflection
            vec3 spec = spec(p, nor, rd, bp);

            // mask out water ball for custom material
            if (distance(bp, p) < 1.)
            {
                // offset origin to center of ball
                // rotate ball
                vec3 pm = p - bp;
                mkrot(pm.xz, -iTime * .2);
                mkrot(pm.yz,  iTime * .3);

                // texture the ball
                alb = mix(vec3(1,0,0), vec3(1,1,0), step(.0, sin(atan(pm.z/pm.x) * 4.)) );
                alb = mix(alb, vec3(1), step(.49, abs(pm.y)) );

                // apply specular and diffuse illumination
                col = mix(spec, alb * (lum * .7 + .3), pow(dot(nor, rd), .6) );
            }
            else
                // we hit the tile environment
                // apply specular and diffuse illumination
                col = mix(spec, alb * lum, pow(dot(nor, rd), .2) );

            // check if water surface was hit before geometry
            if (!ref)
            {
                // water caustics
                vec3 s = smoothstep(0.95, 1., cos(p*16. + (noise(p.xy) + noise(p.yz)) * 32. + iTime*6.));
                col += (s.x+s.z+s.y) * lum * key;

                col = mix(col, vec3(.2,.8,1) * lum, clamp( pow(abs(p.y), 2.) * 7e-2, .0, .7) );
                col = mix(wr.rgb, col, wr.a);
            }

            break;
        } else if (ref && wh < 1e-6)
        {
            vec3 n = normalize(wh + vec3(
                water(p + vec3(eps, 0, 0), bp),
                water(p + vec3(0, eps, 0), bp),
                water(p + vec3(0, 0, eps), bp)
            ));

            rd = refract(rd, n, 1./1.45);
            ref = false;
            wr = vec4(spec(p, n, rd, bp), dot(-n, rd));
        }

        h = ref ? min(h, wh) : h;
        p += rd * h;
    }

    col = mix(max(col, vec3(0)), texture(iChannel0, (fragCoord.xy+.5)/iResolution.xy).rgb, .5);

    fragColor = vec4(col, distance(ro, p));
}

