vec3 uncharted2_tonemap_partial(vec3 x)
{
    float A = 0.15f;
    float B = 0.50f;
    float C = 0.10f;
    float D = 0.20f;
    float E = 0.02f;
    float F = 0.30f;
    return ((x*(A*x+C*B)+D*E)/(x*(A*x+B)+D*F))-E/F;
}

vec3 uncharted2_filmic(vec3 v)
{
    float exposure_bias = 2.0f;
    vec3 curr = uncharted2_tonemap_partial(v * exposure_bias);

    vec3 W = vec3(11.2f);
    vec3 white_scale = vec3(1.0f) / uncharted2_tonemap_partial(W);
    return curr * white_scale;
}

vec3 conv(in sampler2D tex, in vec2 uv, in mat3 ker)
{
    vec3 sum = vec3(0);

    for (int x = 0; x < 3; x++)
    for (int y = 0; y < 3; y++)
    {
        vec2 off = (vec2(x,y)*.3333-.5)/iResolution.xy;
        sum += texture(tex, uv + off).rgb * ker[x][y];
    }

    return sum;
}

vec3 laplace(in sampler2D tex, in vec2 uv, in float strength)
{
    float k = -strength;
    mat3 ker = mat3(
        vec3(k, k, k),
        vec3(k, 8.*strength+1., k),
        vec3(k, k, k)
    );

    return conv(tex, uv, ker);
}

vec3 emboss(in sampler2D tex, in vec2 uv, in float theta)
{
    // init kernel
    mat3 ker = mat3(
        vec3(0),
        vec3(0),
        vec3(0)
    );

    // direction of edge detection
    vec2 dir = vec2(sin(theta), cos(theta));

    // fill kernel with values
    for (int x = -1; x < 2; x++)
    for (int y = -1; y < 2; y++)
        ker[x+1][y+1] = dot(dir, vec2(x,y));

    return conv(tex, uv, ker)+.5;
}

const float Samples = 4.;
const float Strength = 0.02;

vec3 chromatic_aberration(in sampler2D tex, in vec2 uv)
{
    vec3 col = vec3(0);

    uv -= .5;

    vec3 f = 1. - length(uv) * Strength*vec3(2.,1.,0.);

    for (float i = 0.; i < Samples; i++)
    {
        vec3 fs = mix(f, vec3(1), i/Samples);
        col += vec3(
            texture(tex, uv * fs.x+.5).r,
            texture(tex, uv * fs.y+.5).g,
            texture(tex, uv * fs.z+.5).b
        );
    }
    return col / Samples;
}

vec3 vhs(in vec2 uv)
{
    // sharpen the image
    // + filmic tonemap
    vec3 col = laplace(iChannel0, uv, 1.3);

    // add chromatic aberration
    vec3 abbr = chromatic_aberration(iChannel0, uv);
    col = col + (abbr - texture(iChannel0, uv).rgb);

    // add grunge
    col *= texture(iChannel1, uv).rgb * .8 + .2 + .4;

    // bleach out
    col = col * .92 + rand(uv * 345.3567 + iTime)*.08;

    return uncharted2_filmic(col);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    // VHS glitch
    float i = sin(iTime / 5.);
    i *= i * i;
    i *= i * i;  // <<-- pow() function generated some weird NaN value
    i *= i * i;  //            while testing on AMD GPU
    i *= i * i;
    i *= i * i;
    uv.x += fract(uv.y * uv.y + iTime * 4.) * sign(sin(iTime + uv.y * rand(vec2(iTime)) * 8.)) * i * 5e-2;

    // linear RGB
    vec3 col = texture(iChannel0, uv).rgb * 2.;

    col = vhs(uv);

    // grainy stripe
    col += rand(uv.yy+iTime) * .01;
    col -= rand(uv.yy-iTime) * .01;

    fragColor = vec4(col, 1.0);
}

